css:
@-moz-document domain("motoconnect.com") {
body {
	background:url("/*[[WallPaper]]*/") fixed repeat center top !important;
	   color: grey !important;
}

h3 {
	color: LightGrey !important; 
}

.navbar { 
	background: transparent !important; 
}

.corners-top, .corners-bottom {
	display: none !important; 
}

#wrap {
 	background-color: transparent !important;    
}

#wrap > div.inner > table > tbody > tr > td:nth-child(2) > center {
	display: none !important; 
}

.portal-panel {
	background: transparent !important; 
} 

.portal-corners-top, .portal-corners-bottom {
	display: none !important; 
}

.portal-navigation {
	border: 0px solid transparent !important; 
}

.portal-navigation div.menutitle {
	color: Grey !important; 
	border-bottom: 1px solid grey !important; 
	background-color: transparent !important;  
}

.portal-navigation li a {
	color: darkgray !important; 
	background-color: transparent !important; 
	background-image: none !important; 
	border-bottom: 1px solid grey !important; 
	border-left: 7px solid grey !important; 
}

form#qsearch_form {
	color: darkgray !important; 
	background: transparent !important; 
}

input.search {
	color: darkgray !important; 
	background: transparent !important; 
}

select#qsearch_select {
	color: darkgray !important; 
	background: transparent !important; 
}

input.inputbox {
	color: darkgray !important; 
	background: transparent !important; 
}

.forabg, .forumbg {
	color: darkgray !important; 
	background: transparent !important; 
}

ul.topiclist li {
	color: Grey !important; 
}

ul.forums {
	color: darkgray !important; 
	background: transparent !important; 
}

.panel {
	color: darkgray !important; 
	background-color: transparent !important; 
}

.bg2, .bg1 {
	background-color: transparent !important; 
}

select, option {
	color: darkgray !important; 
	background: transparent !important; 
}

.content { 
	color: grey !important; 
}

.signature {
	display: none !important; 
}

blockquote {
	background-color: transparent !important; 
}
}
