css:
@-moz-document domain(kongregate.com) {
/****2016****/

.welcome-user,
.welcome-guest {
    position: fixed;
    right: 0;
    top: 0;
    border: 1px solid #610202;
    z-index: 20001;
    background-color: #610202;
    box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.9);
    margin-right: 8%;
}
#admin_links_placeholder {
    min-width: 940px !important;
    background: #710000 !important;
}

.welcome-user .settings ul {
	box-shadow: 1px 3px 5px rgba(0, 0, 0, 0.5);
    margin-right: -1px;
    border: 1px solid #710000;
    border-top: 0px;
}
    
.admin_links {
    background-color: #710000 !important;
    padding: .4em !important;
    font-size: 11px !important;
    text-align: left !important;
    margin: auto !important;
    max-width: 940px !important;
}
#header h2 a {
    clear: both;
    width: 311px;
    height: 30px;
    background-position: 0 0;
    padding-bottom: 10px;
}
.welcome-user>li>a {
    color: #fff;
    display: block;
    font: 600 14px/27px 'Source Sans Pro', 'Helvetica', Arial, sans-serif;
    padding: 0 15px;
    text-decoration: none;
}
.welcome-user>li {
    background-color: #800000;
    color: #fff;
    float: left;
    height: 27px;
    list-style: none;
    margin-left: 1px;
    text-align: center;
}
#feature h1 {
    font: bold 24px Calibri, sans-serif;
    margin-bottom: 0.5em;
}

.searchbox .searchbox_field {
    border: 1px solid #aaa;
    border-radius: 0px !important;
    float: left;
    background-color: #fbfbfb;
    height: 30px;
    line-height: 30px;
    padding: 0 10px;
}

.searchbox .searchbox_btn {
    float: left;
    line-height: 24px !important;
    margin-left: -1px !important;
    border-radius: 0px;
}

.btn_action:hover, #admin-controls .button_to input[value="Delete Account"]:hover, #admin-controls .new_silencing input[type="submit"]:hover, #admin-controls .new_ban input[type="submit"]:hover, .btn_action:active, #admin-controls .button_to input[value="Delete Account"]:active, #admin-controls .new_silencing input[type="submit"]:active, #admin-controls .new_ban input[type="submit"]:active {
    background-color: #a5a5a5;
    background-image: -webkit-linear-gradient(top, #A20000, #870101);
    background-image: linear-gradient(to bottom, #C30000,#660202);
    text-shadow: 0 0 2px rgba(0,0,0,0.75);
}

.btn_action, #admin-controls .button_to input[value="Delete Account"], #admin-controls .new_silencing input[type="submit"], #admin-controls .new_ban input[type="submit"], body.adjusted .btn_action, body.adjusted #admin-controls .button_to input[value="Delete Account"], #admin-controls .button_to body.adjusted input[value="Delete Account"], body.adjusted #admin-controls .new_silencing input[type="submit"], #admin-controls .new_silencing body.adjusted input[type="submit"], body.adjusted #admin-controls .new_ban input[type="submit"], #admin-controls .new_ban body.adjusted input[type="submit"] {
    background-color: #AA0000;
    background: -webkit-linear-gradient(top, #A00000, #084d70);
    background: linear-gradient(to bottom, #BF0000,#8F0000);
    border-color: #410101 !important;
    box-shadow: none;
    color: #fff !important;
    text-shadow: 0 0 2px rgba(0,0,0,0.5);
}

.mls, .mhs, .mas {
    margin-left: 1px !important;
}

.adjusted h3, .h3 {
    font-size: 18px;
    font-weight: normal;
    font-family: Calibri,sans-serif;
    line-height: 18px;
}

#profile_comment_form textarea {
    border-color: darkgray;
    border-style: solid;
    border-width: 1px;
    height: 30px;
    padding: 4px;
    resize: vertical;
    width: 478px;
    z-index: 1;
}

.hinted_value {
    font: italic 11px Verdana,sans-serif;
    color: #A8A8A8;
}

#profile_comment_form .submission_button {
    margin: 0 0 0 5px;
    padding: 3px 0;
    position: absolute;
    right: 0;
    top: 0;
    width: 70px;
    height: 39px;
}

body#forums div#feature table tr th {
    background: #900;
    color: #FFF;
    font-size: .8em;
    padding: 6px 10px;
    border: none;
}

body#forums div#feature table tr td {
    border: 1px solid #dddddd;
}

img.green {
    background-color: #B20000;
}

.settings-nav a:hover {
    text-decoration: none;
    background-color: #900;
    color: white!important;
    transition: all 0.17s ease;
}

.forum_mods {
    max-width: 700px;
    overflow: hidden;
    background-color: #fbfbfb;
    padding: 3px 3px;
    border: 1px solid #aaa;
}

.mrl, .mhl, .mal {
    margin-right: 10px !important;
    padding: 0px 3px;
}


/****2018****/

body#forums div#feature .vcard, body#forums div#feature .body {
    display: block;
    width: 100%;
}

body#forums div#feature .vcard {
    background-color: #efefef;
    padding-bottom: 0;
    overflow: hidden;
}

body#forums div#feature .body {
    background-color: #fff;
}

body#forums div#feature table.posts {
  table-layout: inherit;
  width: calc(100% - 3px);
  box-sizing: border-box;
  border: none;
  border-bottom: 1px solid #adadad;
}

body#forums div#feature table tr td {
    border-top: 1px solid #b5b3b3;
    border-right: 1px solid #b5b3b3;
    border-left: 1px solid #b5b3b3;
    border-bottom: 1px solid transparent;
    box-sizing: border-box;
}

body#forums div#feature table.posts tr.hentry td.hidden-post {
    background-color: #ffffbc;
}

body#forums div#feature .spacer {
    visibility: hidden;
}

#forum_posts .author .post_flag_link, #forum_posts .author .post_hide_link, #forum_posts .author .post_clear_flags {
    margin: 0;
}

#forum_posts .author .post_flag_link a:before {
    display: inline-block;
    position: absolute;
    top: -42px;
    right: 0;
    content: "Quote Post";
    width: 65px;
    padding: 6px 3px 6px 6px;
    color: #bfbfbf;
    border: 1px solid #bfbfbf;
    background-color: #f9f9f9;
    pointer-events: none;
}

#forum_posts .author .post_flag_link a, #forum_posts .author .post_hide_link a, #forum_posts .author .post_clear_flags a, body#forums div#feature .post .quote a, body#forums div#feature .post .author .edit a {
    text-decoration: none;
    border: 1px solid #adadad;
    background-color: #f9f9f9;
    padding: 6px 8px;
    transition: all 0.1s ease-in-out;
}

body#forums div#feature .post .quote a {
    color: #666666;
    text-transform: capitalize;
}

#forum_posts .author .post_flag_link a:hover, #forum_posts .author .post_hide_link a:hover, #forum_posts .author .post_clear_flags a:hover {
    border-color: #900;
    background-color: #ffe7e7;
}

body#forums div#feature .post .quote a:hover {
    color: #3e5161;
    border-color: #718ea7;
    background-color: #dceaf7;
}

body#forums div#feature .post .author .edit a:hover {
    color: #7d5a14;
    border-color: #cc9831;
    background-color: #fffcbf;
}

body#forums div#feature .media, body#forums div#feature .media .bd {
    overflow: visible;
    padding-bottom: 10px;
    position: relative;
    top: 0;
}

body#forums div#feature .media p.bd:first-of-type {
    padding-bottom: 0;
    margin-bottom: -10px;
}

body#forums div#feature #forum_posts .mtm, body#forums div#feature #forum_posts .mvm, body#forums div#feature #forum_posts .mam {
    margin-bottom: 15px;
}

body#forums div#feature .author .post_hide_link:first-of-type {
    margin-left: 10px!important;
}

body#forums div#feature .post .date:before {
    display: inline-block;
    position: relative;
    content: "Posted: ";
}

body#forums div#feature .post .date {
    float: left;
    position: relative;
    top: 55px;
    z-index: 2;
}

#forum_posts .author .post_flag_link {
    position: relative;
    top: 42px;
    right: 2px;
    z-index: 2;
}

body#forums div#feature .hover_profile {
    position: relative;
    top: -25px;
}

body#forums div#feature .post .author img {
    margin-right: 0.5em;
    position: relative;
    top: 3px;
    z-index: 1;
}

body#forums div#feature .post .author a.post_creator, body#forums div#feature .post .author .name a {
    position: relative;
    top: -23px;
}

body#forums div#feature .post a.post_creator:before {
    display: block;
    position: absolute;
    content: "";
    top: -10px;
    left: -57px;
    width: 600px;
    height: 1200px;
    background: -webkit-linear-gradient(left, rgba(0, 140, 255, 0.15) 0%,rgba(0, 140, 255, 0.15) 1%,rgba(125,185,232,0) 100%);
    pointer-events: none;
}

body#forums div#feature .post .author a.post_creator {
    color: black;
}

body#forums div#feature .post .author a.post_creator:hover {
    color: #4a4a4a;
}

body#forums div#feature .post .author .posts {
    float: left;
    position: relative;
    top: -20px;
}

body#forums div#feature .post .author .quote {
    float: right;
    margin-top: -34px;
    position: relative;
    z-index: 2;
}

body#forums div#feature .edit {
    position: relative;
    top: -65px;
    right: 82px;
    float: right;
}

body#forums div#feature .post .author .edit a {
    color: #666666;
    text-transform: capitalize;
}

#forum_posts .author .post_hide_link a, #forum_posts .author .post_clear_flags a {
    position: relative;
    top: -29px;
    right: 8px;
}

#forum_posts .author .post_flag_count {
    position: relative;
    top: -29px;
    right: 3px;
}

body#forums div#feature .pagination, body#forums div#feature .forum_mods {
    position: relative;
    margin-top: 5px;
    z-index: 1;
}

body#forums div#feature .body .raw_post~em {
    color: #d25656;
}

.forum--entry pre, .CodeMirror pre, .editor-preview-side pre {
    max-width: 850px;
}

#featurewide table tbody {
    border-bottom: 2px solid #b5b3b3;
}

body#forums div#feature #community_content .media, body#forums div#feature #community_content .media .bd {
    overflow: hidden;
}

body#forums div#feature #community_content .hover_profile {
    top: 0;
}

body#forums #featurewide .hentry, body#forums #featurewide tr {
    background-color: #fdfdfd;
    border-bottom: 2px solid #e6e6e6;
}

body#forums.community_page #forums table tr {
    background-color: #ffffff;
    box-shadow: inset 0 0 0 1px #e9e9e9;
    -moz-box-shadow: inset 0 0 0 1px #e9e9e9;
    -o-box-shadow: inset 0 0 0 1px #e9e9e9;
    -webkit-box-shadow: inset 0 0 0 1px #e9e9e9;
}

body#forums.community_page #forums table td {
    padding: 10px;
}

.forum--entry blockquote, .CodeMirror blockquote, .editor-preview-side blockquote {
    background: #dcedff;
    border: 1px solid #b2c3d2;
    border-left: 3px solid #b2c3d2;
    border-right: 1px solid #b2c3d2;
}

}
