css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twoo.com") {
	/* Promos */
	.promo--whatmenwant,#promoboxContainerTopLeft,.tw3-header__navigation__item--unlimited,.jsDiscoverPromo,.tw3-col-12.tw3-bp4-col-5.photoCover-v2__info__extended__ad{
		display: none !important;
	}
	#profileBar .hide{
		display: none !important;
	}
	
	/* tw3-modal  tw3-modal--payments  tw3-modal--payments--featuresActive  tw3-modal--payments--premium   tw3-modal--open" */
	/* Visitors -> paywall */
	.tw3-modal--payments--premium{
		display: none !important;
	}
	/* Visitors -> buy premium */
	.tw3-whoLikesYouMan__footer > h4{
		display: none !important;
	}
	.tw3-whoLikesYouMan__footer > .text--center.mb--default{
		display: none !important;
	}
	.tw3-whoLikesYouMan__footer > p.mb--default a{
		display: none !important;
	}

	/* Gifts */
	/* Visitors page - locked */
	.jsGiftContainer.tw3-overlay{
		display: none !important;
	}
	.jsGiftsRandom.vam{
		display: none !important;
	}
	.jsSendGiftTrigger.tw3-button{
		display: none !important;
	}
	/* Bezorg haar een fantastische dag! Laat je bericht extra opvallen door haar een cadeau te geven */
	/* Stuur X een cadeau */
	.jsGiftContainer p, .jsGiftContainer h4{
		display: none !important;
	}
	/* Voeg een cadeau toe */
	.jsGiftsTrigger{
		display: none !important;
	}
	/* Laat dit bericht opvallen */
	.jsHighlightMessage.mb--compact{
		display: none !important;
	}
	/* Messages */
	/* Geen antwoord gekregen? Zorg dat je bericht bovenaan in haar inbox getoond wordt. */
	.jsHighlightMessage{
		display: none !important;
	}
	/* Heeft zij jouw laatste bericht gelezen? Ontdek het meteen */
	.jsTriggerRead.trigger_read{
		display: none !important;
	}
	/* Gift icon*/
	.mr--compact.jsButtonGifts.tw3-tooltip.jsTooltip{
		display: none !important;
	}
	/* Gifts panel */
	.conversationsContainer__item__addons.conversationsContainer__item__addons--gifts{
		display: none !important;
	}
	/* Bereik 100 vrouwen in 1 keer! */
	#jsMessagebombButton{
		display: none !important;
	}
	/* Contacteer elke dag meer leden. */
	.conversationsContainer__group.JsGroupContainer.stickyPromo{
		display: none !important;
	}
	/* Delayed message / activate premium */
	.tw3-button--delayedmessage{
		display: none !important;
	}
	
	/* Header bar */
	/* Premium */
	.menuItem.tw3-headerV2__actions__item.tw3-headerV2__actions__item__topupgradelink{
		display: none !important;
	}
	/* Credits */
	.menuItem.tw3-headerV2__actions__item.tw3-headerV2__actions__item__credits{
		display: none !important;
	}
	/* Spotlight add me */
	.tw3-itp__addme.tw3-itp__addme--blue.jsSpotlightAddMeWrapper{
		display: none !important;
	}
	/* Hide boost action */
	.menuItem.tw3-headerV2__actions__item.tw3-headerV2__actions__item__boost{
		display: none !important;
	}

	/* Visitors page - unlocked */
	/* Boost je profiel! */
	.jsPromotionBlock{
		display: none !important;
	}
	.tw3-button--boost{
		display: none !important;
	}
	.jsPopularityWrapper .tw3-iconQuestionMark.tw3-iconGrey{
		display: none !important;
	}
	
	/* Optional stuff */
	/* No more bouncing */
	.badge--darkRed.jsCount.bounce{
		display: none !important;
	}
	/* More -> Unread messages counter bouncing */
	.messages__counter.jsCount.bounce{
		display: none !important;
	}
	/* Hide boost (click goes to payment) (boost profile URL and profile picture are retained) */
	.boostSlideInTitle.mb--default{
		display: none !important;
	}
	/* Hide birthday messages */
	li[data-inboxtype="NOTIFICATION"]{
		display: none !important;
	}
	
	/* Hide premium "messages" like Zoek je een heel snelle manier om nieuwe matches te vinden? */
	li[data-inboxtype="GROUP"]{
		display: none !important;
	}
}
