img[src="/images/_global/spacer.gif"]
{display:none;}

/*Abstand per CSS hinzugefÃ¼gt*/
div#fotu
{top:35px;}

/*- - - - - - - - - - - - - - - - - - - -*/

/*Mauszeiger
..sollte sich nicht immer Ã¤ndern, wenn man Ã¼ber Tabellen fÃ¤hrt*/

body{cursor:default;}
input{cursor:text;}

/*..sollte zu einem Hilfezeiger werden, wenn man Ã¼ber ein Element fÃ¤hrt, welches das Messageboard einen Text anzeigen lÃ¤sst*/

div#ress_min, div#ress_ves, div#ress_met
{cursor:help;}

