css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('horochan.ru')
{
.pace .pace-progress {
    background: #863D3D;
}
#board-logo, #create-thread {
    border-bottom: 2px solid #863D3D;
}
body, html {
    background: #353535;
    color: #999;
}
a {
    color: #A75353;
}
#board-logo {
    color: #999;
}
#paginator {
    background-color: #252525;
    border: 1px solid #444;
    padding: 0 10px;
    margin: 17px 0;
}
#paginator > a {
    color: #787878;
}
#paginator>.current {
    color: #A75353 !important;
}
.op {
    background: 0 0 !important;
}
.post {
    background-color: #252525;
    border: 1px solid #202020;
}
.post-preview {
    border-color: #555 !important;
}
.post-subject {
    color: #ddd;
}
.post-name {
    color: #bbb;
    font-weight: normal;
}
.post-id > a {
    color: #777;
}
.open-thread {
    border: 1px solid #863D3D;
    box-sizing: border-box;
    display: inline-block;
    padding: 2px 7px;
    margin: 0 2px;
    font-size: 12px;
    line-height: 14px;
}
.post-hightlight:not(.op) {
    background: #D6BAD0 !important;
    border: 1px solid #BA9DBF !important;
}
.post-file {
    background: #C6D1F9;
}
.file-details {
    background: #2d2d2d;
    padding: 5px;
}
.file-download {
    color: #aaa;
}
.thread {
    border-bottom: 1px solid #863D3D;
}
.thread-omitted {
    color: #999;
}
.file-full {
    background-color: #242424;
    border: 1px solid #000;
}
#post-form {
    background-color: #292929;
    border: 1px solid #404040;
    box-shadow: 0 0 2px #101010;
}
#post-form .form-input {
    background: #202020;
    border: none;
}
.form-input:focus {
    border: none !important;
}
#form-files, #markup-container {
    background: #303030;
    border: none;
    border-top: 1px solid #404040;
    border-bottom: 1px solid #404040;
    padding: 2px 10px;
}
.mark-button {
    color: #999;
    font-size: 14px;
}
.mark-button:hover {
    color: #aaa;
}
#form-close, #form-spin {
    color: #ccc;
}
#buttons-container {
    border-top: 1px solid #404040;
}
#form-submit {
    background: #202020;
    border: 1px solid #303030;
    color: #FFF;
}
#form-errors {
    border: 1px solid #863D3D;
    color: #A75353;
}
#form-files:hover {
    background: #353535;
}
#form-files-instruction {
    color: #aaa;
    font-size: 14px;
}
.spoiler {
    background: #000;
    color: #000;
    padding: 0 4px;
    transition: all ease 500ms;
}
.spoiler:hover {
    color: #fff;
}
.g-recaptcha {
    background: #f9f9f9;
    border: 1px solid #404040;
}
.post-actions {
    background-color: #D6DAF0;
    border: 1px solid #000;
}
.post-actions>span:hover {
    background: #676767;
    color: #FFF;
}

}
