css:
@-moz-document domain("pegforum.com") {
/* Increase body font for more legible text */

#phpbb {
    font-size: 12px;
    line-height: 1.4;
    color: #444;
    /* center body */
    
    margin: 0 auto;
}
/* remove 'fuzzy' text-shadows */

a,
.username,
.username-coloured {
    text-shadow: none;
}
/* open up design by removing border and background color from header*/

#header-subsilver {
    background-color: #fff;
    border: none;
}
/*change colors of navbars and links*/

a {
    color: #105289;
}
.navbar {
    background-color: #E20B00;
    border: 2px solid #000;
    border-radius: 3px;
}
.navbar,
.navbar a {
    color: #fff;
}
/* remove backgrounds on form elements */

select,
.button,
.button:hover,
a.button1,
input.button1,
input.button3,
a.button2,
input.button2 {
    background-image: none;
    background-color: #f5f5f5;
}
/* change colors for forum area */

.forabg,
.body-blok {
    background-color: #fff;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 3px;
}
ul.forums {
    background-color: #fff;
}
li.header dt,
li.header dd {
    color: #444;
    font-weight: bold;
}
li.row {
    border-bottom: none;
    border-top: 1px solid #ccc;
}
li.row:hover {
    background-color: #f5f5f5;
}
.bg1,
.bg2 {
    background-color: #fff;
    border-color: #ccc;
}
.body-blok h3 {
    background-image: none;
    border-bottom: 1px solid #ccc;
}
/* remove borders from posts and views columns */

ul.topiclist dd {
    border-width: 0;
}
#subsilver-nav-topic {
    background-color: #fff;
    background-image: none;
    color: #444;
    border: 1px solid #ccc;
    border-bottom-width: 0;
    border-radius: 3px 3px 0 0;
}
/* improve legibility of code box with increased line-height */

.codebox code {
    line-height: 1.5;
    /* give some more breathing room to code element */
    
    max-height: 400px;
}
/* shadow on avatar is unecessary and look out of place in the theme */

.postprofile .avatar img {
    box-shadow: none;
}
/* add border to search box; shadow is superfluous */

.search-header {
    box-shadow: none;
    border: 1px solid #ccc;
}
/* User Control Panel */

.forumbg {
    background-color: #fff;
    background-image: none;
    border: none;
}
#tabs .tab > a {
    background: #E20B00;
    border: 1px solid #000;
    color: #fff;
    border-radius: 6px 6px 0 0;
}
#tabs .tab > a:hover {
    background: #fff;
    color: #444;
}
.panel-container .panel {
    background-color: #fff;
}
.panel,
.bg3 {
    background: transparent;
    border-radius: 3px;
    border-color: #ccc;
}
.topiclist.two-columns {
    background: #fff;
}
#navigation a {
    background: #fff;
}
/* Team */

.responsive {
    border-color: #ccc;
    border-radius: 3px;
}
table.table1 tbody tr {
    border-color: #ccc;
}
table.table1 tbody td {
    border-color: #ccc;
}
/* Members */

table.table1 .posts {
    text-align: right;
}
.pagination li a {
    background-color: #fff;
    border-color: #ccc;
    color: #5C758C;
}
/* Mobile View */

@media screen and (max-width: 700px) {
    /* remove the padding on the wrapper to use real estate */
    
    #wrap {
        padding: 0;
    }
    /* add left and right padding to top links */
    
    .navbar-top-link {
        padding: 2px 5px;
    }
    /* this bar just looks bad on mobile and provides no real value; ditch it */
    
    #subsilver-nav-topic {
        display: none;
    }
    /* redo margins for these items to compensate */
    
    .navbar,
    .forabg,
    .forumbg,
    .post,
    .panel {
        margin: 5px 0;
    }
    /* remove left padding from avatar container to align better with content */
    
    .avatar-container {
        padding-left: 0;
    }
    /* align post buttons with right edge */
    
    .has-profile .post-buttons {
        right: 5px;
    }
    /* add margins to bring away from edge of screen */
    
    .action-bar {
        margin: 4px;
    }
    p.jumpbox-return,
    p.advanced-search-link {
        margin: 5px;
    }
    #jumpbox {
        margin: 5px;
    }
}
@media screen and (min-width: 701px) {
    /* align left of logo to column; add margin for site title */
    
    .logo {
        padding: 0;
        margin-right: 10px;
    }
    #site-description h1,
    #site-description p {
        position: absolute;
        left: -1000px
    }
}
/* Set max widths for body */

@media screen and (min-width: 768px) {
    #phpbb {
        max-width: 750px;
    }
}
@media screen and (min-width: 992px) {
    #phpbb {
        max-width: 970px;
    }
}
@media screen and (min-width: 1200px) {
    #phpbb {
        max-width: 1170px;
    }
}
}
