css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {
  
body 
{
font-family: Verdana !important;
font-size: 8pt!important;
}

a
{
transition:.5s!important;
}
  
a:hover
{
color:#BCF15C!important;
}
  
body img 
{
opacity:.25!important;
transition:.5s!important;
}
  
body img:hover
{
opacity:1!important;
}
  
#superdamnov a.sdt-hl
{
color:#000!important;
}

div#head
{
background:#000!important;
}

body.chatroom .damnc-tabbar-outer {
background:linear-gradient(180deg, #000, #414141 80%, #5c5c5c) !important;
padding-top: 0px !important;
}

.damncr .self-hl .from span:before
{
content: " [self] ";
}

.damncr .self-hl span
{
color:#0093FA;
background:transparent!important;
}
  
.damncr .other-hl .from span:before
{
content: " [tab] ";
}
  
.damncr .other-hl span 
{
background:transparent!important;
border-radius:4px!important;
padding:1px!important;
color:#fff;
}

a 
{
text-decoration:none!important;
}

.damn-lo-row-top
{
border: 0px solid #fff !important;
background:linear-gradient(0deg, #414141, #414141 40%, #000) !important;
}

#bar-deviant-ctrl3 
{
background:linear-gradient(180deg, #5C5C5C, #414141 20%, #000) !important;
border: none !important;
}

.damncr-title 
{
color: #BCF15C !important;
text-shadow:#000 0px 3px 7px,#000 0px 0px 7px;
background:linear-gradient(0deg, #5b5b5b 80%, #0093FA) !important;
padding:15px!important;
line-height:20px;
}

.damncr-title a,
.damncrc-topic a 
{
color: #0093FA !important;
text-decoration:none!important;
}
  
.damncrc-topic 
{
background:linear-gradient(0deg, #252525, #707070 80%) !important;
color: #c4c4c4 !important;
border:none!important;
border-radius:7px 7px 0px 0px!important;
padding:15px!important;
}

.damncrc-chat-window 
{ 
border:none !important; 
}

div.damncrc-iconbar a
{
padding:0px 100px 0px 0px!important
}

.damncrc-input input, .damncrc-input textarea 
{
margin: 10px 0px 0px;
max-width: 98%;
width: 98%;
position: relative;
z-index: 0;    
color: #0093FA;
font-size: 11px;
letter-spacing: -0.25px;
padding: 5px 8px 4px;
border: 0px!important;
background:linear-gradient(180deg, #000, #414141 80%, #5c5c5c) !important;
border-radius: 3px;
box-shadow:none!important;
}

.damncrc-chat 
{
background:linear-gradient(0deg, #5C5C5C, #000 50%) !important;
background-attachment:fixed!important;
padding-bottom:10px!important;
color: #bbce8a  !important;
padding:10px!important;
}
  
.damncrc-chat a
{
color:#0093FA!important;
text-decoration:none!important;
}

.damncrc-chat img
{
opacity:1!important;
}

.damncrc-icon-roomname 
{
color: #BCF15C !important;
padding-left:50px!important;
}
  
.superdamn-buttonbar
{
padding-left:50px!important;
margin-top:10px!important;
position:relative;
top:3px!important;
}

#bar-user, 
#bar-deviant 
{
border-color: #414141 !important;
}

.damncr-infoctr 
{
border-color: #414141 !important;
border-radius:7px 0px 7px 0px!important;
background:linear-gradient(180deg, #5C5C5C, #414141 20%, #000) !important;
}

.damncri-member .bodyarea 
{
border-color:#000!important;
}

.aside-left, .damncr .userinfo .avatar
{ 
background-color: #000 !important; 
border: 0px !important; 
} 

#bar-deviant-ctrl2, 
#bar-deviant-ctrl1
{
background:linear-gradient(180deg, #5C5C5C, #414141 20%, #000) !important;
}

.damncri-member .bodyarea, 
.damncri-member
{
color: #BCF15C  !important;
background:linear-gradient(180deg, #5C5C5C, #414141 20%, #000) !important;
border-color:#000;
}

.damncri-member strong a
{
color: #0093FA !important;
}

.damncri-member a
{
color: #0093FA !important;
}

.damncri-member .avatar
{
background:linear-gradient(180deg, #5C5C5C, #414141 20%, #000) !important;
border-color: #414141 !important;
}

.damncri-member
{
background:#000!important;
}

.damncri-member .bodyarea UL 
{
list-style-type: none!important;
margin: 0!important;
padding-left: 1.2em!important;
border-color: transparent!important;
background:linear-gradient(180deg, #000, #414141 80%, #5C5C5C) !important;
}

.alt1-border 
{
border-width:0;
border-style:solid;    
border-color:#000;
}

.alt1-top-border 
{
border-width:0;
border-style:solid;
border-top-color:#000;
}

.alt1-bottom-border 
{
border-width:0;
border-style:solid;
border-bottom-color:#000;
}

.alt0-bottom-border 
{
border-width:0;
border-style:solid;
border-bottom-color:#000;
}

.alt1-left-border 
{
border-width:0;
border-style:solid;
border-left-color:#000;    
}

.damnc-serverlog-outer
{
border:1px solid #000!important;
color:#BCF15C!important;
padding:0.2em 1em 0.15em 0.7em;
background:linear-gradient(180deg, #5C5C5C, #414141 20%, #000) !important;
}

.userinfo
{
background:#000!important;
}

.userinfo .connarea
{
margin-top:0.8em;
border:1px solid #000!important;
background:#000!important;
}

.damn-lo-col-right
{
color: #BCF15C  !important;
text-align:right!important;
}

.damn-lo-col-right dt
{
color: #0093FA !important;
margin:15px!important;
background:linear-gradient(180deg, #000, #414141 80%, #5c5c5c) !important;
width:150px!important;
text-align:center!important;
border:none!important;
padding:5px!important;
text-shadow:#000 0px 3px 7px,#000 0px 0px 7px;
}
  
.damncr-members 
{
border-left:1px solid #414141!important;
}

.alt0
{
background:linear-gradient(180deg, #5C5C5C, #414141 20%, #000) !important;
}

.damncr .join, 
.damncr .part
{
color: #7C8680 !important;
}
 
.damnc-tabbar a 
{ 
transition:.5s;
padding: 6px 4px 6px 6px !important; 
margin: 6px 3px 0px 3px !important; 
background:linear-gradient(180deg, #787878, #6A6A6A 60%, #5B5B5B 80%, #414141) !important;
border-radius:7px 0px 0px 0px!important;
height:13px!important;
}

.damnc-tabbar a i { 
background:linear-gradient(180deg, #787878, #6A6A6A 60%, #5B5B5B 80%, #414141) !important;
border-radius:0px 7px 0px 0px!important;
}

.damnc-tabbar a:hover 
{ 
transition:.5s;
margin: -1px 3px -15px 3px !important; 
background:linear-gradient(180deg, #c0c0c0, #787878 60%, #5B5B5B 80%, #414141) !important;
border-radius:7px 0px 0px 0px!important;
height:20px!important;
}

.damnc-tabbar a:hover i 
{ 
margin: 0px 0px 0px 3px !important; 
background:linear-gradient(180deg, #c0c0c0, #787878 60%, #5B5B5B 80%, #414141) !important;
border-radius:0px 7px 0px 0px!important;
}

.damnc-tabbar strong 
{ 
padding: 6px 4px 7px 8px !important; 
margin: 1px 3px -2px !important; 
background:linear-gradient(180deg, #A1D8FF, #0093FA 60%, #0093FA) !important;
border-radius:7px 0px 0px 0px!important;
}

.damnc-tabbar strong i 
{ 
background:linear-gradient(180deg, #A1D8FF, #0093FA 60%, #0093FA) !important;
border-radius:0px 7px 0px 0px!important;
}

.damnc-tabbar strong.withX 
{ 
padding: 6px 24px 7px 8px !important; 
margin: 1px 3px -2px !important; 
height:19px!important;
background:linear-gradient(180deg, #A1D8FF, #0093FA 60%, #0093FA) !important;
border-radius:7px 0px 0px 0px!important;
}

.damnc-tabbar strong.withX i 
{ 
background:linear-gradient(180deg, #A1D8FF, #0093FA 60%, #0093FA) !important;
border-radius:0px 7px 0px 0px!important;
}

.withX img 
{ 
margin-top: 4px !important;
}

.chatroom #top
{
margin-top: 0px !important;
background:linear-gradient(180deg, #DFFC96, #DFFC96 70%, #527C0C)!important;
}

.chatroom .ch-ctrl 
{
margin: 0 10px !important;
padding: 0 0 0 7px !important;
background: none !important;
border: none !important;
}
  
a
{
transition:.5s!important;
}
  
a:hover
{
color:#BCF15C!important;
}
}
