/* V0.2 by Yutyrannus 2015.12.24*/
@-moz-document domain(www.cc98.org)
{
td.TopLighNav,
td.TopLighNav2,
td.TopDarkNav,
table[style="border-collapse: collapse; text-align: center; margin-left: auto; margin-right: auto;"],
table[style="border-collapse: collapse; text-align: center; margin-left: auto; margin-right: auto; word-break: break-all;"],
td[style="line-height: 20px;"],
#copyright,
p[style="margin: 20px 10px 20px 10px"]{
    display: none;
}
td.TopLighNav1{
    background-color:/*[[lightcolor]]*/;
    background-image:none;
}
th{
    background-image:none;
    background-color:/*[[darkcolor]]*/;
}
table[style="border: 1px #6595D6 solid; border-top-width: 0px; border-right-width: 1px; border-bottom-width: 0px; border-left-width: 1px; width: 97%;"]{
    border:1px /*[[darkcolor]]*/ solid!important;
}
.tableBorder1 > tbody > tr{
    border:solid 1px /*[[darkcolor]]*/;
}
td[colspan="2"][style="height: 1px; background-color: #6595D6;"]{
    background-color: /*[[darkcolor]]*/!important;
}
td[colspan="2"][style="height: 5px;"]{
    display: none;
}
tr > td[style="width: 1px; padding: 0;background-color: #6595D6;"]{
    background-color:/*[[darkcolor]]*/!important;
}
.td-border-1{
    border:solid 1px /*[[darkcolor]]*/;
}
.tableBorder1{
    background-color: /*[[darkcolor]]*/;
}
.tableBorder2{
    background-color: /*[[lightcolor]]*/;
}
td.TableBody2 {
    background-color: /*[[lightcolor]]*/;
}
div.TableBody1{
    border:1px /*[[darkcolor]]*/ solid ;
}
div.TableBody2{
    background-color: /*[[lightcolor]]*/;
    border:1px /*[[darkcolor]]*/ solid ;
}
td[bgcolor="#6595D6"]{
    background-color: /*[[darkcolor]]*/;
}
td[style="width: 1px; padding: 0; background-color: #6595D6;"]{
    background-color: /*[[darkcolor]]*/!important;
}
}
