css:
@-moz-document domain(pandora.com) {
* {
    text-shadow: none !important;
}
.contentnav {
    background-color: #074996;
    border-bottom: none;
}
.contentContainer .top {
    border-radius: 0;
    background-color: #074996;
    border: 0;
}
#mainContent {
	min-height: 0;
}
#mainContentContainer {
    box-shadow: 0 0 50px -10px #fff;
}
.stationChangeSelector.highlight {
    background-color: #074996;
}
#playerBar {
    background-color: #1F89D5;
    color: #fff;
}
.view-2,
.contentContainer {
    border-radius: 0;
}
#brandingBar {
    background: #074996;
    color: #fff;
}
#brandingBar .middlecolumn {
    color: #fff;
}
#brandingBar .rightcolumn .user_activator,
#brandingBar .rightcolumn .user_activator:hover {
    color: #fff;
    background: #074996;
}
.slidesBackground {
    background: #1F89D5;
}
.slidesBackground > img {
    opacity: 0;
}
.slidesBackground div img {
    display: none;
}
.slidesBackground div {
    background: #1F89D5;
}
.stationContent {
    background: #fff;
    border-right: 0;
}
.stationListItem .stationName {
    color: #555;
}
#trackInfoButtons {
	border-bottom: 0;
    background: #eee;
}
#trackInfoButtons .buttons .button {
    border-radius: 0;
    background: #f5f5f5;
    border: 0;
    color: #333;
}
#trackInfoButtons .buttons .button:hover {
    background: #eee;
}
#brandingBar .logosubscriber {
    -webkit-filter: brightness(5);
}
#playerBar {
    border: none;
    border-bottom: 1px solid #77BBEB;
}
#playerBar .rightcolumn .info a {
    color: #fff;
}
#playerBar .rightcolumn .albumArt img {
    border-radius: 50%;
}
#skinTab {
    background: none;
    color: #ddd;
}
#skinTab:hover {
    color: #fff;
}
#playerBar .progressBar img {
    -webkit-filter: brightness(1.25);
}
#playerBar .progress .progressBar {
    background: #000;
    border-radius: 0;
}
#playerBar .searchBox {
    border-radius: 0;
}
#mainContainer {
    height: auto;
}
.stationContent {
    border-radius: 0;
    height: auto;
}
.stationContent #promobox,
.platformPromo {
    display: none;
}
#stationSortButtons {
    display: none;
}
.fxCol-cont-1 {
    background: none;
}
.fxCol-cont-5 {
	background: #fff;
}
.col-prop-1 {
	min-height: 0;
}
.detail .heading {
	color: #333;
}
#trackInfo {
    border: 0;
    background: #f5f5f5;
}
#trackInfo .info a {
    color: #333;
}
.slide {
    box-shadow: none;
}
.slidesForeground {
	top: -7px;
}
.slidesForeground .slides {
	margin-bottom: 2px;
    padding-bottom: 3px;
}
.stationListItem.selected {
    background: #EFE992;
}
.stationListItem .options {
    background: #F7F4CA;
}
#currentSlideArrow {
    border-left: 23px solid transparent;
    border-right: 23px solid transparent;
    border-bottom: 23px solid #F1F4F7;
    width: 0;
    height: 0;
    background: none;
}
.home .homeRightSide {
	background: #fff;
    margin-left: 190px;
    padding-left: 0;
    border-left: 1px solid #eee;
}
.slidesForeground .slides {
	padding-top: 0;
}
}
