css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("techdom.ml") {
body {
	margin:0;
	background:#121212
}

.d-header {
	width: 100%;
	position: absolute;
	top: 0;
	z-index: 1001;
	background-color: #222
}

html {
	color: #ffffff;
	font-family: Helvetica,Arial,sans-serif;
	font-size: 14px;
	line-height: 19px;
	direction: ltr;
}

blockquote {
	border-left: 5px solid #313131;
	background-color: #222;
	overflow: hidden;
	clear: both;
}

aside.quote .title {
	border-left: 5px solid #313131;
	background-color: #222;
	color: #646464;
	padding: 12px 12px 1px 12px;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
}

#topic-progress {
	position: relative;
	left: 340px;
	background-color: #222;
	color: #08c;
	border: 1px solid #222;
	border-bottom: none;
	width: 145px;
	height: 34px;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
}

#topic-progress .bg {
	position: absolute;
	top: 0;
	bottom: 0;
	width: 0;
	border: none;
	background-color: #333;
}

.badge-wrapper.bullet span.badge-category {
	color: #ffffff !important;
	display: inline-block;
	overflow: hidden;
	text-overflow: ellipsis;
}

#topic-title h1 a {
	color: #ffffff;
}

.topic-map {
	margin: 20px 0 0 0;
	background: #222;
	border: 1px solid #222;
	border-top: none;
}

.topic-map section {
	border-top: 1px solid #222;
}

.topic-map .buttons .btn {
	border: 0;
	padding: 0 23px;
	color: #a7a7a7;
	background: #222;
	border-left: 1px solid #222;
	border-top: 1px solid #222;
}

.btn {
	border: none;
	color: #a7a7a7;
	font-weight: normal;
	background: #222;
}

.list-controls .category-breadcrumb li.bullet > .badge-category {
	background: #222 !important;
	color: #e2e2e2 !important;
}

.nav-pills > li > a {
	border: none;
	padding: 5px 12px;
	color: #c1c1c1;
	font-size: 1.143em;
	line-height: 20px;
	transition: background .15s;
}

.topic-list a.title.visited:not(.badge-notification) {
	color: #e2e2e2;
}

.topic-list a.title {
	color: #c2c2c2;
}

.topic-list th {
	color: #c2c2c2;
	font-weight: normal;
	font-size: 1em;
}

.topic-list > tbody > tr:first-of-type {
	border-top: 2px solid #222;
}

.topic-list > tbody > tr {
	border-bottom: 1px solid #222;
}

.topic-list td {
	color: #e2e2e2;
	font-size: 1em;
}

.badge-posts[href] {
	color: #e2e2e2;
}

body .coldmap-low {
	color: #e2e2e2 !important;
}

.topic-body {
	width: 690px;
	float: left;
	position: relative;
	z-index: 2;
	border-top: 1px solid #222;
	padding: 12px 11px 15px 11px;
}

.topic-avatar {
	border-top: 1px solid #222;
	padding-top: 15px;
	width: 45px;
	float: left;
	position: relative;
	z-index: 2;
}

#topic-closing-info {
	border-top: 1px solid #222;
	padding-top: 10px;
	height: 20px;
}

.small-action {
	width: 755px;
	border-top: 1px solid #222;
}

p > code, li > code, pre > code {
	color: #e2e2e2;
	background: #333;
}

.menu-panel {
	border: 1px solid #222;
	box-shadow: 0 2px 2px rgba(0,0,0,0.25);
	background-color: #222;
	z-index: 1100;
	padding: 0.5em;
	width: 300px;
}

.user-menu .notifications .read {
	background-color: #222;
}

.user-menu .notifications .fa {
	color: #0a6a99;
}

.user-menu .notifications li span {
	color: #e2e2e2;
}

.user-main .about .secondary {
	background: #333;
	font-size: 0.929em;
}

.user-main .about .secondary dd {
	color: #e2e2e2;
}

.nav-stacked {
	padding: 0;
	overflow: hidden;
	background: #333;
}

.nav-stacked > li > a {
	margin: 0;
	padding: 13px 13px 13px 30px;
	font-size: 1.143em;
	line-height: 20px;
	cursor: pointer;
	color: #e2e2e2;
}

.nav-stacked > li {
	border-bottom: 1px solid #222;
	position: relative;
}

.user-main .user-stream .item {
	padding: 20px 8px 15px 8px;
	background-color: #333;
	border-bottom: 1px solid #222;
}

.user-main .user-stream .excerpt {
	margin: 5px 0;
	font-size: 0.929em;
	word-wrap: break-word;
	color: #e2e2e2;
}

.btn[href] {
	color: #e2e2e2;
}

.user-main .user-content {
	padding: 10px 8px;
	background-color: #333;
	border: 1px solid #222;
	margin-bottom: 10px;
	box-sizing: border-box;
}

.user-preferences .static {
	color: #e2e2e2;
	display: inline-block;
}

.user-main .user-content .user-invite-controls {
	background-color: #222;
	padding: 5px 10px 0px 0;
	height: 35px;
}

.gap.jagged-border {
	background-position: top center,top center,bottom center,bottom center;
	background-size: .9em .9em;
	background-repeat: repeat-x;
	padding: 20px 0;
	margin-bottom: 20px;
	margin-left: -10px;
	margin-right: -10px;
	background-color: #222;
}

#reply-control {
	transition: height 0.4s ease;
	width: 100%;
	z-index: 999;
	height: 0;
	background-color: #222;
	bottom: 0;
	font-size: 1em;
	position: fixed;
}

body .grippie {
	width: 100%;
	cursor: row-resize;
	height: 11px;
	overflow: hidden;
	display: block;
	border-top: 1px solid #333;
	background-color: #333;
}

.bootbox.modal {
	position: fixed;
	top: 50%;
	left: 50%;
	z-index: 1050;
	overflow: auto;
	width: 610px;
	height: auto;
	margin: -250px 0 0 -305px;
	background-color: #222;
	border: 1px solid #121212;
	box-shadow: 0 3px 7px rgba(0,0,0,0.8);
	background-clip: padding-box;
}

.modal-footer {
	padding: 14px 15px 15px;
	border-top: 1px solid #333;
}

#reply-control .wmd-button-bar {
	top: 0;
	position: absolute;
	border-bottom: 1px solid #222;
	color: #e2e2e2;
	background-color: #222;
	z-index: 100;
}

#reply-control .wmd-controls .wmd-input, #reply-control .wmd-controls .wmd-preview-scroller, #reply-control .wmd-controls .wmd-preview {
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	width: 100%;
	height: 100%;
	min-height: auto;
	padding: 7px;
	margin: 0;
	background-color: #121212;
	word-wrap: break-word;
}

#reply-control .contents .wmd-input, #reply-control .contents .wmd-preview {
	color: #fff;
}

body .boxed.white {
	background-color: #222;
}

.admin-controls {
	background-color: #333;
	padding: 10px 10px 3px 0;
}

.admin-contents table th {
	border-top: 1px solid #333;
}

.admin-contents table td {
	border-bottom: 1px solid #333;
	border-top: 1px solid #333;
}

}
