css:
@-moz-document domain(weibo.com) {
#v6_pl_rightmod_rank,
#v6_pl_rightmod_recominfo,
#v6_pl_rightmod_attfeed,
#v6_pl_rightmod_ads36,
#v6_trustPagelet_recom_member,
#v6_pl_rightmod_noticeboard,
#v6_pl_ad_bigday,
#v6_pl_ad_forfqy {
    display: none;
}
.PCD_header .banner_transition {
    opacity: 0.1;
}
.WB_tab_a .tab .b .W_arrow_bor i {
    border-width: 7px;
    margin-left: 2px;
}
.WB_tab_a .tab_box_a_r2_s .tab .li_last {
    width: 232px;
    margin: 0;
}
.WB_tab_a .tab_box_a_r2_s .tab li[filter-type="hot"] {
    width: 72px;
}
.WB_tab_a .tab_box_a_r2_s .tab li[filter-type="hot"] .b .b1 .r i {
    width: 37px;
}
.WB_tab_a .tab_box_a_r2_s .tab li[node-type="moremenu"] {
    width: 98px;
}
.S_bg1 {
    background: none;
    border: 1px solid #f2f2f5;
    border-radius: 10px;
}
.WB_feed_together .wft_hd .wft_info {
    background-color: rgba(255, 255, 255, 0.8);
    border-radius: 5px;
}
.S_bg2,
blockquote,
.W_btn_b,
.W_input,
.SW_fun_bg {
    background-color: rgba(255, 255, 255, 0.3);
}
.S_bg2_br {
    border-color: rgba(255, 255, 255, 0.3);
    color: rgba(255, 255, 255, 0.3);
}
.S_link1 {
    color: #333;
}
.S_txt2 {
    color: #fff;
}
.WB_cardwrap {
    border-radius: 15px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.3);
}
.WB_feed_spec_pic {
    border: none;
    border-radius: 0px;
}
.WB_info a {
    color: #000;
}
.WB_notes {
    background: rgba(255, 255, 255, 0.5);
    margin: 0 0 10px;
    padding: 2px 0 0;
}
.WB_notes a {
    color: #333;
}
.WB_text {
    color: #000;
}
.WB_text a[extra-data="type=atname"] {
    color: #fff;
}
.WB_text a[extra-data="type=topic"] {
    color: #fff;
}
.send_weibo .ficon_swtxt {
    color: #fff;
}
.send_weibo .key {
    display: none;
}
body,
.S_page {
    -moz-background-size: cover;
    -webkit-background-size: cover;
    background: url(http://115.28.95.41/image/52333116_p0.jpg);
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size: cover;
}
}
