css:
@-moz-document url-prefix(https://bugzilla.mozilla.org/buglist.cgi?) {

  body:not([class])::before {
    content: "";
    display: block; position: absolute;
    left: 0; top: 0;
    width: 160px; height: 160px;
    background: url(data:image/gif;base64,) 50% 40% no-repeat transparent;
    z-index: -1;
    animation: vs 1s linear infinite;
    -animation: none;
    transform: scaleX(-1) rotate(30deg);
  }
  @keyframes vs {
    0% {left: 66%; margin-top: 5%; transform: scale(0) scaleX(-1) rotate(55deg); visibility: visible;}
    20% {left: 64%; margin-top: 7%; transform: scale(.2) scaleX(-1) rotate(50deg);}
    40% {left: 62%; margin-top: 9%; transform: scale(.4) scaleX(-1) rotate(45deg);}
    60% {left: 59%; margin-top: 11%; transform: scale(.6) scaleX(-1) rotate(40deg);}
    80% {left: 55%; margin-top: 13%; transform: scale(.8) scaleX(-1) rotate(35deg);}
    99% {visibility: hidden}
    100% {left: calc(50% - 40px); margin-top: 14%; transform: scale(1) scaleX(-1) rotate(30deg);}
  }

}
