css:
@-moz-document 
url-prefix("http://steamcommunity.com/profiles/"), 
url-prefix("https://steamcommunity.com/profiles/"),
url-prefix("http://steamcommunity.com/id/"), 
url-prefix("https://steamcommunity.com/id/"),
url-prefix("http://steamcommunity.com/my/"),
url-prefix("https://steamcommunity.com/my/") {

/*Last update on Feb 2019*/

.profile_header_bg_texture { 
background: transparent url("https://i.imgur.com/darQtry.png") no-repeat scroll center center; 
border: 6px solid #FFF; 
min-height: 212px; 
position: relative; 
}

.profile_header .playerAvatar::after { 
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/hGRBfXm.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 85px;
left: -25px;
pointer-events: none;
position: absolute;
right: -25px; 
top: -25px;
}

.profile_header_bg_texture::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/eoDmiVc.png") no-repeat scroll right top / contain ;
content: "";
display: block;
height: 78px;
max-width: 70%;
pointer-events: none;
position: absolute;
right: -6px;
top: -6px;
width: 169px;
}

.profile_customization::after {
background-image: url("https://i.imgur.com/BpFwbLU.png");
height: 147px;
left: -17px;
right: -18px;
top: -20px;
}

.profile_group.profile_primary_group .profile_group_avatar::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/atFfRRK.png") no-repeat scroll right top / contain ;
content: "";
display: block;
height: 68px;
left: -1px;
pointer-events: none;
position: absolute;
right: -1px;
top: 45%;
z-index: 3;
}

.profile_group.profile_primary_group { 
height: 90px; 
}

.profile_group_links .profile_group:nth-child(2), 
.profile_group_links .profile_group:nth-child(3) { 
position: relative !important; 
top: -30px !important; 
}

/*I had to fix that up because of the changes above...*/
.profile_friend_links { 
position: relative !important; 
top: -30px !important; 
}

.profile_customization_header, 
.profile_customization_editlink, 
.profile_customization_edit_icon { 
text-shadow: 1px 1px 1px #000000;
position: relative !important; 
z-index: +5 !important;
color: #FFF; 
}

/*Achievements*/
.profile_customization .achievement_showcase::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/EZLECTa.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 66px;
left: -1px;
pointer-events: none;
position: absolute;
right: -1px;
top: 2px;
z-index: 3;
}

/*Artwork+Screenshot Shotcase*/
.screenshot_showcase:not(.none_selected)::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/EZLECTa.png") no-repeat scroll center top / contain;
content: "";
display: block;
height: 66px;
left: -1px;
pointer-events: none;
position: absolute;
right: -1px;
top: 2px;
z-index: +4; 
}

/*Badges*/
.profile_customization .badge_showcase:not(.none_selected)::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/2mhsCKi.png") no-repeat scroll center top / cover ;
content: "";
display: block;
height: 100%;
left: 0;
pointer-events: none;
position: absolute;
top: 0;
width: 195px;
z-index: 3;
}

/*Custom Box*/
.profile_customization .customtext_showcase::after {
background: url("https://i.imgur.com/BpFwbLU.png") no-repeat scroll 50% 18px / cover ; 
content: "";
display: block;
pointer-events: none;
position: absolute;
height: 155px;
left: -17px;
right: -18px;
top: -20px;
background-size: 95%; 
z-index: 3;
}

/*Favorite Game*/
.profile_customization .favoritegame_showcase::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/EZLECTa.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 66px;
left: -1px;
pointer-events: none;
position: absolute;
right: -1px;
top: 2px;
z-index: 3;
}

/*Favorite Group*/
.profile_customization .favoritegroup_showcase::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/eoDmiVc.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 78px;
max-width: 40%;
pointer-events: none;
position: absolute;
right: 0;
top: 0;
width: 169px;
}

/*Game Collection*/
.profile_customization .showcase_gamecollector_games::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/eoDmiVc.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 78px;
max-width: 40%;
pointer-events: none;
position: absolute;
right: 0;
top: 0;
width: 169px;
}

/*Guide*/
.profile_customization .favoriteguide_showcase::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/2mhsCKi.png") no-repeat scroll center top / cover ;
content: "";
display: block;
height: 100%;
left: 0;
pointer-events: none;
position: absolute;
top: 0;
width: 195px;
z-index: 3;
}

/*Item Showcase*/
.profile_customization .item_showcase:not(.none_selected)::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/eoDmiVc.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 78px;
max-width: 40%;
pointer-events: none;
position: absolute;
right: 0;
top: 0;
width: 169px;
z-index: 3;
}

/*Review*/
.profile_customization .recommendation_showcase::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/eoDmiVc.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 78px;
max-width: 40%;
pointer-events: none;
position: absolute;
right: 0;
top: 0;
width: 169px;
}

/*Trade Up For Trade*/
.profile_customization .trade_showcase::after {
background: rgba(0, 0, 0, 0) url("https://i.imgur.com/eoDmiVc.png") no-repeat scroll center top / contain ;
content: "";
display: block;
height: 78px;
max-width: 40%;
pointer-events: none;
position: absolute;
right: 0;
top: 0;
width: 169px;
z-index: 3;
}

/*Clickable Links forced on top because of the snow*/
a, 
.showcase_notes, 
.bb_spoiler, 
.profile_topfriends, 
.commentthread_count, 
.commentthread_paging, 
.profile_summary_footer { 
position: relative !important; 
z-index: +2 !important; 
}

/*SNOW!!!*/
@keyframes snow { 
0% { background-position: 0px 0px, 0px 0px, 0px 0px }
100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}

@-moz-keyframes snow { 
0% { background-position: 0px 0px, 0px 0px, 0px 0px }
100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}

@-webkit-keyframes snow { 
0% { background-position: 0px 0px, 0px 0px, 0px 0px }
100% { background-position: 500px 1000px, 400px 400px, 300px 300px }
}

@-ms-keyframes snow { 
0% { background-position: 0px 0px, 0px 0px, 0px 0px; }
100% { background-position: 500px 1000px, 400px 400px, 300px 300px; }
}

#footerLogo:after {
background-image: 
url('https://i.imgur.com/bdE40vq.png'), 
url('https://i.imgur.com/eRIWJIw.png'), 
url('https://i.imgur.com/w77croQ.png'), 
url('https://i.imgur.com/qaSyQxH.png');
-webkit-animation: snow 20s linear infinite;
-moz-animation: snow 20s linear infinite;
-ms-animation: snow 20s linear infinite;
animation: snow 20s linear infinite;
background-origin: padding-box;
position: fixed; 
height: 100%; 
width: 100%; 
content: ''; 
left: 0;
top: 0; 
}

}
