css:
@-moz-document domain("codex.wordpress.org") {
body {
    font-family: 'Open Sans', sans-serif;
	background-color: rgba(222, 222, 222, 0.67);
}


#mw-content-text p, #mw-content-text ul, #mw-content-text ol, #mw-content-text dl, #catlinks ul {
    font-size: 20px;
    line-height: 35px;
}



#bodyContent h2 {
    font-size: 2rem;
    padding-top: 1rem;
    padding-bottom: 5px;
    border: none;
    font-family: 'Open Sans';
    font-weight: bold;
    line-height: 2.3rem;
}
#bodyContent h3 {
    font-weight: bold;
}
#bodyContent h4 {
    font-weight: bold;
	margin-bottom: 0;
    border-bottom: none;
}

/* notifications */
#localNotice div {
    /* background-color: #F9F7DE !important; */
    /* padding: 11px !important; */
    
    border: none !important;
    background-color: transparent !important;
    padding: 0px !important;
    font-style: italic;
    color: #777 !important;
}
#localNotice div a {
    color: #777 !important;
    border-bottom: 1px solid #777;
}
#localNotice div .image {
    margin: 0 7px 0 2px;
    display: none;
}


/** page content */
#toc {
    opacity: 0.7;
    background: rgba(241, 241, 241, 0.61);
    border: 1px solid rgba(218, 218, 218, 0);
    padding: 8px 4px 8px 0;
}
#toc #toctitle {
    display: none;
}
#toc ul {
    list-style: none;
    font-size: 12px;
    line-height: 19px;
    margin-left: 12px;
}
#toc ul ul {
    margin-left: 18px;
}
#toc > ul > li {
    padding-bottom: 5px;
}
#toc .tocnumber {
    color: #ccc; 
    display: none;
}
#toc > ul > li > a > .tocnumber {
    display: none;
}
#toc a {
    opacity: 0.9;
}



.LanguageLinks {
    display: none;
}




/** http://stackoverflow.com/questions/5839553/how-can-i-change-the-color-of-the-dot-in-an-unordered-list/18720644#18720644 */
#mw-content-text > ul {
    margin-left: 0;
}
#mw-content-text > ul li {
    color: black;
    list-style-type: none;
}

#mw-content-text > ul li:before {
    color: #444;
    float: left;
    /* margin: 0 .2em 0 -0.6em; */
    margin: 0 0em 0 -2em;
    width: 1em;
    content: '\2014';
}



tt, code, kbd, samp {
    font-family: "Anonymous Pro", monospace;
    background-color: #F7F7F7;
    padding: 2px 4px 2px 6px;
    border-radius: 3px;
}

blockquote {
    background-color: #F7F7F7;
    padding: 11px 22px 0px 22px !important;
    margin-left: -23px;
    border-radius: 8px;
    
}
}
