css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("giessener-allgemeine.de") {

  /* Werbung */
  #superbanner,
  #canvas,
  #IAB_clicktag,
  #billboard_1,
  #medium_rectangle_1,
  #omsv_sky_DhtmlLayer,
  div[id^="ligatusframe"]{
  /* display:none !important; */
  }
  /* Werbung */
 
  /* Struktur */
  body {
    width:auto;
  }
  #header,
  #container,
  #footermenue,
  #copyright { 
    width: 1240px;
  }
  #header {
    height:auto;
    border-bottom:1px solid #666;
  }
  #infobox {
    float:right;
    width:1230px;
  }
  #login_box {
    margin-left: -160px;
    box-shadow: 0 0 10px #666;
    border-radius: 10px;
  }
  #login_top {
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
  }
  #login_bottom {
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
  }
  #menue {
    width:800px;
  }
  #search {
    width:400px;
  }
  #search .searchform {
    padding:2px 10px;
    margin:0;
    width: 290px;
    height:auto;
    border-left:1px solid #666;
    border-right:1px solid #666;
    border-top:none !important;;
    border-bottom:none !important;;
    background-color: rgba(90,90,90,0.4) !important;
    text-shadow: 2px 2px #000;
    border-radius: unset !important;
  }
  #sbb {
    padding:6px 17px;
    margin: 0;
  }
  #search,
  #menue {
    height:30px;
    margin: 0;
    padding: 0 10px;
  }
  #submenue {
    width: 1220px;
    height:auto;
    float:none;
    clear: both;
    padding:0;
    padding:0 10px;
  }
  #submenue a,
  #submenue a.aktiv {
    padding:0 15px;
    margin: 0;
  }
  .ssh {
    padding:0 10px;
    margin: -14px 0 0 0;
    float: none;
    width: auto;
  }
  .ssh + div + span {
    display: none;
  }
  /* Container */
  #container {
    float:none;
    margin: 0 auto;
  }
  #container:after {
    content: ".";
    display: block;
    height: 0;
    clear: both;
    visibility: hidden;
  }
  #logo a img {
    display: none;
  }
  #logo a:after {
    content: "Gießener-Allgemeine.de";
    display: block;
    transform: skewX(20deg);
    transition: transform 0.3s ease-in-out;
    text-shadow: 2px 2px 5px #000;
  }
  #logo a:hover:after {
    transform: skewX(0deg);
  }
  #ssh {
    padding: 20px;
  }
  #left {
    width:840px;
    margin: 0 0 0 10px;
    border:2px solid #666;
    border-radius: 10px;
    box-shadow: 0 0 10px #666;
  }
  #left .galpicturefullcontent_white {
   width: 640px;
  }
  #left .galpicturefullcontent_white .comment_grey,
  #left .galpicturefullcontent_white .commentborder {
    width: 600px; 
  }
  #left .artikel,
  #left .artikel2,
  #left .commentwriteform {
    width:auto;
  }
  #left .artikeldetailansicht a.sendprintlinks img,
  #left .galpicturefulltop_white,
  #left .galpicturefullbottom_white,
  #left .sendprintlinks img {
    display:none;
  }
  #left .line_region,
  form[name=msusport] .spieltag table caption,
  form[name=msusport] .modulname {
    width:auto;
    height:auto;
    border-top-left-radius: 10px;
  }
  form[name=msusport] table {
    width:838px
  }
  .suchebox {
    background-image:none;
  }
  #right .LinkArrow2,
  .LinkArrow1 {
    margin: 1px 0;
    display: inline-block
  }
  .pagenavi,
  #pagenavi {
    float:none;
    width:auto;
  }
  #pagenavi a img {
    display: none;
  }
  /* right */
  #right {
    width:auto;
    padding:0 20px;
  }
  #right .servicebox {
    padding: 0;
    width: 316px;
    border-radius: 10px;
    box-shadow: 0 0 10px #666;
  }
  #right .serviceboxhead > img,
  #right .serviceboxfoot2 {
    display: none;
  }
  #right .serviceboxcontent {
    background-image:none;
  }
  #right .serviceboxcontent {
    width:302px;
    padding:5px;
  }
  #right .serviceboxhead {
    height:auto;
    padding:0 10px;
    width: 296px;
    border-top-right-radius: 10px;
    border-top-left-radius: 10px;
  }
  #right .serviceboxcontent {
    border-left: 2px solid #666;
    border-right: 2px solid #666;
    border-bottom: 2px solid #666;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
  }
  #right .serviceboxcontent .box_ver1,
  #right .serviceboxcontent .servicebox_footer_grey {
    text-align: center;
  }
  #right .serviceboxcontent .servicebox_footer_grey {
    float: none;
    width:auto;
    font-size: 18px;
    line-height: 20px;
  }
  #right .servicebox .linie_punkt {
    background-image: none;
  }
  #footermenue,
  #copyright {
    margin: 0 auto;
    padding:10px 0;
  }
  #footermenue .LinkRotImp img {
    display: none;
  }
  /* Struktur */
  
  /* Schriftgrößen */
  #logo a:after {
    font-size: 28px;
    line-height: 30px;
  }
  #infobox,
  .ssh, 
  .ssh h1,
  form table tbody td.tabs,
  form table tbody td.tabs a,
  form[name=msusport] table {
    font-size:14px;
    line-height: 16px;
  }
  #menue a,
  #menue a:hover {
    line-height: 30px;
    height: auto;
  }
  #search .searchform {
    font-size: 22px;
    line-height: 26px;
  }
  #menue a,
  #menue a.aktiv,
  #menue a:hover {
    font-size: 17px;
    line-height: 26px;
    height:27px;
  }
  #menue a.aktiv:nth-child(2n) {
    border-left:none !important;
  }
  #submenue a,
  #submenue a.aktiv,
  #left .artikel span,
  #left .artikel2 span,
  #left .fettvorspann,
  td.msu_reiter {
    font-size:15px;
    line-height: 20px;
  }
  #left .galpicturefullcontent_white .comment_grey {
    font-size:15px;
  }
  
  h3 {
    font-size: 18px;
    line-height: 20px;
  }
  #left .LinkArrow1,
  #left .artikeldetailansicht a,
  #left .datearticle,
  #left .commentwriteform,
  .medienleiste .medienleiste_onepic,
  .medienleiste_oneline_headline,
  #right .serviceboxcontent h4, 
  #right .serviceboxcontent label,
  #right .serviceboxcontent .servicebox_zweispaltig_right p,
  #right .news_time,
  #right .news_grey,
  #right .news_text,
  #right .LinkArrow1,
  #right .LinkArrow2,
  #right .servicebox_zweispaltig_right a,
  #pagenavi,
  #footermenue,
  #copyright,
  #login a.login_link,
  #left .sendprintboxtop a,
  #left .pic_untertitel{
    font-size:13px;
    line-height: 15px;
  }
  .line_region,
  #right .serviceboxheadline {
    font-size:13px;
    line-height: 20px;
  }
  .lastcomments div {
    font-size:13px !important;;
    line-height: 15px;
  }
  /* Schriftgrößen */
  
  /* Farben */
  body {
    background-color:#101010 !important;
    background-image:none !important;
    color:#ccc;
  }
  #header,
  #menue,
  #search,
  #submenue {
    background-image: none;
    background: linear-gradient(#252525, #444);
  }
  #infobox,
  .ssh, 
  .ssh h1,
  #right .serviceboxcontent,
  h2,
  h3,
  h4,
  label,
  #right .news_time,
  #left .artikel span,
  #left .artikel2 span,
  #left .artikel h2,
  .medienleiste,
  #pagenavi ,
  #copyright,
  #left .fettvorspann,
  #left .teaser span,
  #login input.login_input,
  .contentgenerictd,
  .contentgenerictdright,
  .contentgenerictdkontakt,
  .galpicturefullcontent_white .comment_autor,
  form table tbody td.tabs,
  form table tbody td.tabs b,
  form table tbody tr td b,
  .ueberschrift,
  form table td,
  form[name=msusport] .spieltag table caption,
  form[name=msusport] .modulname,
  .TextSchwarzNormal,
  .galpicturefullcontent_white .comment_grey > div,
  #left .line_region,
  #left .auflistung {
    color:#ccc !important;
  }
  #menue a.aktiv,
  #menue a:hover,
  #submenue a.aktiv,
  #submenue a:hover,
  #container,
  #footermenue,
  #copyright {
    background-color:#252525;
  }
  .tl-slider-background {
    background-color:#00f !important;
    border: 3px solid #00f !important;
  }
  #login_top,
  #login,
  #login_bottom,
  #login form,
  #right .serviceboxcontent .servicebox_footer_grey,
  #left .galpicturefullcontent_white,
  #left .commentwriteform {
    background:#252525;
    color:#ccc;
  }
  #menue a,
  #menue a.aktiv {
    border-right:1px solid #666;
  }
  #menue a.aktiv,
  #submenue a:nth-child(n+2) {
    border-left:1px solid #666 !important;
  }
  #submenue,
  #pagenavi {
    border-top:1px solid #666;
    border-bottom: 1px solid #666;
  }
  #container {
    border-bottom:2px solid #666;
  }
  #left .artikel {
    border-top:none;
  }
  #left .galpicturefullcontent_white {
    border:1px solid #666;
    margin:10px;
    border-radius: 10px;
  }
  #left .pic_artikeldetail_left {
    border:2px solid #666;
    background:none;
    border-radius: 10px;
  }
  #left .pic_artikeldetail_top,
  #left .pic_artikeldetail_bottom {
    display: none;
  }
  .line_region,
  form[name=msusport] .spieltag table caption,
  form[name=msusport] .modulname {
    background: linear-gradient(#252525, #444);
    border-left: 5px solid #666;
  }
  a.LinkArrow1,
  a.LinkArrow2,
  #left .auflistung {
    background-image: none;
    border-left:10px solid #666;
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
  }
  #right .serviceboxhead {
    background-color:#487dbf;
  }
  #right .servicebox .linie_punkt,
  form[name=msusport] tr[bgcolor]:nth-child(2n),
  .artikeldetailansicht span table tr,
  .suchebox {
    background-color:#444;
  }
  form[name=msusport] tr[bgcolor]:nth-child(2n+1) {
    background-color:#666;
  }
   #right .news_grey {
    background: none;
  }
  #right .text_fullsize img {
    background-color:#fff;
  }
  #copyright {
    border-top:2px solid #666;
  }
  /* Farben */

  /* forms */
  #login input[type=text],
  #login input[type=password],
  #left input[type=text],
  #left input[type=password],
  #left input[type=submit],
  #left input[type=reset],
  #left select,
  #right input[type=text],
  #right input[type=password],
  #right input[type=submit],
  #right select,
  #left a.buttonsubmit,
  textarea,
  input[type=text] {
    background-color:#444 !important;
    border:1px solid #666 !important;
    border-radius: 10px;
    padding:1px 10px;
    font-size:14px;
    line-height: 20px;
    padding:0 15px;
    transition: all 0.5s linear;
    color:#ccc;
  }
  #login input[type=text]:hover,
  #login input[type=password]:hover,
  #left input[type=text]:hover,
  #left input[type=password]:hover,
  #left input[type=submit]:hover,
  #left select:hover,
  #right input[type=text]:hover,
  #right input[type=password]:hover,
  #right input[type=submit]:hover,
  #left input[type=reset]:hover,
  #right select:hover,
  input[type=text]:hover,
  input[type=password]:hover,
  input[type=submit]:hover,
  select:hover,
  #left input[type=text]:focus,
  #left input[type=password]:focus,
  #left input[type=submit]:focus,
  #left select:focus,
  #right input[type=text]:focus,
  #right input[type=password]:focus,
  #right input[type=submit]:focus,
  #right select:focus,
  input[type=text]:focus,
  input[type=password]:focus,
  input[type=submit]:focus,
  input[type=reset]:focus,
  select:focus,
  #left a.buttonsubmit:hover {
    background-color:#666 !important;
    border:1px solid #999;
    transition: all 0s linear;
  }
  .formspan,
  .inputbg_normal {
    color:#ccc;
    font-size: 13px;
    line-height: 15px;
  }
  a.buttonsubmit {
    color: #ccc !important;;
  }
  a.buttonsubmit:hover{
    text-decoration: none;
  }
  /* forms */
  /* loginbox */
  .loginbox .loginleft {
    width:150px;
    font-size:13px;
    line-height: 15px;
    background-image: none;
  }
  .loginbox .loginright {
    width:116px;
  }
  .loginright span {
    font-size:13px;
    line-height: 15px;
  }
  .logininput {
    height: auto;
  }
  /* loginbox */
  /* topStats */
  #topStats a {
    color:#fff;
    border:none;
    background: none;
    font-size: 13px;
    height: 18px;
    padding:3px 1px;
  }
  #topStats a:hover {
    text-decoration: none;
  }
  #topStats.topcommented_active .topcommented,
  #topStats.topviewed_active .topviewed, 
  #topStats.topsearched_active .topsearched {
    color: #82e6ff;
  }
  /* topStats */
  /* medienleiste */
  .medienleiste {
    background-image: none;
    border:2px solid #666;
    border-radius: 10px;
    padding:5px 25px;
    margin-bottom:10px;
    font-family: Verdana,Helvetica,Arial,sans-serif;
  }
  .medienleiste > div:first-child,
  .medienleiste > div:last-child {
    display: none
  }
  .medienleiste_oneline_headline {
    border:none;
  }
  .medienleiste .medienleiste_oneline_content,
  .jcarousel-container jcarousel-container-horizontal,
  .jcarousel-skin-leiste .jcarousel-clip-horizontal {
    width:734px;
  }
  .medienleiste_oneline a[id^=galvid-next],
  .medienleiste_oneline a[id^=galvid-prev] {
    display:inline-block;
    background-color:rgb(60, 160, 240);
    border-radius: 10px;
    padding:0;
    transition: all 0.5s linear;
    height: 40px;
    width: 15px;
  }
  .medienleiste_oneline a[id^=galvid-next]:hover,
  .medienleiste_oneline a[id^=galvid-prev]:hover {
    background-color:rgb(130, 230, 255);
    transition: all 0s linear;
    text-decoration: none;
  }
  .medienleiste_oneline a[id^=galvid-next] img,
  .medienleiste_oneline a[id^=galvid-prev] img {
    display:none;
  }
  .medienleiste_oneline_headline {
    background-image: none !important;
    padding-left: 0;
  }
  .jcarousel-skin-leiste .jcarousel-clip-horizontal {
    height: auto;
  }
  .medienleiste_oneline + div {
    border-top:1px solid #666 !important;;
  }
  /* medienleiste */
  #left .lupe {
    background-color: #ccc;
    border-radius: 10px;
    padding:4px;
  }
  /* Veranstaltungen */
  .veranstaltung_searchframe,
  .veranstaltung_searchfoot {
    background-image:none;
    background:#333;
  }
  .veranstaltung_searchframe h2,
  .veranstaltungsblock {
    background:#333 !important;;
  }
  .veranstaltungsblock a {
    font-size:15px;
    line-height: 18px;
  }
  .trenner_rot {
    color:#ccc;
    border-bottom: 1px solid #ccc;
  }
  /* Veranstalungen */
  /* kauebersichtbox */
  .kauebersichtbox {
    border-radius: 10px;
    margin:10px;
  }
  .kauebersichtbox,
  .kauebersichtcontent,
  .kauebersichtend,
  .kauebersichthead span {
    background-image: none;
    background:#333;
  }
  .kauebersichthead span {
    font-size:15px;
    font-size:18px;
  }
  /* */

  /* Links */
  .searchform:hover {
    border-left:1px solid #ccc;
    border-right:1px solid #ccc;
    background-color:#252525;
    color:#fff;
  }
  #sbb:hover {
    background-color: rgba(90,90,90,0.4);
  }
  a,
  .LinkRot,
  #footermenue a,
  #infobox_right a,
  #anmeldung > span,
  .LinkSchwarz,
  .sendprintlinks,
  .datearticle a,
  .line_region a,
  .servicebox_footer_grey a,
  .servicebox_zweispaltig_right a,
  .news_text a,
  .servicebox_footer_grey_middle a,
  .inputbg_normal a,
  .contentgenerictdright a,
  form[name=msusport] a,
  form[name=msusport] a > h1,
  .LinkRotFett,
  .veranstaltungsblock a,
  .kauebersichthead a,
  .select_paper a {
    color:#3ca0f0 !important;
    transition: color 0.4s linear;
    text-shadow: 1px 1px #000;
  }
  a:hover,
  .LinkRot:hover,
  #footermenue a:hover,
  #infobox_right a:hover,
  #logonavi a:hover,
  #anmeldung > span:hover,
  .LinkSchwarz:hover,
  .sendprintlinks:hover,
  .datearticle a:hover,
  .line_region a:hover,
  .servicebox_footer_grey a:hover,
  .servicebox_zweispaltig_right a:hover,
  .news_text a:hover,
  .servicebox_footer_grey_middle a:hover,
  .inputbg_normal a:hover,
  .contentgenerictdright a:hover,
  form[name=msusport] a:hover,
  form[name=msusport] a:hover > h1,
  .LinkRotFett:hover,
  .veranstaltungsblock a:hover,
  .kauebersichthead a:hover,
  .select_paper a:hover {
    color:#82e6ff !important;
    transition: color 0s linear;
    text-decoration: none;
  }
  #pagenavi a {
    color:#000;
    background-color:#3ca0f0;
    border-radius: 10px;
    min-width: 60px;
    min-height: 16px;
    padding:2px 10px;
    display: inline-block;
    transition: background-color 0.6s linear;
  }
  #pagenavi a:hover {
    text-decoration: none;
    background-color:#82e6ff;
    transition: background-color 0s linear;
  }
  /* Links */


}
