css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aimware.net") {
	body{
		background-color: #333;
		color:  #fff;
		border: none;
	}
	
	#header{
		background-color: #333;
		border: none;
		margin-top:19px;
	}
	
	#top_header{
		background-color: #333;
		border-top: 1px solid #fff;
		border-bottom: 1px solid #fff;
	}
	
	#footer, #footer .upper, #footer  .lower {
		background-color: #333;
		color:  #fff;
	}
	
	#header #logo,  #header #panel  .lower{
		background-color: #222
	}
	
	#container #header #panel  .upper{
		background-color: #131313;
		
	}
	
	#container #header #panel .upper #search .button{
		background-color: #333;
		border:none;
		border-radius: 0px;
	}
	
	#content_container{
		background-color: #222;
		border: none;
		color: #fff;
	}
	
	.cheat_list_table,	.cheat_list_table a{
		color: #fff;
		border: none;
		border-radius: 0px;
		background-color: #333;
		background: #333;
	}
	
	#sidebar{
		color: #fff;
		background-color: #222;
		border-right: 4px solid #222;
		border-left: none;
	}
	
	#sidebar .tab,#sidebar .ribbon-full{
		background-color: #333;
	}
	
	.text_field_2{
		background-color: #222;
		color: #fff;
	}
	
	#sidebar blockquote{
		color: #fff;
	}
	
 h1.format_text_red, h2.format_text_red {
		color: #fff;
		border-bottom: 1px solid #fff;
	}
	
		#container #header #panel .upper #search .textbox{
			border-radius: 0px;
		}
	
	#header #logo  div a img{
		display: block;
	 
	}
	
	#content .wrapper, #content .wrapper a, #content .wrapper .navigation .active{
		border: none;
		color: #fff;
	}

	.content #wrapper, #content .wrapper, #container #content .wrapper .tborder, .tcat,  #container #content .wrapper .tborder #inline_row, .tborder #posts_container #posts, .editor_control_bar{
		background-color: #222;
		box-shadow: 0 0 0;
		color: #fff;
		
		border-radius: 0px;
	}
	
	.pm_alert{
		background: #222;
		border: 1px solid #fff;
		border-radius: 0px;
	}
	
	.post .post_author{
		background-color: #333;
	}
	
	.post_controls{
		background-color: #333;
		
		border-radius: 0px;
		color: #fff;
	}
	
	.codeblock{
		background: #222;
	}
	
	code{
		background: #333;
	}
	
	.post_body{
		color: #fff;
	}
	
	.pagination, .pagination .pagination_current, .pagination .pagination_previous, .pagination .pagination_page, .pagination .pagination_next, .pagination a{
		background: transparent;
		color: #fff;
	}
	
	.postbit_buttons a:link{
		background: none;
	}
	
	#container #content .wrapper #notice_box {
		background-color: #333;
		box-shadow: 0 0 0;
		border: none;
	}
	
	.trow1, .trow2, .tfoot{
		background: #333 none repeat scroll 0% 0%;
		border-radius: 0px;
		color: #fff;
	}
	
	.thead{
		background-color: #333;
		border: none;
	}
	
	.trow_sep{
		background-color: #444;
		color: #fff;
	}
	
	fieldset, fieldset table{
		color: #fff;
		border-radius: 0px;
	}
	
	blockquote{
		background-color: #333;
		border-radius: 0px;
	}
	
	.container .wrapper #quick_reply_form .tborder #quickreply_e{
		background-color: #333;
	}
	
	textarea{
		border-radius: 0px;
	}
	
	#wrapper #slider{
		border: none;
		background-color: #222;
		background-image: none;
	}
	
	.order_table_td .tab{
		background-color: #333;
	}
	
	.order_table_td_unb .button_payment{
		border: none;
		background: #333;
		background-color: #333;
	}
	
	#wrapper form .bit_button{
		border: none;
		background: #333;
		background-color: #333;
	}
	
	.arrow_box{
		border: none;
		border-radius: 0px;
		background-color: #222;
		padding: auto;
		border: 1px solid #333;
	}
	
	#header .container nav a:hover{
		background-color: #222;
	}
	
	table{
		color: #fff;
	}
	
	.cheat_list_table .cheat_list_td .sup .new{
		background: none;
		background-color: #222;
	}
		
	nav ul li:hover a{
		background-color: #222;
	}
	
	#wrapper #content_container #content_full form div{
		background: none;
	}
	
	#go_page_1_popup{
		background: #222;
		border-radius: 0px;
	}
	
	#go_page_1_popup .textbox{
		border-radius: 0px;
	}
	#go_page_1_popup .button{
		border: none;
		border-radius: 0px;
		background: #333;
		color: #fff;
		padding-left: 20px;
		padding-right: 20px;
	}
	
}
