css:
@-moz-document url-prefix("http://www.grafikart.fr/") {
body{
    background-color: #222 !important;
}

.topbar{
    background: #191919;
}

.table tr:nth-child(2n) {
    background-color: #171717;
}
.CodeMirror-lines {
    color: #CECECE;
}

.table {
    background-color: #222;
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.26);
}
.formations-last_content {
    background-color: #222;
}
.header{
	background-color: #222;
}
.forum_title {
    color: #9E9E9E;
    font-weight: bold;
}
.grey .header {
    background-color: #222;
}
a {
    text-decoration: none;
    color: #C5C5C5;
}
.forum-bloc-mono {
    background: #2D2D2D;
}
.forum-post_body {
    border: 1px solid #000;
    background-color: #222;
    padding: 20px 20px 0 20px;
    margin: 0 10px 0 70px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    -moz-box-shadow: 0 4px 4px rgba(243,243,243,0.56);
    -webkit-box-shadow: 0 4px 4px rgba(243,243,243,0.56);
    box-shadow: 0 4px 12px #000;
}
.restform p{
	color: #7B7B7B;
}
.mdeditor_body {
    background-color: #222;
}
.grey .form-control {
    background-color: #2D2D2D;
}
.sidebar .sidebar-wave {
    fill: #191919;
}
.sidebar-menu a {
    border-top: 1px solid rgba(255,255,255,0.05);
    border-bottom: 1px solid #000;
    color: #797979;
}
.title{
    color: #D6D6D6;
}
.sidebar-menu a.sidebar_logout {
    background: #191919;
}
.sidebar-avatar {
    border: 6px solid #101010;
}
.topbar.is-scrolled {
    background-color: rgba(34, 34, 34, 0.98);
}
.footer{
        background-color: #191919;
}
.forum-post_avatar {
    -moz-box-shadow: 0 1px 9px #e0e0e0,inset 0 0 2px rgba(215,215,215,0.75);
    -webkit-box-shadow: 0 1px 9px #e0e0e0,inset 0 0 2px rgba(215,215,215,0.75);
    box-shadow: 0 1px 9px #000,inset 0 0 2px rgba(0, 0, 0, 0.75);
}
.forum-bloc-mono .forum_name {
    font-size: 16px;
}
.tutoriel_title {
    color: #222;
}
.forum-category-title {
    margin: 20px 0 10px 0;
    font-size: 1.2em;
    color: #D6D6D6;
    font-weight: bold;
}
.footer_title{
    color: #BBB;
}
}
