css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("memedroid.com") {
body {
  background:#000;
}
  
  #container-content {
    background:#000;
  }
  
  #memes-container .memes {
    background:#000;
  }
  
  .meme .header-meme, #general-container .general-header {
    background:#000;
    width: 528px;
  }
  
  .header-meme a {
    color:#fff;
  }
  
  .meme {
    border: 1px solid #A5C63B!important;
}
  
  #ads_follow {
    display: none;
  }
  
  #right-column-content .box .box-content {
    background:#000;
  }
  
  #right-column-content .box .box-top {
    background:#000;
  }
  
  #right-column-content .box .box-bottom {
    background:#000;
  }
  
  #right-column-content .box {
    border: 1px solid #A5C63B;
  }
  
  #right-column-content .box h4 {
    color: #fff;
  }
  
  .tags a {
    background: #000 none repeat scroll 0% 0%;
border: 1px solid #595959;
box-shadow:none;
color: #595959;
  }
  
  .tags a:hover {
        background: #000 none repeat scroll 0% 0%;
    color: #A5C63B;
    border: 1px solid #A5C63B;
}
  
  .meme .actions-meme {
    background: #000;
    width:528px;
  }
  
  .meme .actions-meme .social_sharing_left {display: none}
  
  #memes-container .memes-paginator:first-child, #general-container .error-top, #general-container .general-top {
    background:#000;
    border: 1px solid #A5C63B;
  }
  
  #memes-container .memes-paginator, #general-container .error-bottom, #general-container .general-bottom {
    background:#000;
    border: 1px solid #A5C63B;
  }
  
  #header #header-menu-general {
    width: 680px;
  }
  
  #general-container .error-middle, #general-container .general-middle {
    background: #000;
    border: 1px solid #A5C63B;
  }
  
  .form {
    color:#fff!important;
  }
  
  #login-options {
    background: #000;
  }
  
  .ad-boxpursuit {display: none}
  
  #container-footer {display: none}
  
  .user-share-profile {display: none}
  
  .user-header h3 {
    color:#fff!important;
  }
  
  .user-data-profile-container .user-data-profile-middle {
    background:#000;
  }
  
  .user-data-profile-container .user-data-profile-top {display: none}
  
  .user-data-profile-container .user-data-profile-bottom {display: none}
  
  .user-data-profile-container .container-table-rank table {
    background:#000;
  }
  
  .user-data-profile-container .container-table-statistics table, .user-data-profile-container .container-table-medals table {
    background:#000;
  }
  
  .user-data-profile-container .container-table-statistics table .header-col, .user-data-profile-container .container-table-medals table .header-col {
    color:#fff;
  }
  
  .user-data-profile-container .container-table-rank table td {
    color:#fff;
  }
  
  .form-comment h4 {
    color:#fff;
  }
  
  .meme .header-meme h3, .meme .header-meme h2 {
    color:#fff;
  }
  
  /*.meme .actions-meme .social_sharing_right, .meme .actions-meme .comment-meme-container {display: none}*/

}
