css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(last.fm) {
.header-background {
background-image: url(data:image/png;base64,) !important;
}
  
.profile-header-set-obsession, .header-featured-track, div.nag-bar, .grid-items-item-playlink, .image-overlay-playlink-link {
display:none !important;
}

* {
border-radius:0% !important;
}
  
.tasteometer-avatar {
border-radius:50% !important;
}
  
.masthead-logo {
display:none !important;
}

.header-scrobble-since {
color:#ffffff !important;
}
  
.countbar-bar-slug {
background: #48779A !important;
}
  
div.feedback-message, .copyright.col-xs-12, .col-xs-2 {
display:none !important;
}

.overlay-leaderboard {
display:none !important;
}
  
.overlay-content {
margin-top:100px !important;
}

.overlay-control-bar {
top:50px !important;
}

div.overlay-side-rail {
margin-top:111px !important;
}
  
.widget_listeningnow {
display: none !important;
}

.user-follow {
display: none !important;
}

.featured-item--truncated {
display: none !important;
}

.suggest-correction-cta {
display: none !important;
}
  
.subscribe-cta {
display: none !important;
}

.shout--deleted {
display: none !important;
}
 
.alert-warning {
display: none !important;
}
  
.gallery-upload-rules {
display: none !important;
}

.js-close-player {
display: none !important;
}
  
#_evh-link {
display: none !important;
}

/* Last.year */
  
.user-dashboard-banner-heading,.user-dashboard-banner.link-block.js-link-block {
display: none !important;
}

.user-dashboard-module-share {
display: none !important;
}

.user-dashboard-musical-matches, .user-dashboard-mainstream {
display: none !important;
}

.share-footer {
display: none !important;
}

.clearfix.content-form.section-with-settings.content-form {
margin-top: 0px;
}

/* Chart List */
  
.chartlist-image, .image-overlay-playlink-link {
display:none !important;
}
  
.chartlist-index, .chartlist.chartlist--has-image td, .chartlist-ellipsis-wrap, .chartlist-countbar, .chartlist-name, .chartlist-ellipsis-wrap, .chartlist-timestamp,  .chartlist-delete {
padding-top:1px !important;
padding-bottom:1px ! important;
}

.chartlist-play {
height:100% !important;
}
  
.chartlist-name {
height:100% !important;
}
  
.chartlist-image, .image-overlay-playlink-link {
display:none !important;
}
  
/* Player Bar */

.player-bar-playback-options-button, .player-bar-playback-options-mega-menu {
display: none !important;
}
  
.player-bar-progress {
height: 4px !important;
}
  
/* Recommandations */

.recommended-next-page-cover-link, .recommended-next-page--artist , .recommended-next-button {
display:none !important;
}
.recommended-next-page--track , .recommended-next-page-artist , .recommended-next-page-title {
display:none !important;
}
  
/* Social Links */ 

.share-desktop {
display: none !important;
}

.share-button-profile {
display: none !important;
}

.share-button-library {
display: none !important;
}

/* Listening Trends */  
    
.kerve .trendwave_inner .bar, .kerve a.btn_trendnav.current, .kerve .trenduser, .kerve a.btn_trendnav.current, .kerve .trenduser {
background: #48779A !important;
}

 /* Buy Links */
 
.lazy-buylinks-toggle--with-icon, .lazy-buylinks-toggle, .amazon-music-deal {
display: none !important;
}

#buy {
display: none !important;
}

div[id*="lazy-load"] {
display: none !important;
}

/* Ads */

.mpu-subscription-upsell--mpu {
display: none !important;
}

.cf_amazon_plain, #tonefuze-desktop, #leader_top, #mpu_top, #mpu_bottom, .related-ads, #leader_bottom, .related-content, .yahoo-search-ads, .yahoo-search-ads iframe, .features-promoted, .mobile_pos_1, .mobile_pos_2, .mobile_pos_3, .mobile_pos_4 {
display: none !important;
}

.features-footer {
display: none !important;
}

.trending-tracks {
display: none !important;
}

.lastfm-ad
{
  display: none!important;
}

.full-bleed-ad-container, .sidebar-ad-container
{
  display: none!important;
}

/* Useless things */

.trending-tracks-section-wrapper{
display:none!important;
}

.lazy-features-page-bottom{
display:none!important;
}

.footer{
display:none!important;
}

}
