css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("videosift.com") {

.lolite,.hilite, .contents {background:#222 none repeat scroll 0 0 !important;
border:1px solid #444 !important;
}

a, .post-summary {color: #aaa !important;}
a:hover {color: #ddd !important;}

body {background: #000 !important;}
#header {background:#000000 !important;}

.active {background: #333 !important; border: 1px solid #333 !important; border-bottom-width:0px !important;}
.inactive {background-color: #aaa !important;border: 1px solid #444 !important; border-bottom-width:0px !important;background-image:url(http://i36.tinypic.com/2md1nyt.png) !important;}
.active a {color: #ddd !important;}
.inactive:hover {background: #333 !important;}
.inactive:hover a {color: #eee !important;}
.r4, .r3, .r2, .r1 {background:#333 !important; border:0px !Important;} 

.niftycorners {background:#000 !important;}

.pager {}

.votebox {color: #ddd !important;}

#power-points {border: 0px !important; top: 0px !important; display: block !important;}

#synergy {display: block !important; background-color: #333 !important; border-bottom: 1px solid #444 !important;}

.main-body .paged-contents {
background:#222 none repeat scroll 0 0 !important;
border-color:-moz-use-text-color #444 !important;
border-style:none solid !important;
border-width:0 1px !important;
padding-bottom:15px !important;
padding-top:15px !important;
}

a img {border: 0px solid #444 !important;}

.main-body .paged-contents hr, #content hr{border-color: #222 !important; color: #333 !important; height: 0px !important}

.postcontainer {background-color:#000 !important;
border:1px solid #444 !important;
overflow:hidden !important;
padding-top:15px !important;
padding-bottom:15px !important;
padding-left: 10px !important;
background-image:url(http://i36.tinypic.com/2q2oap1.gif) !important;
background-repeat: repeat-x !important;
color: #aaa !important;
}

.talk-body{color: #aaa !important;}

.talk-body a{color: #ddd !important;}

.talk-body a:hover{color: #fff !important;}

.lolite,.hilite {background-image:url(http://i36.tinypic.com/2md1nyt.png) !important; background-repeat:repeat-x !important;
}
.contents {background: transparent !important;border: none !important; color: #aaa !important;}

#content {color:#ddd !important;}

p strong, .talk-body strong {color:#ddd !important;}

.comment-body{border: 1px solid #444 !important; color: #ddd !important; background-image:url(http://i36.tinypic.com/df85q9.png) !important;}

.comment-popup{background: #000 !important; border: 1px solid #444 !important;}

legend {color: #aaa !important;}

fieldset {border: 1px solid #444 !important;}

#chat-log, #chat-links, #chat-users, #chat-msg, #chat-send, imput.button, textarea, #vidtitle, #tags {background-color:#222 !important;border-color:#444 !important; color: #aaa !important;}

#chat-msg {color: #aaa}

select {background-color:#222 !important;border:1px solid #444 !important;color:#aaa !important;}

#lounge {background-color:#222 !important; border:1px solid #444 !important;color:#aaa !important; font-size: 12px !important;}

#main-profile .bio a, .post-summary a {color: #ddd !important;}
#main-profile .bio a:hover, .post-summary a:hover {color: #fff !important;}

#invoDiv {background-color:#222 !important; border:1px solid #444 !important; background-image:url(http://i36.tinypic.com/2md1nyt.png) !important; color: #aaa !important;}

#involinks a {background-color:#222 !important; background-image:url(http://i36.tinypic.com/2md1nyt.png) !important;}
}
