css:
@-moz-document url-prefix("https://my.bigpond.com/mybigpond/myaccount/myusage/daily/default.do") {
#bx-page {
	background: #090755;
}

body, select, input, textarea, th, td {
	color: #999;
	font-family: Arial, Helvetica, sans-serif;
}

#bx-page-body-2 {
	padding-top: 10px;
	background-color: #111;
	min-height: 366px;
}

#bx-page-header-banner {
	display: none;
}

#bx-page-content-1 .bx-clear {
	display: none;
}

#bx-page-content-2 > div.bx-col-center > div > div.bx-row.bx-first > section.bx-s-64.bx-first {
	width: auto;
}

.ui-corner-all {
	-moz-border-radius: 0px;
	-webkit-border-radius: 0px;
	border-radius: 0px;
}

.ui-state-default {
	background-color: #222 !important;
}

.ui-state-active {
	background-color: #444 !important;
}.ui-state-active .bx-tab-label {
	color: #AAA !important;
}
.bx-tab-label {
	color: #999 !important;
}
.ui-corner-top {
	-moz-border-radius-topleft: 0px;
	-webkit-border-top-left-radius: 0px;
	border-top-left-radius: 0px;
	-moz-border-radius-topright: 0px;
	-webkit-border-top-right-radius: 0px;
	border-top-right-radius: 0px;
}

#bx-page > .bx-first {
	border-radius: 0px;
	background: #111;
	padding: 0px;
}

#tmx-header {
	display: none;
}

.usage-table table {
	width: 100% !important;
}

.usage-table .usageScale {
	width: auto !important;
	margin-left: 1%;
}

.usage-table table tbody {
	width: 100% !important;
}

.bx-col-right {
	display: none;
}

.bx-616 {
	width: 98%;
	margin-left: 1%;
}

.bx-col-center {
	float: left;
	overflow: hidden;
	width: 100%;
}

#bx-page > .bx-last {
	border-radius: 0px;
	background-color: #222;
}

table.usageScale tbody tr td:nth-child(2) table tbody tr:nth-child(1) td:nth-child(3) {
	background-color: #DDD;
}

table.usageScale tbody tr td:nth-child(2) table tbody tr:nth-child(2) {
	background-color: #666;
}

#bx-page-head section:nth-child(2) .bx-last {
	display: none;
}

body footer {
	display: none;
}

.bx-616 .bx-s-11 {
	width: 96%;
}

#bx-page-header, .bx-page-body, #bx-page-footer {
	width: 100%;
}

tr[bgcolor='#EBF3F9'] {
	background-color: #060C14 !important;
}

tr[bgcolor='#CDE1F1'] {
	background-color: #070E17 !important;
}

tr[bgcolor='#E8F1F8'] {
	background-color: #0E1E32 !important;
}
}
