css:
@-moz-document domain("www.baidu.com") {
/**Ads**/

#content_right,
.c-recommend,rrecom-btn-parent,
body > div[class="result-op xpath-log"],
#content_left > * {
    display: none !important;
}
#content_left > .c-container,
#content_left > [class^="result"] {
    display: block !important;
}
/**
*Baidu Bear
**/

#baidu_bear,.bd_bear_home_nocard {
    display: none !important;
}
* {
    font-family: 方正兰亭黑, 微软雅黑 !important;
}
.shouji,
.EC_mr15,
.ec_bdtg,
#ec_im_container,
td[align="left"]>div:nth-child(1) {
    display: none!important;
}
a {
    text-decoration: none !important;
    transition: all ease-out 0.05s;
}
#wrapper_wrapper a,
#u a {
    color: #3388FF !important;
    text-decoration: none !important;
}

#wrapper a:visited {
    text-decoration: none !important;
}
body {
    background: #fff!important;
    color: #666;
    -webkit-transition: none!important;
    -moz-transition: none!important;
    transition: none!important;
    margin: 0px !important;
    padding: 0px !important;
}
body:before {
    content: "";
    position: fixed;
    bottom: -10px;
    left: 0;
    width: 100%;
    height: 10px;
    z-index: 999;
    box-shadow: 0 0 30px 10px #fff;
}
.favurl {
    background-position: 3px 4px;
}
.f {
    width: 35em !important;
}
#app-list-scene,
#app-rcmd {
    height: auto !important;
}
.f >.t {
    padding-bottom: 3px !important;
}
h3.t {
    border-bottom: thin solid #ccc;
    margin-bottom: 0px!important;
}
#content_left>table> h3.t + *,
#content_left>div > h3.t + *,
.op_tieba2_container {
    border-top: thin solid rgba(255, 255, 255, 0)!important;
    margin-top: 0px!important;
    padding-top: 3px!important;
}
h3.t a:first-child {
    font-weight: 500;
    border-left: thick solid #3388FF;
    padding-bottom: 3px !important;
}
h3.t a:first-child:before {
    content: "|";
    color: transparent;
}
h3.t a:first-child:visited {
    border-left: thick solid #ccc;
    color: #999;
}
h3 a,
a em {
    text-decoration: none !important;
}
#wrapper_wrapper .c-text-public {
    color: #fff!important;
}
.ecl-weigou-header-inner {
    overflow: hidden;
}
#wrapper a.ecl-weigou-filter-first,
#wrapper .s-mod-hotnews-img-title a,
#wrapper a.icon-gw,
#wrapper #new_weather_air_link {
    color: #fff!important;
}
.anchorBL {
    display: none;
}
.opr-jigou-danpoi-map div,
.opr-jigou-danpoi-mapContainer div {
    -webkit-transition: all ease-out 0.2s;
    -moz-transition: all ease-out 0.2s;
    transition: all ease-out 0.2s;
}
.BMap_stdMpCtrl {
    -webkit-transition: none;
    -moz-transition: none;
    transition: none;
}
.f>div {
    padding-left: 8px;
}
div.likebox {
    padding: 0px;
}
/*顶部搜索区*/

#head {
    background: #fff;
    border-top: 2px solid #3388FF;
    transition: box-shadow 0.5s;
    box-shadow: 0 0 10px #f9f9f9;
}
#head.s_down {
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.25)!important;
}
/*搜索框*/

.s_ipt_wr,
form > #kw {
    transition: box-shadow 0.5s;
    box-shadow: 0 0 1px #888;
}
.s_ipt_wr:hover,
form > #kw:hover {
    background: none!important;
    box-shadow: 0 0 1px #3388FF;
}
/*搜索按钮*/

.s_btn {
    box-shadow: 0 0 3px #888!important;
    outline-color: rgba(0, 0, 0, 0)!important;
    border: none!important;
    transition: all 0.5s;
}
.s_btn:hover {
    box-shadow: 0 0 5px #3388FF!important;
}
.s_btn:active,
#su:active {
    -moz-transform: scale(0.95, 0.95);
    -webkit-transform: scale(0.95, 0.95);
    box-shadow: 0 0px 2px #3388FF!important;
    -webkit-transition: all 0.1s;
    -moz-transition: all 0.1s;
    transition: all 0.1s;
}
/*用户条*/

#u {
    -webkit-transition: none!important;
    -moz-transition: none!important;
    transition: none!important;
    top: -3px;
}
#u a,
#u em {
    text-decoration: none !important;
}
#nv {
    height: 24px;
}
/*相关搜索*/

#rs {
    background: #f9f9f9 !important;
    position: relative;
    border-top: 2px solid #3388FF;
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.25);
    -webkit-transition: none!important;
    -moz-transition: none!important;
    transition: none!important;
    padding: 0 0.5em!important;
    box-sizing: border-box;
}
/*导航条*/

#s_tab {
    position: relative;
    background: #f9f9f9;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.25);
}
#s_tab>* {
    position: relative;
    top: 1px;
}
#s_tab b {
    color: #3388FF !important;
    text-decoration: none !important;
}
#s_tab b:after {
    content: " ";
    position: absolute;
    bottom: -5px;
    left: 50%;
    margin-left: -3px;
    border: 3px solid #3388FF;
    border-left-color: transparent;
    border-top-color: transparent;
    transform: rotate(45deg);
}
#s_tab a,
#nv a {
    color: #777 !important;
    text-decoration: none !important;
}
#s_tab a:hover {
    color: #000000 !important;
    text-decoration: none !important;
}
#nv b {
    text-decoration: none !important;
    border-bottom: medium solid #3388FF !important;
}
#content_left em {
    color: #d35 !important;
    font-weight: bold;
}
#new_weather_air_link {
    color: #fff !important;
}
.mod-pk a,
.s-weather-border a,
.weather-ok a,
#bottom_container a {
    color: #999 !important;
}
@-webkit-keyframes a001 {
    0% {
        -webkit-transform: translateY(64px);
        opacity: 0;
    }
    50% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}
#content_right {
    -webkit-animation-name: a001;
    -webkit-animation-duration: 1s;
    -webkit-animation-timing-function: ease;
}
#content_left>* {
    width: 550px;
    margin-bottom: 20px!important;
    transition: all ease-out 0.25s;
    box-shadow: 0 0px 0px 5px rgba(255, 255, 255, 0);
}
h3.t a:first-child:hover {
    border-left-width: 10px;
}
#content_left>*:active {
   text-shadow: 1px 0.5px 5px rgba(0, 0, 0, 0.25);
	transition: all ease-out 0.1s;
}

#content_left>table table {
    margin: 0;
    padding: 0;
    box-shadow: 0 0px 0px rgba(0, 0, 0, 0.25);
}
.op-zx-mussong-ctrl span,
.op-zx-mussong-ctrl div {
    -webkit-transition: all ease-out 0s;
    -moz-transition: all ease-out 0s;
    transition: all ease-out 0s;
}
#content_left .c-btn-primary,
#op_wiseapp_download a,
#wrapper .c-btn {
    color: #FFF!important;
}

/**翻译**/

.c-border {
    padding: 15px;
}
}
