css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filelist.ro") {
DIV[class="statusbar"]:nth-child(2)>DIV:nth-child(2)>DIV:nth-child(1) { position:absolute!important; top: 2px !important; left: 1%!important;} /* welcome */
DIV[class="statusbar"]:nth-child(2)>DIV:nth-child(2)>DIV:nth-child(2) { position:absolute!important; top:20px !important; left: 1%!important;} /* message */
DIV[class="statusbar"]:nth-child(2)>DIV:nth-child(2)>DIV:nth-child(4) { position:absolute!important; top:22px !important; right: 0%!important;} /* ratio */
DIV[class="statusbar"]:nth-child(2)>DIV:nth-child(2)>DIV:nth-child(5) { position:absolute!important; top:22px !important; right: 30%!important;} /* up down */ 
DIV[class="statusbar"]:nth-child(2)>DIV:nth-child(2)>DIV:nth-child(3) { position:absolute!important; top: 2px !important; right: 0%!important;} /* logout */
div.statusbar > div.clearfix{height: 18px;box-shadow:0 0 2px #895252;}
.clearfix {box-shadow:0 0 1px #895252;}
table td{background:none!important;}
.main {height: 135px; box-shadow: 0 0 8px #66CCFF;} /* comment border */
.mainheader {height: 75px;background: none !important;}
#container {padding-top: 0px;} 
.alert {width: 11em;position: fixed !important;top: 5px;left: 5px;}
table.main, table.mainouter{background: none !important;}
.globalFreeLeech {color: #3dff4f;}
.cblock-top{margin-top:0px!important;background: none !important}
.cblock-content{padding-top: 1px!important;background: none !important;}
.status_avatar img {width: 0px; height: 0px;} /* avatar */
.logo {width: 0px; height: 0px;}
.torrenttable{color: gray !important; padding: 0px;}
/*.visitedlinks a:visited{color: yellow !important; }*/
.visitedlinks a:hover { color: whitesmoke !important; }
a.gototop{position: fixed !important; top: 318px; left: 29px!important;font-size:11px!important;} /*go to top*/
a:hover {color:white}
html body {font-family: Tahoma !important;}
select{background: gray; border-color:gray; border-radius:0px;}
input:focus {border-color:gray;}
input{background: gray; border-color:gray; border-radius:0px; height: 20px;}
input[value="Cauta!"], input[value="Categorii"], input[value="Login"]{height: 27px; background: gray; font-size: 9px; border-radius: 0;}
input[value="Cauta!"]:hover, input[value="Categorii"]:hover, input[value="Login"]:hover{height:27px; background: burlywood;}
input[type="checkbox"]{position:relative; top: 6px !important;}
#nav{position: relative; height: 1px;font-size: 12px !important;}
#nav ul li, #nav ul li a {color: #1598f4;} /* bar inactive*/ 
#nav ul li.active, #nav ul li a:hover, #nav ul li.active a {color:#F1EB7D;} /* bara navigare active*/

#nav ul li a{
  padding: 2px;
  padding-top: 1px;
  padding-bottom: 1px;
  transition: background-position .1s ease-in;
  background-size: 100% 250%;
  background-image: linear-gradient(to bottom , transparent 40%, #807e7e 78%);}

#navigation{
  position: fixed !important;
  left:1px;
  right:1px;
  margin-left: 5px;
  margin-right:5px;
  top: 110px;
  width: 85px;
  height: 228px;
  border-width: 1px 1px 1px 1px;
  border: 1px solid#807e7e;
  background:none;}

.statusbar{
  position:absolute!important;
  left:0px;
  right:0px;
  margin-left:auto;
  margin-right:auto;
  margin-top: 11px;
  width:70%!important;
  height:40px!important;
  border-width: 1px 1px 1px 1px;
  border: 1px solid#807e7e;
  background:none;
  padding: 0px 0px 0px 0px;
  font-size: 9px !important;}
}

@-moz-document url("http://filelist.ro/login.php") {
body{background-image:url("http://i1382.photobucket.com/albums/ah271/Pc4jat/Stylish/FileListLogin.jpg") !important;background-size: 100% 180% !important}
  input {max-width: 200px;} /* user and pass */
  input.btn{font-size: 11pt; background: border-box; color:#00FF81 !important; box-shadow: 0  0 11px #66CCFF;} /* login button */
  input.btn:hover{ font-size: 8pt; background: border-box;}
  div.clearfix:nth-child(2){width: 0px !important;}
  div.clearfix:nth-child(9){width: 0px !important;}
  .main {width: 250px; height: 110px !important;margin-top:-80px;box-shadow: 0  0 22px #66CCFF; background: border-box;}
  .main > DIV:nth-child(2) > DIV:nth-child(1) {width: 0px; color:transparent !important;}
  .main > DIV:nth-child(2) > DIV:nth-child(4) {width: 0px !important;}
  .main > DIV:nth-child(2) > DIV:nth-child(6) {width: 0px !important;}
  .main > DIV:nth-child(2) > DIV:nth-child(7) {width: 140px; height: 0px !important;}
  .main > DIV:nth-child(2) > DIV:nth-child(7) > div:nth-child(6){width:0px !important;}
  .main > DIV:nth-child(2) > DIV:nth-child(8) {padding:17px 12px !important;}
  .logo { position: relative !important;width: 250px; height: 110px; background: transparent url('http://filelist.ro/styles/images/logo_silver.png') no-repeat;}
}
