.head_qk_wrap .w_qk {right: 450px;}

.talkBox {height: 280px; background-image: url(/*[[tbg]]*/); background-repeat: no-repeat;box-shadow: 0px 0px 2px #888888;}

.talkBox .sendCnt {background: rgba(248, 248, 248, 0.45);}
.talkBox .cntBox {background:rgba(255, 255, 255, 0.4);height: 150px;}
