.mainWrapperBg {border-radius: 15px;}
.tl_imgNum_6, .tl_imgNum_9 {width: 860px;}
.headShadow {background-image: linear-gradient(to bottom,rgb(44, 44, 44),rgba(25, 25, 25, 0.65));}
.sc_profile_w {position: absolute;top: 35px;}
.sendCnt {width: 620px;}
