.m_profile_h .tx_edit_lk {width: 100px;}
.m_profile_h .avatar {width: 100px;}
.m_profile_h .list {margin-top: 20px;}
.tit  {font-size: 44px; font-weight: 300;margin-top: 30px; }
.text_user {margin-bottom:20px;}
.proposal  ,#homeBannerNew ,#sideBanner {display:none;}

