css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("videosift.com") {

body {background-color: #fcf3f9 !important;}

a {color: #fc9cd6 !important;}

a:hover {color: #da83b7 !important;}

.lolite,.hilite, .contents {background:#ffe8f6 none repeat scroll 0 0 !important;
border:1px solid #ffb9e4 !important;
}

.active {background: #ffb9e4 !important; border: 1px solid #ffb9e4 !important; border-bottom-width:0px !important;}
.inactive {border: 1px solid #ffb9e4 !important; border-bottom-width:0px !important;background-image:url(http://i36.tinypic.com/2ltmux5.png) !important;}
.active a {color: #fff !important;}
.inactive:hover {background: #ffb9e4 !important;}
.inactive:hover a {color: #fff !important;}
.r4, .r3, .r2, .r1 {background:#FFE9F6 !important; border:0px !important} 

.niftycorners {background:#fff !important;}

.pager {}

#power-points {border: 0px !important; top: 0px !important; display: block !important;}

#synergy {display: block !important; background-color: #ffe8f6 !important; border-bottom: 1px solid #ffb9e4;}

.main-body .paged-contents {
background:#FEFEFE none repeat scroll 0 0 !important;
border-color:-moz-use-text-color #ffb9e4 !important;
border-style:none solid !important;
border-width:0 1px !important;
padding-bottom:15px !important;
padding-top:15px !important;
}

.main-body .paged-contents hr {border-color: #fff !important; color: #fff !important; height: 0px !important}

.postcontainer {background-color:#FFE9F6 !important;
color: #111 !important;
border:1px solid #ffb9e4 !important;
overflow:hidden !important;
padding-top:15px !important;
padding-bottom:15px !important;
padding-left: 10px !important;
background-image:url(http://i38.tinypic.com/mubfir.jpg) !important;
background-repeat: repeat-x !important;}

.votebox {color:#fc9cd6 !important;}

.lolite,.hilite {background-image:url(http://i36.tinypic.com/2ltmux5.png) !important; background-repeat:repeat-x !important;
}
.contents {background: transparent !important;border: none !important;}

.title {color: #ffb9e4 !important;}

#chat-log, #chat-links, #chat-users, #chat-msg, #chat-send, imput.button, textarea, #vidtitle, #tags {background-color:#FFE9F6 !important;border-color:#ffb9e4 !important;}

#chat-msg {color: #111}

#lounge {background-color:#FFE9F6 !important; border:1px solid #ffb9e4 !important;color:#000 !important; font-size: 12px !important;}

select {background-color:#FFE9F6 !important;border:1px solid #ffb9e4 !important;color:#111 !important;}

#invoDiv {background-color:#FFE9F6 !important; border:1px solid #ffb9e4 !important; background-image:url(http://i36.tinypic.com/2ltmux5.png) !important;}
}
