css:
@-moz-document domain("hyunsdojo.com") {
body {
    background: url('http://i.imgur.com/noKx4VZ.jpg') 0 0;
}
.front_submit, .ptl_submit,.post_error,.ptl_up_btn{
    background: linear-gradient(to bottom, rgb(45, 98, 176) 0%, rgb(0, 0, 0) 100%);
}
#profile_top_footer {
    background: linear-gradient(to bottom, #FFF 0%, #FFF 65px);
}
.forum_row_text_hi, a.mention_url{
    color: #444444;
}
.forum_row_right_topic a, .topictitle, a.topictitle:visited, [class="postlink"], .previewPosted a, #chat_tabs .chat_tab .chat_new_box .chat_box_messages a{
    color: #5B8DD7!important;
}
.forum_cat, .view_forum_top, .ptl_title, #ptl_menu .front_menu_a.label, #front_menu .front_menu_a.label, #chat_tabs .chat_tab .chat_new_box .chat_box_title, .button, .right_cat,.usr_cat,input[type=submit], button, #chat_tabs .chat_tab, .more_info h3,.ptl_cat,dd input, .submit-buttons input,.box_header,.box_footer {
    background: linear-gradient(to bottom, #000000 0%, #2D62B0 100%);
}
.u_com_blue, .dropdown{
    background: linear-gradient(to bottom,#73ABD0 0%,#47629E 100%)!important;
}
.u_com_blue:hover, .logo:hover,.dropdownlist li.hover{
    background: linear-gradient(to bottom,rgb(131, 136, 175) 0%,rgb(112, 176, 203) 100%);
}
.logo, #favorites div, #notifications, #messages div, .post_info{
  -webkit-filter: grayscale(0%) brightness(60%) sepia(100%) hue-rotate(150deg) saturate(500%) !important;
}
.activity_right a{
    color: #012354
}
#top-search:focus{
	border: 1px solid rgba(20, 74, 201, 0.7);
}
.mentions-input-box{
    background: -webkit-linear-gradient(top,#1A1A33 0%,#1F1C42 100%);
}
.paginate,.paginate_btn{
    background: linear-gradient(to bottom, rgb(45, 98, 176) 0%, rgb(0, 0, 0) 100%)!important;
}
.forum_row_icon{
    background-image: url(http://i.imgur.com/qbyLsUl.gif);
    background-repeat: no-repeat;
}
.forum_row_icon img{
    width:0px
}
.quotetitle, .attachtitle, .codetitle{
    border-color: #BFBB9C;
    background-color: #BFBB9C;
}
/*following written by m_x*/
.photo_view_image {
     max-width: 100% !important;
  	 width: auto !important;
    max-height: 100vh !important;
}
}
