css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("videosift.com") {

body {background-color: #f4fff4 !important;}

a {color: #4ccf4c !important;}

a:hover {color: #3c9e3c !important;}

.lolite,.hilite, .contents {background:#d4ffd4 none repeat scroll 0 0 !important;
border:1px solid #4ccf4c !important;
}

.active {background: #4ccf4c !important; border: 1px solid #4ccf4c !important; border-bottom-width:0px !important;}
.inactive {border: 1px solid #4ccf4c !important; border-bottom-width:0px !important;background-image:url(http://i34.tinypic.com/jv1zk4.png) !important;}
.active a {color: #fff !important;}
.inactive:hover {background: #4ccf4c !important;}
.inactive:hover a {color: #fff !important;}
.r4, .r3, .r2, .r1 {background:#e7ffe7 !important; border:0px !important} 

.niftycorners {background:#fff !important;}

#power-points {border: 0px !important; top: 0px !important; display: block !important;}

#synergy {display: block !important; background-color: #d4ffd4 !important; border-bottom: 1px solid #4ccf4c !important;}

.main-body .paged-contents {
background:#FEFEFE none repeat scroll 0 0 !important;
border-color:-moz-use-text-color #4ccf4c !important;
border-style:none solid !important;
border-width:0 1px !important;
padding-bottom:15px !important;
padding-top:15px !important;
}

.main-body .paged-contents hr {border-color: #fff !important; color: #fff !important; height: 1px !important}

.postcontainer {background-color:#D4FFD4 !important;
color: #111 !important;
border:1px solid #4ccf4c !important;
overflow:hidden !important;
padding-top:15px !important;
padding-bottom:15px !important;
padding-left: 10px !important;
background-image:url(http://i36.tinypic.com/wk2t7k.png) !important;
background-repeat: repeat-x !important;}

.votebox {color:#4ccf4c !important;}

.lolite,.hilite {background-image:url(http://i34.tinypic.com/jv1zk4.png) !important; background-repeat:repeat-x !important;
}
.contents {background: transparent !important;border: none !important;}

.title {color: #4ccf4c !important;}

#chat-log, #chat-links, #chat-users, #chat-msg, #chat-send, imput.button, textarea, #vidtitle, #tags {background-color:#d4ffd4 !important;border-color:#4ccf4c !important;}

#chat-msg {color: #111}

#invoDiv {background-color:#d4ffd4 !important; border:1px solid #4ccf4c !important; background-image:url(http://i34.tinypic.com/jv1zk4.png) !important;}

#lounge {background-color:#d4ffd4 !important; border:1px solid #4ccf4c !important;color:#000 !important; font-size: 12px !important;}

select {background-color:#d4ffd4 !important;border:1px solid #4ccf4c !important;color:#000 !important;}
}
