}
/*Upper Line (Welcome To Scriptfodder Line)*/
.page-header {
    padding-bottom: 9px;
    margin: 40px 0 20px;
    margin-top: 40px;
    margin-right: 0px;
    margin-bottom: 20px;
    margin-left: 0px;
    border-bottom: 1px solid #FF0000;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(255, 0, 0);
}
/*Hey there Box*/
.jumbotron {
    padding: 30px 15px;
    padding-top: 30px;
    padding-right: 15px;
    padding-bottom: 30px;
    padding-left: 15px;
    margin-bottom: 30px;
    color: inherit;
    background-color: #1F1F1F;
    border-style: outset;
}
.navbar .nav > li > a {
    color: #EFEFEF;
    font-size: 13px;
    line-height: 22px;
    font-weight: 500;
}
/*Basic Text Color*/
a {
    color: #EFEFEF;
    text-decoration: none;
}
/*Hey There Alert Balk + Page Background*/
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #FFFFFF;
    background-color: #257526;
}
/*Review Stars*/
.script-listing .caption {
    padding-left: 4px;
    padding-top: 6px;
    color: #EFEFEF;
}
.price {
    background: #EFEFEF;
    padding: 2px 7px;
    padding-top: 2px;
    padding-right: 7px;
    padding-bottom: 2px;
    padding-left: 7px;
    color: #C51C1C;
    font-size: 12px;
    text-align: center;
}

.script-listing {
    position: relative;
    display: block;
    padding: 6px;
    padding-top: 6px;
    padding-right: 6px;
    padding-bottom: 6px;
    padding-left: 6px;
    margin-bottom: 20px;
    line-height: 1.42857143;
    background-color: #C51C1C;
    border: 1px solid #ddd;
    border-top-color: rgb(221, 221, 221);
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: rgb(221, 221, 221);
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: rgb(221, 221, 221);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: rgb(221, 221, 221);
    border-left-style: solid;
    border-left-width: 1px;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;  
}
/*Scripts: Categorie Boxes*/
.btn-primary {
    color: #EFEFEF;
    background-color: #C51C1C;
    border-color: #EFEFEF;
    border-top-color: rgb(239, 239, 239);
    border-right-color: rgb(239, 239, 239);
    border-bottom-color: rgb(239, 239, 239);
    border-left-color: rgb(239, 239, 239);
}  
/*Onderbalk Color "Active users online...*/
.footer-container {
    margin-top: 32px;
    padding: 39px 0 15px;
    padding-top: 39px;
    padding-right: 0px;
    padding-bottom: 15px;
    padding-left: 0px;
    color: #ECF0F1;
    background: #257526;
    background-image: initial;
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: rgb(37, 117, 38);
}
/*"These are random warnings: Warning Banner*/
.alert-warning {
    background-color: #257526;
    border-color: #C51C1C;
    border-top-color: rgb(197, 28, 28);
    border-right-color: rgb(197, 28, 28);
    border-bottom-color: rgb(197, 28, 28);
    border-left-color: rgb(197, 28, 28);
    color: #EFEFEF;
} 
/*Scripts: Sort Order Box*/
.well {
    min-height: 20px;
    padding: 19px;
    padding-top: 19px;
    padding-right: 19px;
    padding-bottom: 19px;
    padding-left: 19px;
    margin-bottom: 20px;
    background-color: #257526;
    border: 1px solid #EFEFEF;
    border-top-color: rgb(239, 239, 239);
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: rgb(239, 239, 239);
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: rgb(239, 239, 239);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: rgb(239, 239, 239);
    border-left-style: solid;
    border-left-width: 1px;
    border-radius: 4px;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);
}  
/*Top Banner (Your not logged in)*/ 
.guest-banner {
    background-color: #3c763d;
    color: #EFEFEF;
    text-align: center;
    padding: 16px;
    padding-top: 16px;
    padding-right: 16px;
    padding-bottom: 16px;
    padding-left: 16px;
    line-height: normal;
    font-size: 16px;
    margin-bottom: -16px;
}

