css:
@-moz-document domain("teamwork.com") {
#header {
  position:fixed;
  z-index:100;
  top:0;
}
#mainTable {
    background-color: #333333;
    color: #fff;
}
#sidebarContent {
	margin-top: 210px;
}

#mainContent
{
    background-color: #333333;
}


/* Top menu selected tab */
.Tabs > ul > li.sel > a
{
     color: #fff;
}

/* Top menu selected tab underline */
#MainTabs > li.sel
{
	border-bottom: 3px solid #ffff00 !important;
}

/* top menu unselected tabs */
.Tabs > ul > li > a
{
    color: #777;
}

/* Top menu unselected tabs underline */
#MainTabs > li
{
	border-bottom: 5px solid #000000 !important;
}

.task.topTask
{
    background-color: #333333;
    color: #fff;
}

