}

/* Task list project title block*/
h3.tlh3
{
    background-color: #444444 !important;
}

/* Task list project title text */
h3.tlh3 > a.ql
{
    color: #dddddd  !important;
    font-weight: bolder !important;
}


/* Background for contents section on the right */
.sectionContent
{
    background-color: #333333;
}

/* The text for each task name */
span[id^="taskName"], div[id*="taskName"]
{
    color: #dddd33;
}




/* The title "Tasks" */
#pageTitle
{
    color: #ffffff;
}

#sidebar
{
     background-color: #333333;
    border-color: #444444;
}

.currentTaskListsItem.ui-droppable
{
    background-color: #333333;
    border-color: #444444;
    border-top: 2px solid #222222 !important;
}

