css:
@-moz-document domain("forum.dashnet.org") {
  #Content{background: #2B2B2B}
  #Content, .DismissMessage, .FlyoutMenu, div.Popup .Body, .Content, .Box{background: #2B2B2B!important;color:#A9B7C6!important;}
  .PageTitle, .H, #Panel, .PanelInfo .Active{background: #383838!important}
  .Item, .Box h4{color:#A9B7C6!important;}
  a.Selected{background-color: transparent!important;}
  .HasNew, .Selected{background-color: #214283!important;color: #A9B7C6}
  a, a.TextColor, .FlyoutMenu a, .Button{color:#A5C25C;}
  .Box a{color:#A5C25C!important;}
  .FlyoutMenu .PhotoWrap {left:3px!important;top:0px!important;}
  .CodeInline{background: #383838; color: #619647; box-shadow: 0px 0px 4px #444}
  .WarningMessage{background: #7B2B2B!important;}
  
  blockquote.Quote, blockQuote.UserQuote{border-left: 4px solid #666;color: #a5a4a4;font-style: italic;margin: 0px 0 0px 10px;padding-left: 10px;}
  .MessageList .Message blockquote div{background: none;box-shadow: none;font-style: initial;opacity: 1;}
  .MessageList .Message blockquote::before{content: none}
  .MessageList .Message blockquote::after{content: none}
  .MessageList .Message blockquote{display: flex;flex-direction: column}
  .QuoteText{margin-top:0em;padding: 0px}
  .QuoteText p{margin:0px}
  .QuoteAuthor{margin: 0px;padding: 0px;position: initial;font-variant: small-caps;text-transform: lowercase}
  
  .Excerpt img {max-width: 660px}
  .Activity-Status{box-shadow: 0 0 5px #b784a7 inset}
  .Activity-WallPost{box-shadow: 0 0 5px #0D4DBA inset}
  .Activity-PictureChange{box-shadow: 0 0 5px #652D89 inset}
  .Activity-Badge{box-shadow: 0 0 5px #EB428A inset}
  .Activity-Registration{box-shadow: 0 0 5px #FF6600 inset}
  
  .Item.Read a{font-weight: normal!important;font-style: italic; color: #ddd;}
  .Item.Read .Meta{display: none}
  
  .Message{clear:none}
  .CheckBoxLabel, .RadioLabel, h3, label{color:#A9B7C6!important;}
}
