css:
@-moz-document domain("nicegame.tv") {
body { background-color:#fff; color:#999; }
a { color:inherit; }

img[src*="level"] { -webkit-filter:saturate(0); filter:saturate(0); opacity:0.5; }
img[src*="level"]:hover { opacity:1; }
img[src*="ico_"] { -webkit-filter:saturate(0); filter:saturate(0); opacity:0.2;}
img[src*="/thumbnails/"],
img[src*="profileImage"] { opacity:0.1; }
img[src*="/thumbnails/"]:hover,
img[src*="profileImage"]:hover { opacity:1; }

/* Header */
#top { border-bottom:1px solid #ebebeb; background-color:#fff; }
#top li { background:none; }
#top li a { color:#999; }
#top li a:hover { color:#999; text-decoration:underline; }

#header { border-top:none; border-bottom:1px solid #ebebeb; background-color:#fff; }
#header a[href="/"] { position:absolute; top:5px; left:50%; text-decoration:none; color:#999; margin-left:-490px; }
#header a[href="/"]:before { content:'NiceGameTV'; }
#header a[href="/"] img { display:none; }

/* GNB */
#gnb { height:auto; border-bottom:1px solid #ebebeb;background:none; }
#gnb li a { height:auto !important; padding:10px !important; background:none !important; color:#999; font-size:12px; }
#gnb li.onair a:before { content:'온에어'; }
#gnb li a[href*="guide"]:before { content:'편성표'; }
#gnb li a[href*="vod"]:before { content:'다시보기'; }
#gnb li a[href*="shop"]:before { content:'NP샵'; }
#gnb li a[href*="setpc"]:before { content:'NPC'; }
#gnb li a[href*="freeboard"]:before { content:'커뮤니티'; }
#gnb li a[href*="events"]:before { content:'이벤트'; }
#gnb li a[href*="challengers"]:before { content:'챌린저스 코리아'; }
/* #gnb li a[href*="league.nicegame.tv"]:before { content:'클랜배틀'; } */
#gnb li a[href*="clubseries"]:before { content:'클럽시리즈'; }
#gnb li a[href*="tekkenstrike2"]:before { content:'철권 리그'; }
#gnb li a[href*="overwatch"]:before { content:'오버워치'; }
#gnb li a[href*="lcs"]:before { content:'LCS'; }
#gnb li img{ display:none; }
#gnb .leagueLink { height:auto; margin:0; background:none; }

#footer { border-color:#ebebeb }
#footer * { border-color:#ebebeb !important; background:none !important; color:#999 !important;}
#footer a:hover { text-decoration:underline; }

/* Sidebar */
.side * { border-color:#ebebeb !important; background:none !important; color:#999 !important; }
.side a:hover { text-decoration:underline; }
.sideMenu dt { width:auto; border:1px solid #ebebeb; }
.sideMenu>dl+div:not([class]){ clear:left;}

/* 로그인 영역 */
.side .login a[href*="logout"]:before { content:'로그아웃'; }
.side .login a[href*="logout"] img { display:none; }

/* 편성표, 라이브 */
.side .subBanner { height:auto; }
.side .subBanner a[href*="guide"]:before { content:'편성표'; }
.side .subBanner a.live:before { content:'라이브'; }
.side .subBanner a[href*="schedule"]:before { content:'주간편성표'; font-size:2em;}
.side .subBanner a img { display:none; }

/* 컨텐츠 */
.viewContainer>a:first-child>img,
.viewContainer>.caption { -webkit-filter: saturate(0) invert(1); filter: saturate(0) invert(1); opacity:0.2; }
#BoardTop *,
#userBanner,
#userBanner *,
#BoardList * { border-color:#ebebeb !important; background:none !important; color:#999 !important; }
#BoardTop h4 img { display:none; }
.boardView .recommend .score+a:before { content:'추천'; }
.boardView .recommend .score+a img { display:none; }
#BoardView .func .left .list:before { content:'목록'; }
#BoardView .func .left .list img { display:none; }
.viewContainer a[href*="/write/"]:before { content:'수정'; }
.viewContainer a[href*="/write/"] img { display:none; }
.viewContainer a[href$="/write"]:before { content:'글쓰기'; }
.viewContainer a[href$="/write"] img { display:none; }
.del:before { content:'삭제'; }
.del img { display:none; }
.comment .recommend_cmt:before { content:'추천'; }
.comment .recommend_cmt img { display:none; }
.btnSubmit,
.btnSearch { -webkit-filter:saturate(0); filter:saturate(0); opacity:0.2;}

/* 글작성 */
#BoardWrite { background:none; }
.writeForm th,
.writeForm td,
.writeForm tbody td input,
.writeForm tbody td textarea { border-color:#ebebeb; background:none; color:#999 }
.writeForm tbody td input,
.writeForm tbody td textarea { border-width:1px; border-style:solid;}
.cke_top { border-color:#ebebeb; background:none; }
.cke_editable { background:none; }
.cke_toolgroup { border-color:#ebebeb; background:none; }
a.cke_button { -webkit-filter:invert(1); filter:invert(1); opacity:0.2; }
.cke_reset_all, .cke_reset_all * { color:#999; }
.cke_toolbar_separator {background:#ebebeb;}

/* 글 상세 */
#BoardView * { border-color:#ebebeb !important; background:none !important; color:#999 !important; }

/* 광고 */
.top_banner,
.sideMenu>div:not([class]),
#mainPromotion,
#NeoInteractiveScreenViewLayer { opacity:0.1; }
.top_banner:hover,
.sideMenu>div:not([class]):hover,
#mainPromotion:hover,
#NeoInteractiveScreenViewLayer:hover { opacity:1; } 

/* 메인 */
#mainSide .schedule .weekday .active { background:#ebebeb !important; }
  
#mainIndex * { border-color:#ebebeb !important; background:none !important; color:#999 !important;}
#latestVod h5 { font-size:12px; }
#latestVod h5:before { content:'최근 업로드 영상';}
#latestVod h5 img { display:none; }

.carousel-inner { opacity:0.1; }
.carousel-inner:hover { opacity:1; }
#hotVod img { opacity:0.1; }
#hotVod img:hover { opacity:1; }

/* 편성표 */
#guideIndex * { border-color:#ebebeb !important; background:none !important; color:#999 !important;}
#guideIndex .weekList .prev:before { content:'이전'; } 
#guideIndex .weekList .next:before { content:'다음'; }
#guideIndex .weekList .prev img , #guideIndex .weekList .next img { display:none; }
#guideIndex .weekList li a { padding-left:0; padding-right:0; }
#guideIndex .weekList li { width:95px; }
img[src*="/tit_guide.png"] { -webkit-filter:saturate(0) invert(1); filter:saturate(0) invert(1); opacity:0.2; }
}
