/*------------------------------Forum-----------------------------*/
BODY {background-image: url(/*[[back]]*/

);
opacity: /*[[trans]]*/ ;
 background-repeat: no-repeat;
background-position: 0px 0px;
background-attachment: fixed;
background-size: 100%;
}
 
  
table {
border-collapse: separate;
border-spacing: 3px;
border-color: transparent;
}

::selection {
    color: gold;
    background: #7533ff;
}
::-moz-selection { /* Code for Firefox */
    color: gold;
    background: #7533ff;
}
.windowbg, #preview_body
{
	color: #Ffebef;
	background-color: /*[[wcolor1]]*/ !important;
}
.windowbg2
{
	color: #Ffebef;
	background-color: /*[[wcolor2]]*/ !important;
}
.windowbg3
{
	color: #Ffebef;
	background-color: #26282F;
}
.stickybg
{
	background: #362030;
}
.stickybg2
{
	background: #362030;
}

.lockedbg
{
	background: #323232;
	font-style: italic;
}
.lockedbg2
{
	background: #383838;
	font-style: italic;
}
  .alert
{
	color: Yellow;
}
.description, .description_board, .plainbox
{
	padding: 0.5em 1em;
	font-size: 0.9em;
	line-height: 1.4em;
	border-top: 1px solid #545454; 
	border-left: 1px solid #545454; 
	border-bottom: 1px solid #545454; 
	border-right: 1px solid #545454;
	background: #171717;
	margin: 0.2em 1px 1em 1px;
}
h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a
{
	color: #3EA87E;
}
h3.catbg2 a, h3.catbg2
{
	color: #3EA87E;
}
h3.catbg a:hover, h4.catbg a:hover, .table_list tbody.header td a:hover
{
	color: #CFBA93;
	text-decoration: none;
}
h3.catbg2 a:hover
{
	color: #fff;
	text-decoration: none;
}
h3.titlebg a, h3.titlebg, h4.titlebg, h4.titlebg a
{
	color: #69580D;
}
h3.titlebg a:hover, h4.titlebg a:hover
{
	color: #FFFFFF;
	text-decoration: none;
}
h3.catbg {
color: #FFFFFF;

}
.roundframe
{
	padding: 0 10px;
	background: #1F190D;
	border-left: 3px solid #292314;
	border-right: 3px solid #292314;
}
.dropmenu a
{
	display: block;
	color: #FFFFFF;
	text-decoration: none;
}
.dropmenu li li
{
	width: 19em;
	margin: 0;
	border-bottom: solid 1px #3E9EA8;
	border-left: solid 1px #3E9EA8;
	border-right: solid 1px #3E9EA8;
	border-top: solid 1px #3E9EA8;
}
.dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel
{
	color: #933EA8;
	cursor: pointer;
	text-decoration: none;
}
.dropmenu li li a:hover, .dropmenu li li:hover>a
{
	color: #933EA8;
	text-decoration: none;
}
.buttonlist ul li a
{
	display: block;
	font-size: 0.8em;
	color: #FFFFFF;
	padding: 0;
	margin-left: 12px;
	text-transform: uppercase;
	cursor: pointer;
}
.buttonlist ul li a:hover
{
	color: #171717;
	text-decoration: none;
}
h1.forumtitle
{
	line-height: 45px;
	font-size: 1.8em;
	font-family: Geneva, verdana, sans-serif;
	margin: 0;
	padding: 0;
	float: left;
}
.navigate_section ul
{
	display: block;
	margin: 0;
	font-size: 0.9em;
	padding: 1em 0 0.6em 0;
	border-top: 
    dashed #3E9EA8 1px;
	overflow: hidden;
	list-style: none;
}
.table_list tbody.content td.info a.subject
{
	font-weight: bold;
	font-size: 110%;
	color: #D1B09B;
}
.table_list tbody.content td.children
{
	color: #3E77A8;
	font-size: 85%;
}
input, button, select, textarea
{
	font: 95%/115% verdana, Helvetica, sans-serif;
	color: #FFFFFF;
	background: #2e2d2d;
	border: 1px solid #656565;
	padding: 2px;
}
.button_submit, .button_reset
{
	border: 1px solid #933EA8;
	cursor: pointer;
	font-weight: normal;
}
input:hover, textarea:hover, button:hover, select:hover
{
	border: solid 1px #A8983E;
}
.button_submit:hover, .button_reset:hover
{
	border: 1px solid #A8983E;
}
input:focus, textarea:focus, button:focus, select:focus
{
	border: solid 1px #A8983E;
}
tr.titlebg td a, tr.titlebg2 td a
{
	color: #292314;
}
tr.catbg td a, tr.catbg2 td a, .catbg a
{
	color: #292314;
}
}
