css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.h528.com") {
  html,body,#page {background: #fff;text-rendering: optimizelegibility;}
    #header,#main > table {display: none;}
    #main,#content,#sidebar {width:100%;}
    #footer {display:none;}
    #sidebar {margin:0;padding:0;background: #3f51b5;width:100%;position:fixed;top:0;box-shadow: 0 2px 5px rgba(0, 0, 0, 0.26);}
    #sidebar > ul > iframe {display: none;}
    #sidebar > ul > li {clear:both;padding:0rem;margin-bottom:0;}
    #sidebar > ul > li > h2 {padding:1rem 1rem 2rem;text-align: center;color:rgba(255,255,255,0.87);font-size:3.8rem;}
    #sidebar > ul > li > h2:after {content: "+";}
    #sidebar > ul > li:last-child {display: none;}
    #sidebar > ul > li > ul {padding-bottom:1rem;}
    #sidebar > ul > li > ul > li {padding:1rem 0rem;margin:0rem;width:33.333%;float:left;color:rgba(255,255,255,0.87); font-size:2.2rem;text-align: center;}
    #sidebar > ul > li > ul > li a {text-decoration:none;color:rgba(255,255,255,0.87);font-size:2.4rem;}
    #sidebar > ul > li > ul > li:before {content: unset;}
    #sidebar > ul > li.categories > ul {display: none;}
    #sidebar > ul > li.categories:hover > ul {display: block;}

    #content,.post {padding:0.5rem 0;}
    #content {padding-top:4rem;padding-bottom: 5rem;}
    #content > h2 {font-size:1rem;color:#3f51b5;}
    .post {border:none;}
    .post a {color:#8e869d;} 
  .post > h2 {text-align: center;font-size:3rem;}
  .post h3 {font-size:2.8rem;font-weight:normal;}
  
  .post .entry p {padding:0 0.5rem;}
  .post .entry p:first-child {padding-top:2rem;}
  .post .entry p:last-child {padding:1rem;}
  .post .entry p {font-size:2.8rem;font-weight:normal;line-height:3rem;}
  .post table td:first-child {width:12rem;}
  .post td small {color:#fff;}
  .post td small > a {color:#3f51b5;font-size:2rem;padding:0.5rem;border:1px solid #3f51b5; border-radius:0.3rem;}
  .postmetadata {display: none;}
  
  .navigation {position:fixed;bottom:0;width:100%;padding:0;margin:0;}
  .navigation > div {width:50%;margin:0;text-align:center;color:rgba(255,255,255,0.87);}
  .navigation > div.alignleft {background:#03a9f4;}
  .navigation > div.alignright {background: #9c27b0;}
  .navigation > div > a {display:inline-block;margin:1rem;color:rgba(255,255,255,0.87);font-size:2rem;}
}
