css:
@-moz-document url-prefix("http://4pda.ru/forum/"), url-prefix("https://4pda.ru/forum/") {
/*Блокируем верхний рекламный баннер*/
img[height="90"],
iframe {
display: none !important;
}

div#ipbwrapper.noindex.div,
div#gc_1,
div.globalmesswrap,
div.borderwrap,
div#navstrip {
margin: 0; margin-bottom:0; margin-top:0; padding:0; border:0 !important;
}

div#logostrip, 
/* br, */
.social-icons {
 display: none !important;
}

.tr.table.ipbtable {
border:0 !important;
}
}

@-moz-document regexp("(.*)4pda.ru\\/forum\\/index\\.php\\?act=mmod&t=(.*)") {
/* мультимодерация */

  * {vertical-align:middle !important}
  
input[type="checkbox"] {
height:40px; width:40px !important;
}

body {
font-size:16; width: 60%; margin: 0 20%; background-color: rgb(240, 247, 255)
 !important;
}
}
