css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(uh.ru){

  body {color: #FFFFFF !important; border-radius: 0.5em  !important;}

  input, textarea, select {color: #FFFFFF;background-color: #333333;border-color: #4F5765;-moz-appearance: none !important;}

  font[color="#009900"] {color: #FF75FF !important;}

  font[color="red"] {color: #1AFFFF !important;}

  body, form, table {color: #FFFFFF !important;}

  body {background-color: #4C4C4C !important;}

  a {color: #EBEB00 !important;}

  a:hover {color: #1AFFD1 !important;}

  #main_col {border-top-color: #474747 !important;border-right-color: #474747 !important;border-bottom-color: #474747 !important;border-left-color: #474747 !important;background-color: #333333 !important;}

  #right_col {border-top-color: #474747 !important;border-right-color: #474747 !important;border-bottom-color: #474747 !important;border-left-color: #474747 !important;background-color: #333333 !important;}

  #menu {background-image: -moz-linear-gradient(top, rgba(201,133,79,1), rgba(198,140,92,1), rgba(198,146,104,1), rgba(200,152,113,1), rgba(204,157,120,1), rgba(204,157,120,1) 44px, rgba(0,0,0,0) 44px) !important;background-repeat: repeat-x !important;}

  .menu_list {color: #1A1A1A !important;}

  .menu_list:hover {background-color: #5A4035 !important;color: #474747 !important;}

  .menu_list.a {color: #FFFFFF !important;}

  .bookmarks .a {color: #1A6BFF !important;}

  .image_list {border-top-color: #8C8C8C !important;border-right-color: #8C8C8C !important;border-bottom-color: #8C8C8C !important;border-left-color: #8C8C8C !important;}

  .templete .a {color: #1AFFFF !important;}

  .message_points_link {color: #8C8C8C !important;}

  .article_comment .date {color: #676767 !important;}

  .table2 .tr1 {background-color: #A300A3 !important;}

  .table3 tr td {border-top-color: #8C8C8C !important;}

  .pagination {border-top-color: #28252A !important;border-right-color: #28252A !important;border-bottom-color: #28252A !important;border-left-color: #28252A !important;color: #FFFFFF !important;background-color: #373737 !important;}

  .pagination .a {color: #8C8C8C !important;}

  .paginator2 {border-top-color: #28252A !important;border-right-color: #28252A !important;border-bottom-color: #28252A !important;border-left-color: #28252A !important;color: #FFFFFF !important;background-color: #373737 !important;}

  .paginator2 a {border-top-color: #28252A !important;border-right-color: #28252A !important;border-bottom-color: #28252A !important;border-left-color: #28252A !important;}

  .paginator2 span {border-top-color: #28252A !important;border-right-color: #28252A !important;border-bottom-color: #28252A !important;border-left-color: #28252A !important;color: #8C8C8C !important;}

  #paginator a {background-color: #FFCC99 !important;border-top-color: #FFC58C !important;border-right-color: #FFC58C !important;border-bottom-color: #FFC58C !important;border-left-color: #FFC58C !important;color: #1A1A1A !important;}

  #paginator a:hover {background-color: #674E38 !important;color: #FFC58C !important;}

  #paginator .a {background-color: #674E38 !important;color: #FFC58C !important;}

  .form .field label span {color: #8C8C8C !important;}

  .form .field label.error {color: #1AFFFF !important;}

  .form .field.input input {border-top-color: #8C8C8C !important;border-right-color: #8C8C8C !important;border-bottom-color: #8C8C8C !important;border-left-color: #8C8C8C !important;}

  .form .field.input input.error {border-top-color: #1AFFFF !important;border-right-color: #1AFFFF !important;border-bottom-color: #1AFFFF !important;border-left-color: #1AFFFF !important;}

  .form .field.input input.valid {border-top-color: #FF8CFF !important;border-right-color: #FF8CFF !important;border-bottom-color: #FF8CFF !important;border-left-color: #FF8CFF !important;}

  .form .field.captcha input {border-top-color: #8C8C8C !important;border-right-color: #8C8C8C !important;border-bottom-color: #8C8C8C !important;border-left-color: #8C8C8C !important;}

  .form .field.captcha input.error {border-top-color: #1AFFFF !important;border-right-color: #1AFFFF !important;border-bottom-color: #1AFFFF !important;border-left-color: #1AFFFF !important;}

  .form .field.captcha input.valid {border-top-color: #FF8CFF !important;border-right-color: #FF8CFF !important;border-bottom-color: #FF8CFF !important;border-left-color: #FF8CFF !important;}

  #form_edit_article label.error {color: #1AFFFF !important;}

  .message {color: #1AFFFF !important;}

  .sbi_box {background-color: #5E4C1C !important;}

}
