@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#content tab {
    background-position: left top !important;
    background-repeat: no-repeat !important;
}

/* Black numbers */
#content tab:first-child {
    background-image: url("data:image/gif;base64,R0lGODlhBgAHAIABAAAAAP///yH5BAEKAAEALAAAAAAGAAcAAAILhBEZpovK4EvxnAIAOw==") !important;
}
#content tab:first-child + tab {
    background-image: url("data:image/gif;base64,R0lGODlhBgAHAIABAAAAAP///yH5BAEKAAEALAAAAAAGAAcAAAILhBGZpsGLoHrwhAIAOw==") !important;
}
#content tab:first-child + tab + tab {
    background-image: url("data:image/gif;base64,R0lGODlhBgAHAIABAAAAAP///yH5BAEKAAEALAAAAAAGAAcAAAIKhBGZpsH+VITwFQA7") !important;
}
