/* ------------------------------------------
 * Couchtuner, Elegant ~
 *
 *   This style's purpose is to not restrict
 *   Couchtuner's functionality but still
 *   force it to appear less cluttered.
 *
 *   This style flattens the overall
 *   appearance of the site and hides
 *   irrelevant elements. Overall, I believe
 *   it provides a very modern and minimal
 *   design.
 *
 * Features:
 *
 *   The sidebar is dimmed.
 *   br elements are hidden.
 *   Troubleshooting tips are hidden.
 *   Player tabs are made less obnoxious.
 *   Navigation bar is made less intrusive.
 *   Banners and promotional links are
 *   hidden.
 *     # Ads are NOT removed.
 *     # If you want to do that, use uBlock.
 *   
 *
 *
 * --------------------------------------- */

@namespace url(http://www.w3.org/1999/xhtml);

  /* global */
@-moz-document domain("couchtuner.city"),domain("couchtuner.ag"),domain("couchtuner.fr"),domain("iwatchonline.us"),domain("couch-tuner.ag"),domain("couch-tuner.city") {
  * {
    background-color: rgba(40, 40, 40, 1) !important;
    color: rgba(220, 220, 220, 1) !important;
    font-family: noto sans !important;
    font-size: 10pt !important;
    border: none !important;
  }
  
  /* links */
  a {
    text-decoration: none !important;
    color: rgba(172, 199, 210, 1) !important;
    transition-duration: 0.5s !important;
  }
  
  a:hover {
    color: rgba(213, 231, 239, 1) !important;
    transition-duration: 0.5s !important;
  }
  
  a:visited {color: rgba(115, 141, 246, 1) !important;}
  
  /* hiding of empty, irrelevant, and ugly elements */
  br,#header,#container > div:nth-child(2),#container > center:nth-child(4),.post > div:nth-child(5),.entry > strong:nth-child(1),.entry > iframe:nth-child(7),.entry > iframe:nth-child(8),.entry > iframe:nth-child(9),.entry > iframe:nth-child(10),.entry > iframe:nth-child(11),.entry > iframe:nth-child(12),.entry > iframe:nth-child(13),.entry > div:nth-child(15) > strong *,.entry > center *,.meta,.comments_part > strong:nth-child(4),.g-plusone,.pagination > center *,#s_top,#footer {display: none !important;}

  /* navigation bar */
  #menu {
    margin: 1% 0 1% -0.5% !important;
  }
  
  #menu a {
    margin-right: 10px;
    color: rgba(43, 42, 42, 1) !important;
    transition-duration: 0.5s !important;
    box-shadow: 0px 0px 6px black !important
  }
  
  #menu a:hover {
    color: whitesmoke !important;
    transition-duration: 0.5s !important;
  }
  
  #menu li {margin: -0.1% !important;}
  
  /* setting the colour of the content */
  iframe,#content *,#menu ul li a,.post *,.post > h2:nth-child(1),.postTabs,.entry * {background-color: rgba(51, 51, 51, 1) !important;}
  
  /* setting content's shadow */
  #content {box-shadow: 0px 0px 6px black !important}
  
  /* setting the color of the content's border */
  .post {
    min-height: 60px !important;
    border: 8px solid !important;
    border-color: rgba(51, 51, 51, 1) !important;
    padding: 0 8px 18px 0 !important;
  }
  
  /* field on the search page */
  #searchform #s2 {
    margin-bottom: 5% !important;
    background: whitesmoke !important;
    color: black !important;
  }
  
  /* colour of "Posted by" text */
  .descr,.descr > abbr:nth-child(1) {
    background-color: rgba(51, 51, 51, 1) !important;
    color: rgba(220, 220, 220, 0.2) !important;
  }
  
  /* player tabs */
  ul.postTabs {
    margin: -3% auto -0.3% auto !important;
    border: none !important;
  }
  
  ul.postTabs li a {
    background-color: rgba(66, 66, 66, 1) !important;
    color: inherit !important;
    border: none !important;
  }
  
  ul.postTabs li.postTabs_curr a {color: rgba(213, 231, 239, 1) !important;}
  
  /* page navigation on "New Releases" */
  .pagination {margin: -4% 0 0 1%;}
  
  /* tv list on page right */
  #sidebar h2 {
    color: rgba(220, 220, 220, 0.6) !important;
    transition-duration: 0.5s !important;
  }
  
  #sidebar h2:hover {
    color: rgba(220, 220, 220, 1) !important;
    transition-duration: 0.5s !important;
  }
  
  #sidebar a {
    color: rgba(172, 199, 210, 0.2) !important;
    transition-duration: 0.5s !important;
  }
  
  #sidebar a:hover {
    color: rgba(213, 231, 239, 1) !important;
    transition-duration: 0.5s !important;
  }
  
  #sidebar a:visited {color: pink !important;}
  
  /* sidebar placement */
  #left,#right {margin: -4.3% 0 0 0 !important;}
}
