css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("https://forum.roblox.com"), url-prefix("http://forum.roblox.com") {
#upgrade-now-button,
#navigation .rbx-text-notes,
.rbx-nav-sponsor,
.ad-annotations,
.other,
.rbx-nav-notification,
#Footer,
.rbx-icon-nav-menu,
#Skyscraper-Adp-Left,
#Skyscraper-Adp-Right,
#Skyscraper-Ad-Left,
#Leaderboard-Abp
{
    display: none !important;
}

#tab-outfits,
#tab-wardrobe,
#FriendsTab,
#BestFriendsTab,
#FriendRequestsTab,
#GroupPlacesContainer,
.tab-content-container
{
    border-left: 1px solid rgb(200, 200, 200) !important;
    border-bottom: 1px solid rgb(200, 200, 200) !important;
    border-right: 1px solid rgb(200, 200, 200) !important;
}

.container-main > .content
{
    min-width: 970px !important;
    left: 260px;
    position: absolute;
}

#horizontal-tabs,
.game-main-content,
.section
{
    border-radius: 3px;
    /*overflow: hidden;*/
    
    ;
}

#GamesPageLeftColumn
{
    width: 1024px !important;
    background-color: inherit !important;
}

.games-list-container
{
    background-color: inherit !important;
}

.rbx-tab-heading
{
    -webkit-transition: ease 0.2s;
}

.home-friends,
.home-right-col .section,
.home-left-col .section
{
    border-radius: 3px;
}

#header
{
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.3) !important;
}

#navigation
{
    background-color: rgb(255, 255, 255) !important;
    display: block !important;
    position: fixed;
    left: 0;
    top: 40px;
    width: 175px;
    z-index: 1031 !important;
    box-shadow: 2px 0px 4px rgba(0, 0, 0, 0.3) !important;
}

#navigation .rbx-scrollbar
{
    width: 175px;
}

/*#navigation > div {
    width: 175px;
}

#navigation div > ul > li {
    width: 180px;
    position: relative;
    left: -15px;
    padding-left: 15px;
    line-height: 24px;
    margin: 0;
    -webkit-transition: ease 0.2s;
}

#navigation div > ul > li:hover {
    background-color: rgb(250, 250, 250);
}*/

::selection
{
    background: rgba(145, 180, 255, 0.4);
}

::-moz-selection
{
    background: rgba(145, 180, 255, 0.4);
}

.container-footer
{
    display: none;
}

.votes,
.percent
{
    border-radius: 3px !important;
}

#ResponsiveWrapper,
.nav-content-inner,
.content,
body,
html,
.Avatar,
.Post,
.StandardPanelWhite,
#Body,
.UserBlurb,
.accord-section-open,
#UserPlaces
{
    background-color: rgb(250, 250, 250) !important;
    background-image: none !important;
}

/*.nav-content-inner,
.content {
	position: absolute !important;
	left: 20%;
}*/
/*#ResponsiveWrapper {
	position: relative !important;
	left: 0px;
}*/

#ctl00_cphRoblox_ThreadView1_ctl00_ThreadList .normalTextSmaller,
.forum-post .normalTextSmaller,
#ctl00_cphRoblox_MyForums1_ctl00_ParticipatedThreads .normalTextSmaller
{
    color: rgb(100, 100, 100) !important;
}

.thread-link-container
{
    color: rgb(80, 80, 80) !important;
}

.forum-post .normalTextSmall
{
    color: rgb(30, 30, 30) !important;
}

.forum-table-row
{
    transition: all 0.2s ease !important;
    -ms-transition: all 0.2s ease !important;
    -moz-transition: all 0.2s ease !important;
    -webkit-transition: all 0.2s ease !important;
}

.forum-table-row:hover
{
    background-color: rgb(230, 230, 230) !important;
    border-radius: 5px !important;
}

#forum-nav .menuTextLink
{
    border-left: 1px solid rgb(200, 200, 200) !important;
    color: #2E8BE8 !important;
    text-decoration: none !important;
}

#forum-nav .menuTextLink:hover
{
    color: #4A9DF0 !important;
    text-decoration: none !important;
}

#forum-nav .menuTextLink.first
{
    border: none !important;
}

.linkMenuSink
{
    color: #2E8BE8 !important;
    text-decoration: none !important;
}

.linkMenuSink:hover
{
    color: #4A9DF0 !important;
    text-decoration: none !important;
}

.forum-table-header,
.forum-table-footer
{
    border: none !important;
    background-color: #5C88D1 !important;
}

.forum-table-header th
{
    background-color: #5C88D1 !important;
}

.see-all-button,
.btn-control,
.btn-small,
.btn-medium
{
    color: white !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
    border-radius: 3px !important;
    background-image: none !important;
    background-color: rgb(0, 162, 255) !important;
    border: none !important;
    transition: all 0.2s ease !important;
    -ms-transition: all 0.2s ease !important;
    -moz-transition: all 0.2s ease !important;
    -webkit-transition: all 0.2s ease !important;
}

.see-all-button:hover,
.btn-control:hover,
.btn-small:hover,
.btn-medium:hover
{
    background-color: rgb(50, 181, 255) !important;
    box-shadow: 0px 2px 2px rgb(230, 230, 230) !important;
}

.btn-primary
{
    color: white !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
    border-radius: 0px !important;
    background-image: none !important;
    background-color: #00BA19 !important;
    border: none !important;
    transition: all 0.2s ease !important;
    -ms-transition: all 0.2s ease !important;
    -moz-transition: all 0.2s ease !important;
    -webkit-transition: all 0.2s ease !important;
}

.btn-primary:hover
{
    background-image: none !important;
    background-color: #00D11C !important;
    box-shadow: 0px 2px 2px rgb(230, 230, 230) !important;
}

#blacklist-group
{
    font-size: 12px;
    height: 21px;
    line-height: 21px;
    color: white !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
    border-radius: 0px !important;
    background-color: rgb(0, 162, 255) !important;
    background-image: none !important;
    border: none !important;
    transition: background-color 0.2s ease !important;
    -ms-transition: background-color 0.2s ease !important;
    -moz-transition: background-color 0.2s ease !important;
    -webkit-transition: background-color 0.2s ease !important;
}

#blacklist-group:hover
{
    background-color: rgb(50, 181, 255) !important;
    box-shadow: 0px 2px 2px rgb(230, 230, 230) !important;
}

.GroupListItemContainer
{
    transition: background-color 0.2s ease !important;
    -ms-transition: background-color 0.2s ease !important;
    -moz-transition: background-color 0.2s ease !important;
    -webkit-transition: background-color 0.2s ease !important;
}

.GroupListItemContainer:hover
{
    background-color: rgb(230, 230, 230) !important;
}

#GroupPlaces
{
    height: 150px !important;
}

.GroupListContainer,
.GroupControlsBox,
#SearchControls,
#description
{
    background-color: white !important;
    border-radius: 3px !important;
    border: none !important;
    box-shadow: 0px 1px 3px rgba(150, 150, 150, 0.75) !important;
}

#SearchControls > .content
{
    margin-left: 100px;
    margin-right: 100px;
    margin-top: 2px;
}

.GroupControlsBox
{
    padding-bottom: 10px;
}

.GroupControlsBox:last-child
{
    display: none !important;
}

#ctl00_cphRoblox_GroupStatusPane_status
{
    background-color: rgb(250, 250, 250) !important;
    border-radius: 3px !important;
    border: none !important;
    box-shadow: 0px 1px 3px rgba(150, 150, 150, 0.75) !important;
    overflow: hidden !important;
    position: relative !important;
    left: -30px !important;
    width: 350px !important;
}

#ctl00_cphRoblox_GroupStatusPane_status .bottom .content
{
    float: none !important;
    position: relative !important;
    top: 5px !important;
    width: 350px !important;
    left: -5px !important;
    background-color: rgb(200, 200, 200) !important;
    padding: 2px !important;
}

#ctl00_cphRoblox_GroupStatusPane_status .bottom .content > a
{
    color: rgb(50, 50, 50) !important;
    font-style: normal !important;
    font-size: 12px !important;
}

#ctl00_cphRoblox_GroupStatusPane_status .bottom .content > a:before
{
    content: "By: ";
}

#rbxenhancer-shout-rank
{
    color: rgb(90, 90, 90) !important;
    font-size: 12px !important;
}

#ctl00_cphRoblox_GroupStatusPane_StatusDate
{
    color: rgb(90, 90, 90) !important;
    font-size: 12px !important;
}

#ctl00_cphRoblox_GroupStatusPane_StatusDate:before
{
    content: "on ";
}

#ctl00_cphRoblox_GroupStatusPane_status .top,
#ctl00_cphRoblox_GroupStatusPane_status .bottom
{
    background: none !important;
}

#ctl00_cphRoblox_GroupStatusPane_status .ReportAbuse
{
    display: none !important;
}

.accord-header
{
    background-image: none !important;
    background-color: rgb(245, 245, 245) !important;
    border-radius: 0px !important;
    transition: all 0.2s ease !important;
    -ms-transition: all 0.2s ease !important;
    -moz-transition: all 0.2s ease !important;
    -webkit-transition: all 0.2s ease !important;
}

.accord-header:hover
{
    background-color: rgb(230, 230, 230) !important;
}

.details span,
.allowed-gear
{
    color: rgb(100, 100, 100) !important;
}

.place-description-content
{
    color: rgb(30, 30, 30) !important;
}

#SocialMediaBarTarget
{
    display: none !important;
}
}
