css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("photosight.ru")
  
{
  .layout, .info-block, .white-board .content, .dropdown, .min-body .layout, .sidebar, .page-wrapper, .header-wrap .columns, .comments .comment-header h3, .comments .comment .comment-content:hover, .comments .comment .comment-content.author, .min-body, .white-board, .dropdown
  {background-color: #1b1b1b}


  .events .info a, .col1 a, .col1 p, .col1 h3 a, .white-board .content h2, .white-board .content a, .filter-menu a, .catalog .col ul li a, a.is-black, span.is-black, .competition h2 a, .competition-item p, .competition-item .congrat, .competition-item span b, .info-block, .community h2, .community p, .dropdown .col a, .dropdown .col span, .dropdown .col .group-title, .catalog-layout.cols h1, .photos-content .photo-item h2 a, .photos-content .photo-item p, .photos-content .photo-item p a, .standard-textblock h1, .standard-h1, .standard-textblock h3, .standard-h3, p.standard-p-big, .standard-p-big, .user-services-texts .user-service, .filters ul li.active .filter-item, .filters ul li, .standard-textblock h4, .standard-h4, .standard-textblock p.standard-p-small, .standard-p-small, .standard-textblock h2, .standard-h2, .standard-textblock p, .standard-p, .static-header .columns .columns-wrap, .header-wrap .columns .column a, .user-info h1, .user-info .author-title, .user-voter-value, .user-voter-action.disabled .user-voter-desc, .user-voter-title, .user-tabs-second .tab.active, .user-tabs-second .tab.active:hover, .user-info-stat-value, .user-info-stat-desc, .standard-textblock h5, .standard-h5, .standard-textblock ul, .standard-ul, table.user-stats, .album-info .album-author, .album-info, .user-info-status, .user-info .author-link, .photo h1, .photo .menu-item, .user-info .author-name, .photo-info, .photo .dropdown a.dropdown-item, .comments .comment-header h3, .comments .comment .comment-content .name, .date, .comments .comment .comment-content p, .header-wrap .head .user-content .money .dropdown .left, .header-wrap .head .user-content .money .dropdown .right, .comments .comment.small .right-part .date, .photo-info .item .type, .photo-info span b, .photo-info span a, .comments .comment .comment-content .answer, .cols-3 .col h3.critic, .comments .comment .comment-content .likes .count, .comments .write .right-part .crop, .cols-3 .col h3 span b, .cols-3 .col h3 a, .competition .comp-text, .info-block a, .comments .comment-head .dropdown-wrap a, .comments .comment-head .dropdown-wrap span, .service-section, .service-list-item, .album-info .album-name, .comments .comments-disallow, .flash-message, input, .album-new-title, .form-element label, .form-element input.is-inline[type="text"], .form-element input.is-inline[type="password"], .form-element label.is-inline, p.user-info-status-text, textarea, .user-tabs-second .tab.active .counter, .comment-goto, table.user-stats .user-table-folder a, .header-wrap .columns .column .name, .catalog-layout.cols p.description, input[type="text"], input[type="password"], textarea, .wallet-table th, .photo h1 a, .dropdown .col .group-title:hover
  {color:white}
  
  .user-tabs .tab, .user-tabs-second .tab, table.user-stats span.user-table-desc, .user-info-id, .user-tabs .tab .counter, .user-tabs-second .tab .counter
  {color: rgba(255, 255, 255, 0.5)}
  
  .album-item
  {border-right:1px solid #1b1b1b; border-bottom:1px solid #1b1b1b}
  
  .photo-info .item .count
  {text-shadow: -1px 0px black, 0px 1px black, 1px 0px black, 0px -1px black; color:yellow}
  
  .static-header, .header-wrap .columns
  {background-color:#272727; border-bottom:1px solid white}
  
  .photo-board .content, .photo-comment, .comments .comments-disallow, .flash-message, .form-element input.is-inline[type="text"], .form-element input.is-inline[type="password"], .form-element label.is-inline, p.user-info-status-text, textarea, .catalog-layout.cols p.description, input[type="text"], input[type="password"], textarea
  {background-color:#272727; border:1px solid white}
  
  .photos-content .photo-item img, .photo .photo-content img#big_photo, .photo-content + .photo-strip.scrollable .links-wrapper .links .photo-list div.photo-link a, .button, .user-tabs-second .tab.active .counter
  {border:1px solid white}
  
  .min-body .layout
  {border-right:unset}
  
  .icon-critic-big, .comments .comment .comment-content .likes .comment-vote, .comments .write .right-part .crop .icon, .competition h2 i, .icon-more, .photo .menu-item .icon-share-black, .filters ul li .arrows, .icon-like, .icon-warn, .icon-window, .icon-slice, .icon-logout, .icon-vote-up, .icon-vote-confirm, .icon-edit-status, .calendar, .album.is-new::before, .icon-critic
  {-webkit-filter: invert(100%); filter: invert(100%)}
  
  .icon-like.active
  {-webkit-filter: invert(0%); filter: invert(0%)}
  
  .button.button-small, .header-wrap .head .head-part .adult .trigger, .button, .form-element input.is-inline[type="text"], .form-element input.is-inline[type="password"], .form-element label.is-inline, textarea, input[type="text"], input[type="password"], textarea
  {border-radius:unset}
  
  .icon-warning, .icon-stop, .icon-like.active
  {-webkit-filter: grayscale(50%); filter: grayscale(50%)}
  
  .strip-moving-warning
  {background:rgba(239, 75, 62, 0.50)}
  
  .button.green
  {background-color:rgba(139, 184, 52, 0.5); border-color:rgba(104, 138, 39, 0.5)}
  
  .user-tabs .tab.active, .user-tabs-second .tab.active, .user-tabs .tab.active:hover, .user-tabs-second .tab.active:hover, .flash-message.warning-message, .comments .comment-header .button-container
  {background-color: unset}
  
  .photo h1 a, .header-wrap .columns .column a span
  {border-bottom: 1px solid rgba(255, 255, 255, 0.3)}
  
  div[class="sidebar"]
  {display:none !important}
  
  div[class="direkt-partner"]
  {display:none !important}
  
  .page-wrapper.page-with-sidebar .wrapper
  {margin:unset}
  
  body
  {color:white}
  
  .dk-option, .dk-select-options-highlight .dk-option-selected
  {color:black}
}
