css:
/* i really want this to be global */

html, body {  
    background: transparent url("http://www.tapeciarnia.pl/tapety/normalne/173716_warszawa_rzeka_most.jpg") no-repeat 50% 0 fixed;
	background-size:cover;
	font-size:85%;
}

body:after {
	width:100%;
	height:100%;
}

br {display: block !important; }

.navbar .rightside {
	display:none;
}

#wrap {
    padding: 0 20px;
    width: 1160px;
    margin: 0 auto;
	position:relative;
}

#search-box {
	position:absolute;
	top:120px;
	right:5px;
	margin-right:20px;
	z-index:100;
}

div#page-header {
	 position: relative;
}

.headerbar {
    background: transparent;
	height:150px;
}

#logo {
	left:0;
	top:0;
}

.navbar {
	padding:0;
	padding:5px 0;
}

.navbar .navlinks {
	 border-bottom:0;
	background-color:rgba(94,94,94,.5);	
	border-radius:5px;
}

.forabg,
.forumbg{
	background:#fff;
	margin-bottom:15px;
	padding:5px;
	border-radius:5px;
	box-shadow: 0px 5px 10px 0px rgba(102,102,102,1);
}

ul.linklist {
	padding:5px  !important;
}

ul.forums {
	background-image:none;
}

li.row {
	padding:10px;
	border-bottom:1px solid #d2d2d2;
	background-color:#f8f8f8;
}
li.row:last-child {
	border:0;
}
li.row:hover {
	background-color:#fff;
}


a.forumtitle,
a.topictitle {
	font-family:inherit;
	font-weight:normal;
	color:#39c;
	font-size:1.3em;
}
.post h3 a{
	font-family:inherit;
	font-weight:normal;
	color:#39c;
}

.forabg .header a,
.forumbg .header a,
.icon a,
.post a{
	color:#39c !important;	
	font-weight:normal;
}

li.header {
	padding:10px 0;
}
li.header dt,
li.header dd {
	color:#999;	
}

li.header dt {
	text-indent:55px;
}


.display-options,
.quick-login,
.jumpbox {
	background-color:rgba(94,94,94,.8);	
	padding:10px;
	border-radius:5px;
}
.quick-login label,
.display-options label,
.topic-actions label,
.jumpbox label {
	color:#fff;
}

.topic-actions{
	padding:10px 0;
}
.topic-actions .pagination{
	background-color:rgba(94,94,94,.8);	
	padding:5px;
	border-radius:5px;
}

.buttons div.post-icon,
.buttons div.reply-icon {
	background-color:#f93;
	width:auto;
	height:auto;
	border-radius:5px;
}
.buttons div.post-icon span,
.buttons div.reply-icon span {
	display:none;
}
.buttons div.post-icon a,
.buttons div.reply-icon a {
	padding:5px 10px;
}

#page-footer {
	background:transparent;	
}

hr { display:none }

.content {padding:10px; color:#666;}

.corners-top,
.corners-bottom { 
	display:none !important; 
}

.panel,
.post {
	border-radius:5px;	
	padding:10px;
	margin-bottom:0;
	border-bottom:1px solid #ccc;
}
.panel { margin-bottom:15px;}
.panel.bg3 {
	background-color:#fff;	
}
.panel h3 { 
	color:inherit;
	border-color:inherit;
}

/* PAGINATION */
.page-sep { display:none}

.pagination span strong,
.pagination span a,
.pagination span a:link {	
	padding:2px 5px;
	border:0;
}
.pagination span strong { background:transparent; }
li.row:hover .pagination span a {	
	color:#fff !important;
	background-color:#39c;
}


input.button1,
input.button2,
a.button1,
a.button2 {
	border:0;
	background:#39c;
	padding:5px 10px;
	color:#fff !important;
	display:inline-block;
	border-radius:5px;
}
input.button1:hover,
input.button2:hover,
a.button1:hover,
a.button2:hover {
	border:0;
}


/* FAQ */
.section-faq .panel {
	overflow:hidden;
	background-color:#fff;
	box-shadow: 0px 5px 10px 0px rgba(102,102,102,1);
}

.faq{
	font-size:1.2em;
}

.faq dd {
	padding:5px 0;
}
	


dl.icon dt {
	width:60%;
}
dl.icon dd:last-child {
	width:15%;
}


.postlink {
	border:0;
}
.postlink:hover {
	border:0;
	background:transparent;
	text-decoration:underline;
}
