css:
@-moz-document domain("dubtrack.fm") {
body {
  cursor:url(http://i1027.photobucket.com/albums/y340/Nassim_Amzil/dtl_mari_cursor_by_thatgeekytoonbubbles-d7p97b5_zpsqu2fllfa.gif~original), Crosshair	 !important;
}
/* Background colour */
#main_player,
.chat_tools,
#chat LI,
.pusher-chat-widget-input,
.avatar_tools,
#main_avatar_room LI,
.chat-option-header,
.chat-option-buttons SPAN{
  background-color: rgba(0, 0, 0, 0.70) !important;
}
  /* By default this is 10% more than the above. */
.play-song-link{
  background-color: rgba(0, 0, 0, 0.80) !important;
}
  /* Color-match the below with the above */
    /* Bottom filler */
#chat LI:last-child,
#avatar-list LI:last-child{
  box-shadow: 0 1500px 0 1500px rgba(0, 0, 0, 0.70) !Important;
}
    /* Bottom for user list */
#main_avatar_room{
  border-bottom: 16px solid rgba(0, 0, 0, 0.70) !important;
}
    /* Background hack for options */
.chat-options{
  background-image: linear-gradient(to bottom,
                                    transparent 240px,
                                    rgba(0, 0, 0, 0.70) 240px,
                                    rgba(0, 0, 0, 0.70) 100%) !Important;
}

/* Positioning and sizing */
#chat .chat-welcome-message,
#chat .system,
#chat .chat-system-loading{
  text-align: center !Important;
}

LI[class^='user'].deleted-message{
  padding-bottom: 16px !Important;
}

#chat LI[class^='user'].deleted-message P.deleted,
#chat .chat-me-command-joined P.deleted{
  padding-top: 18px !Important;
  padding-bottom: 22px !Important;
}

#chat LI[class^='user']:not(.deleted-message) P.deleted{
  padding-top: 8px !Important;
}

.chat_tools SPAN{
  height: 50px !Important;
}

.dubx #chat .chat-container .chat-messages .chat-main LI .stream-item-content .image_row,
.dubx #chat .chat-container .chat-messages .chat-main LI .chatDelete,
.avatar_tools{
  top: 0px !Important;
}
#chat .stream-item-content,
#chat .image_row,
#chat .chatDelete{
  margin-top: 16px !Important;
}
#chat .image_row,
#main_avatar_room .username{
  margin-left: 16px !Important;
}

#chat .timeago,
#chat .chatDelete,
#main_avatar_room .dubs{
  margin-right: 16px !Important;
}

#chat .chat-me-command-joined .chatDelete{
  margin-top: 13px !Important;
}

.chat-welcome-message,
#chat .chat-me-command-joined,
.avatar_tools,
.chat-option-header{
  padding-top: 8px !Important;
  padding-bottom: 8px !Important;
}

#main_avatar_room LI{
  padding-top: 4px !Important;
  padding-bottom: 4px !Important;
}

#main_avatar_room UL{
  padding-left: 0px !important;
  padding-right: 0px !Important;
}

#chat .stream-item-content,
.chat-welcome-message,
#chat .system,
#chat .chat-me-command-joined,
#chat .chat-system-loading{
  padding-left: 16px !important;
  padding-right: 16px !important;
}

.avatar_tools{
  margin-bottom: 8px !Important;
}

#chat .chat-me-command-joined,
#chat .chat-me-command-joined P.deleted,
#main_avatar_room LI{
  margin-bottom: 0px !Important;
}

#main_avatar_room{
  margin-top: 51px !Important;
  padding-top: 41px !Important;
  height: calc(100% - 51px) !important;
}

#chat .chat-options{
  height: 100% !Important;
}

  /* Gap removal */
#chat .ps-container{
  top: -3px !Important;
  height: calc(100% + 3px) !important;
}

#main_avatar_room{
  padding-bottom: 0px !Important;
}

.chat-option-header,
.chat-option-buttons{
  position: relative !Important;
  top: -5px !Important;
}
.chat-option-header{
  margin-top: 0px !important;
}

#private-messages .ps-container{
  top: -7px !Important;
  height: calc(100% + 22px) !important;
}

  /* Emote height override */
#chat .chat-me-command-joined{
  min-height: 0px !Important;
}

  /* Undoing DubX defaults */
.dubx #chat .chat-container .chat-messages .chat-main LI .stream-item-content .image_row,
.dubx #chat .chat-container .chat-messages .chat-main LI .chatDelete{
  top: 0px;
}
.dubx .chat-main.splitChat li:nth-child(2n) .chatDelete{
  right: 0px !important;
}

/* Border radius */
.chat_tools{
  border-radius: 16px 16px 0px 0px !important;
}

.pusher-chat-widget-input,
#main_avatar_room,
#chat .chat-options{
  border-radius: 0px 0px 16px 16px !Important;
}

/* Remove / Hide other stuff */
  /* Hide right-side colour (enables selective user colouring) */
.right_section{
  background-color: transparent !important;
}
  /* Chat padding */
#chat UL,
#chat .system{
  padding: 0px !Important;
}

LI.deleted-message{
  padding-top: 0px !Important;
  padding-bottom: 0px !Important;
  line-height: 0px !Important;
}

  /* Undoing DubX defaults */
.dubx #chat .chat-container .chat-messages .chat-main LI .activity-row,
.dubx #chat .chat-container .chat-messages .chat-main LI.chat-system-loading,
.dubx #chat .chat-container .chat-messages .chat-main LI.system,
.dubx #chat .chat-container .chat-messages .chat-main LI.system-error,
.dubx #chat .chat-container .chat-messages .chat-main LI.chat-me-command-joined{
  padding-top: 0px;
}

/* Other border settings */
.avatar_tools INPUT{
  border: none !Important;
}

.avatar_tools{
  border-bottom: 1px solid rgb(135, 140, 142) !important;
}

.deleted-message .timeago{
  display: none !Important;
}

/* User-specific colours
     Below you can specify colours for specific users. While placement of this code doesn't
     necessarily matter, keeping user-specific changes in a single place makes finding them
     a bit easier than otherwise. Use whatever colours, gradients and images you want!

     Below are example selectors, commented for obvious reasons.
  */
/* #chat LI.user-1ARANDOMUSERIDENTNUMBER0 *//* Whole chat item */
/* #chat LI .is? *//* Ranked users when DubX is used. Replace "?" with:
                      Vip, Dj, Resident-dj, Mod, Manager, Co-owner */
/* .user-1ARANDOMUSERIDENTNUMBER0 .username *//* Chat username */
/* .user-1ARANDOMUSERIDENTNUMBER0 P *//* Chat text */
/* .user-1ARANDOMUSERIDENTNUMBER0 .timeago *//* Chat since when */
/* #avatar-list .userid.1ARANDOMUSERIDENTNUMBER0 *//* User list item */

/*--chat--*/

/*messages*/
body #chat li:nth-child(odd) {
  background: rgba(50,50,60,.0);
}

body #chat li:nth-child(even) {
  background: rgba(50,50,60,.3);
}

body #chat .chat-container .chat-messages .chat-main li .stream-item-content .image_row {
  border-radius: 0;
}

body #chat .chat-container .chat-messages .chat-main li {
  padding: 0;
}

body #chat .chat-container .chat-messages .chat-main li .activity-row {
  padding: 4px 4px 4px 52px;
}

#chat .chat-container .chat-messages .chat-main li .stream-item-content .image_row {
  top: 4px;
  left: 4px;
}

#chat .chat-container .chat-messages .chat-main li .meta-info .timeinfo {
  bottom: 1px;
  right: 6px;
  font-size: 11px;
  font-weight: 500;
}

#chat .chat-container .chat-messages .chat-main li .chatDelete {
  top: 3px;
  right: 4px;
}

/*background*/
#chat .pusher-chat-widget-input {
  background: rgba(0,0,20,.4);
  border-top: 0;
  border-radius: 0 0 6px 6px;
}

#chat .chat_tools {
  background: rgba(0,0,20,.4);
  border-bottom: 0;
  border-radius: 6px 6px 0 0;
}


/*--footer--*/
body #main_player .player_header span {
  border-radius: 4px;
}

body #player-controller .left {
  background: #000;
}

body #player-controller .right {
  background: #222222;
}


/*--right section--*/
#main-room .right_section {
  border-radius: 6px;
  background: rgba(0,0,0,.7);
}


/*--main player--*/
#main_player {
  border-radius: 6px;
  background: rgba(0,0,0,.5);
}

#main_player .play-song-link {
  border-radius: 6px;
  background: rgba(0,0,0,.5);
  bottom: -75px;
}

#main_player .player_sharing .icon-facebook, 
#main_player .player_sharing .icon-twitter {
  display: none;
}


/*--playlists--*/
#browser .browser-content {
  border-radius: 6px 6px 0 0;
}




/*--options--*/

/*background*/


/*--player--*/
#main_player {
  opacity: .5;
  transition: .2s;
}

#main_player:hover {
  opacity: 1;
}

#main_player .player_container {
  padding-bottom: 220px;
  transition: .2s;
}

.main-player-wrapper:hover #main_player .player_container {
  padding-bottom: 55%;
}

/* ! Don't touch this; it places footer content
     above the "Footer overlay" pseudochild */
#player-controller *{
  z-index: 1 !Important;
}

/* Global font weight (except for dropdown) */
  /* ! Remove any of the :not tokens
       to also affect those elements
  */
*:not(SELECT):not(OPTION){
  font-weight: 200 !important;
  letter-spacing: 0.2px !Important;
}
  /* Exceptions */
.videoquality-el{
  font-weight: 700 !Important;
}
    /* Browser dropdown letter spacing fix */
.icon-arrow-dropdown{
  letter-spacing: 0px !Important;
}

/* Inport button fix */
  /* Make entire button's font proxima nova
     (or sans-serif default if not installed)
  */
SPAN.import-playlist.icon-inbox{
  font-family: proxima nova, sans-serif !Important;
}
  /* Make button icon use Dubtrack's icon font */
SPAN.import-playlist.icon-inbox:before{
  font-family: icomoon !Important;
}

/* Animation stuff */
  /* Types */
  /* ! Match "to" BG position first value with
       BG size in "Animation size and type" */
@keyframes rainbowProg{
  from{
    background-position: 0px 0px
  }
  to{
    background-position: 800px 0px
  }
}
@keyframes rainbowVol{
  from{
    background-position: 0px 0px
  }
  to{
    background-position: 200px 0px
  }
}
  /* Constants */
#player-controller .left,
#player-controller .infoContainerInner:after,
#player-controller .progressBg,
.ui-slider-range-min,
.ui-slider,
.playerPreview .loaded,
.playerPreview .progress{
  /* ! Duration for speed; Lower is faster */
  animation-duration: 5s !Important;
  animation-timing-function: linear !important;   /* <--+++++++++++++++++++++++++++ */
  animation-iteration-count: infinite !important; /* <--+                         + */
  background-repeat: repeat-x !Important;         /* <--+ Don't touch any of this + */
  background-attachment: fixed !Important;        /* <--+                         + */
  background-color: transparent !Important;       /* <--+++++++++++++++++++++++++++ */
}

/* Colours */
  /* Footer overlay */
#player-controller .infoContainerInner:after {
  z-index: 0; /* ! Don't touch this, explained at bottom */
  width: 100%;        /* <--+++++++++++++++++++++++++++++++++++++++++++++ */
  height: 5px;        /* <--+                                           + */
  content: '';        /* <--+   Don't touch any of this; These define   + */
  position: absolute; /* <--+ pseudochild size, position and visibility + */
  bottom: 0;          /* <--+                                           + */
  left: 0px;          /* <--+++++++++++++++++++++++++++++++++++++++++++++ */
}
  /* Volume slider */
.ui-slider-handle{
  background-color: #fff !Important;
}
  /* Rainbow gradients */
    /* Bright */
#player-controller .progressBg,
.ui-slider-range-min,
.playerPreview .progress{
  background-image: linear-gradient(to left,
                                    rgb(255, 0, 0) 0%,
                                    rgb(255, 128, 0) 8.34%,
                                    rgb(255, 255, 0) 16.67%,
                                    rgb(128, 255, 0) 25%,
                                    rgb(0, 255, 0) 33.34%,
                                    rgb(0, 255, 128) 41.67%,
                                    rgb(0, 255, 255) 50%,
                                    rgb(0, 128, 255) 58.33%,
                                    rgb(0, 0, 255) 66.67%,
                                    rgb(128, 0, 255) 75%,
                                    rgb(255, 0, 255) 83.33%,
                                    rgb(255, 0, 128) 91.67%,
                                    rgb(255, 0, 0) 100%) !Important;
}
    /* Dark */
#player-controller .infoContainerInner:after,
.ui-slider,
.playerPreview .loaded{
  background-image: linear-gradient(to left,
                                    rgb(47, 0, 0) 0%,
                                    rgb(47, 23, 0) 8.34%,
                                    rgb(47, 47, 0) 16.67%,
                                    rgb(23, 47, 0) 25%,
                                    rgb(0, 47, 0) 33.34%,
                                    rgb(0, 47, 23) 41.67%,
                                    rgb(0, 47, 47) 50%,
                                    rgb(0, 23, 47) 58.33%,
                                    rgb(0, 0, 47) 66.67%,
                                    rgb(23, 0, 47) 75%,
                                    rgb(47, 0, 47) 83.33%,
                                    rgb(47, 0, 23) 91.67%,
                                    rgb(47, 0, 0) 100%) !Important;
}

/* Animation size and type */
  /* Progress bars */
#player-controller .left,
#player-controller .infoContainerInner:after,
#player-controller .progressBg,
.playerPreview .loaded,
.playerPreview .progress{
  background-size: 800px 100% !Important;
  animation-name: rainbowProg !Important;
}
  /* Volume */
.ui-slider-range-min,
.ui-slider{
  background-size: 200px 100% !Important;
  animation-name: rainbowVol !Important;
}

/* Opacity for player preview */
.playerPreview .loaded{
  opacity: 1 !Important;
}

/* ! Don't touch this; it places footer content
     above the "Footer overlay" pseudochild */
#player-controller *{
  z-index: 1 !Important;
}

.room-item .name{
  color: magenta !Important;
}

.room-item .description{
  padding: 0px !important;
  margin: 0px !important;
  text-align: center !Important;
}

.room-item .current-song{
  position: relative !important;
  top: -2px !important;
  font-style: normal !important;
}

.room-item .user-info{
  padding: 0px !important;
  margin: 0px !important;
}
.room-item .room-user{
  float: right !important;
  position: relative !important;
  top: -1px !important;
  padding-right: 8px !important;
}

.room-item .name{
  font-size: 18pt !important;
  line-height: 24px !important;
  position: relative !important;
  padding-left: 4px !important;
  padding-right: 4px !important;
  top: 2px !important;
}

.room-item *,
FIGURE{
  border-radius: 0px !Important;
}
/* Body */
a:active, a:focus, a:hover {
    color: #fff;
}
a, body, button, input, select, textarea{
    font-family: 'Arial', sans-serif;
    color: #ccc;
}
#header-global .header-left-navigation a.room-active-link.active-room .icon-chevron {
    color: #5A93CC;
}
#warning {
    background: #935ACC;
    color: #222229;
}

/* Main page */
#main-loading {
    opacity: 1;
    background: rgba(50,50,50,1);
    color: #eee;
    border-radius: 1rem;
}

/* Mobile menu */
#mobile-room-menu {
    background: rgba(15,15,15,0.66);
}
#mobile-room-menu a {
    font-size: .85rem;
    font-weight: 700;
    border-right: none;
}

/* Top bar */
#header-global {
    /*background: linear-gradient(to bottom, rgba(20,20,20,0.66) 0%, rgba(20,20,20,0.66) 90%, rgba(20,20,20,0) 100%);*/
    background: rgba(15,15,15,0.66);
}
#header-global .header-right-navigation .user-info span:hover,
#header-global .header-right-navigation .user-messages:hover,
#header-global .header-right-navigation .for:hover {
    color: #fff;
}
#header-global .header-right-navigation .user-messages .message-counter {
    background: #CC5A93;
}
#header-global .header-right-navigation .user-messages .message-counter {
    color: #fff;
}
/*
#header-global .header-left-navigation h1 {
    width: 32px !important;
    width: 2rem;
    height: 32px !important;
    height: 2rem;
    background-image: url(https://raw.githubusercontent.com/PossiblyKris/DubtrackCSS/master/assets/icons/cm.png) !important;
    background-size: 2rem 2rem;
}
*/
/*
#main-room-active-link .room-name:after {
    content: " Sample text";
    display: block;
    position: absolute;
    margin-top: -24px;
    margin-left: 166px;
}
*/
/* Bottom bar */
/*@media screen and (min-width: 72em) {}*/
#player-controller {
    /*background: linear-gradient(to top, rgba(20,20,20,0.5) 0%, rgba(20,20,20,0.25) 60%, rgba(20,20,20,0) 100%);*/
    background: rgba(15,15,15,0.42);
}
#player-controller .left {
    /*background: linear-gradient(to top, rgba(20,20,20,0.5) 0%, rgba(20,20,20,0.30) 60%, rgba(20,20,20,0) 100%);*/
    background: rgba(15,15,15,0.42);
}
#player-controller .right {
    background: rgba(0,0,0,0);
}
#player-controller .left ul li.imgEl {
    cursor: pointer;
}
#player-controller .imgEl img {
    border-radius: 100%;
    margin: 5px;
    max-width: 83%;
}
#player-controller .left ul li.infoContainer .infoContainerInner {
    padding: .5rem 5rem .5rem 1rem;
}
#player-controller .left ul li.infoContainer .infoContainerInner .currentDJSong {
    color: #888;
    font-size: .95rem;
}
#player-controller .left ul li.infoContainer .infoContainerInner .currentTime,
#player-controller .left ul li.infoContainer .infoContainerInner .currentSong {
    color: #ddd;
}
#player-controller .left ul li.infoContainer .infoContainerInner .progressBg {
    background: #5A93CC;
    border-radius: 1rem;
    left: 1rem;
}
#player-controller .right ul li a.dubup:hover,
#player-controller .right ul li a.dubup.voted,
#player-controller .right ul li a.dubdown:hover,
#player-controller .right ul li a.dubdown.voted,
#player-controller .right ul li a.add-to-playlist-button.grabbed {
    background: rgba(0,0,0,0);
}
#player-controller .right ul li a.dubup.voted span,
#player-controller .right ul li a.dubup:hover:not(.voted) .icon-arrow-up {
    color: #93CC5A;
}
#player-controller .right ul li a,
#player-controller .right ul li a.dubup:hover:not(.voted) span,
#player-controller .right ul li a.dubup .icon-arrow-up,
#player-controller .right ul li a.dubdown:hover:not(.voted) span,
#player-controller .right ul li a.dubdown .icon-arrow-down,
#player-controller .right ul li a.add-to-playlist-button.grabbed span,
#player-controller .right ul li a.add-to-playlist-button:hover span,
#player-controller .right ul li a span.icon-heart:before {
    color: #ccc;
}
#player-controller .right ul li a.dubdown.voted span,
#player-controller .right ul li a.dubdown:hover:not(.voted) .icon-arrow-down {
    color: #FF4060;
}
#player-controller .right ul li a:hover span.icon-heart:before,
#player-controller .right ul li a.add-to-playlist-button.grabbed span,
#player-controller .right ul li a.add-to-playlist-button.grabbed .icon-heart:before {
    color: #CC5A93;
}
#player-controller .right ul li .display-browser:hover .icon-playlist {
    color: #5A93CC;
}


/* Right side menu */
#private-messages .loading {
    background: rgba(50,50,50,1);
    color: #eee;
    border-bottom-left-radius: .5rem;
    border-bottom-right-radius: .5rem;
}
#private-messages .main-message-list, 
#private-messages .message-details {
    background: linear-gradient(to bottom, rgba(15,15,15,0.75) 0%, rgba(30,30,30,0.95) 10%, rgba(15,15,15,0.95) 90%, rgba(15,15,15,0.66) 100%);
}
#main-menu-right .close-menu {
    background: linear-gradient(to bottom, rgba(25,25,25,1) 0%, rgba(25,25,25,1) 175%);
}
#main-menu-right .close-menu:hover span {
    color: #fff;
}
#main-menu-right .close-menu .icon-close {
    padding: 0 0 0 2px;
    color: #aaa;
}
#main-menu-right .close-menu .icon-close:before {
    content: "\e800";
    line-height: 1.9;
    font-size: 20px;
    padding-left: 1px;
}
#private-messages .message-details .message-header {
    padding: 1em 1em;
    border-bottom: 1px solid #333;
    background: #1a1a1a;
    z-index: 12;
}
#private-messages .main-message-list .message-header, 
#private-messages .message-details .message-header {
    background: rgba(25,25,25,1);
    border-bottom: 1px solid #222;
}
#private-messages .main-message-list .message-header input, 
#private-messages .message-details .message-header input {
    border: 1px solid rgba(40,40,40,1);
}
#private-messages .main-message-list ul.message-list li.message-item, 
#private-messages .message-details ul.message-list li.message-item {
    border-bottom: 1px solid #222;
}
#private-messages .main-message-list .message-header a, 
#private-messages .message-details .message-header a {
    color: #aaa;
    font-size: 1rem;
}
#private-messages .main-message-list .message-header a:hover, 
#private-messages .message-details .message-header a:hover {
    color: #eee;
}
#private-messages .main-message-list ul.message-list li.message-item .message-content h3, 
#private-messages .message-details ul.message-list li.message-item .message-content h3 {
    color: #5A93CC;
}
#private-messages .main-message-list ul.message-list li.message-item.new-message:before, 
#private-messages .message-details ul.message-list li.message-item.new-message:before {
    background: #5A93CC;
}
#private-messages .private-message-details-info, 
#private-messages .private-message-info {
    color: #aaa;
}
#private-messages .main-message-list ul.message-list li.message-item .message-content .username-handle, 
#private-messages .message-details ul.message-list li.message-item .message-content .username-handle {
    color: #eee;
    font-weight: 400;
}
#main-message-list .search-results .search-results-users .result-item {
    background: rgba(20,20,20,1);
}
#main-message-list .search-results .search-results-users .result-item:hover {
    background: #333;
    color: #fff;
}

/* Left side Menu and Lobby */
#main-menu-left {
    padding: 5rem 0 0;
    background: linear-gradient(to bottom, rgba(20,20,20,0.66) 0%, rgba(20,20,20,0.75) 10%, rgba(20,20,20,0.75) 90%, rgba(20,20,20,0.66) 100%)!important;
    height: calc(100% - 114px);
    left: -224px;
    left: -14rem;
    top: 58px;
    width: 215px;
}
#main-menu-left a,
#main-menu-left a span,
#main-menu-left a.room-active-link {
    color: #ccc;
}
#main-menu-left a:hover,
#main-menu-left a span:hover,
#main-menu-left .close-menu span:hover {
    color: #fff;
}
/* Lobby */
#room-list .room-loby-header {
