css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nyan.cat") {
  .bottom-pane, .announce, #storeitem, .tweet-score, a, .langchooser, iframe, #twitter-widget-1 {
    visibility: hidden !important;
    position: fixed !important;
    z-index: 1 !important;
    top: 0px !important;
  }

  body {
    overflow: hidden;
  }

  #muctr {
    visibility: hidden;
  }
  
  .time {
    height: 10px;
  }
  
  #storeitem {
    height: 0px;
    position: fixed;
    top: 0px;
  }
  
  #andcopy {
    margin-top: -100px;
  }
  
  img[src*="/images/lovebot.gif"] {
    opacity: 0;
  }
}
