css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mokum.place") {
  
  .box-header, .box-header-empty, .box-header-timeline, .box-header-friends, .box-header-groups {
    border: none;
}


body {
    background-image: url("http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/sativa.png");
    background-size: auto;
    background-attachment: fixed;
  }


.content .box-body {
    background-color: #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}

.sidebar .box-body {
       background-color: #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
 
div.box-header-timeline {
    font-size: 16px;
}

li.previous a, li.next a {
    background-color: #d1d1d1;
    color: #000000;
}

}
