css:
@-moz-document regexp("(https|http)://(www|vb).forumua.org/"), regexp("(https|http)://forumua.org/(?!forum).*"), regexp("(https|http)://(www|vb).forumua.org/(?!forum).*") {
.topics-count {
    display:none;
}
.posts-count {
    display:none;
}
.header-topics {
    display:none;
}
.header-posts {
    display:none;
}
.header-lastpost {
    border-left: 1px solid #A9B8C2 !important;
}

.ad_184_inner {
    display: none !important;
}

/*ширина столбца с последними сообщениями*/
.lastpost {
	width: 40% !important
}
}

@-moz-document regexp("(https|http)://forumua.org/search.*"), regexp("(https|http)://(www|vb).forumua.org/search.*") {
/* Отображение списка "Мои сообщения" и "Мои темы" 
в предыдущем варианте отображался неправильно из-за отключения столбцов на главной странице */

.header-lastpost {
    border-left: 0px solid #A9B8C2 !important;
}

/*внутри разделов*/
/* убрать левые границы т.к. если будет отключение столбцов будут сдвоенные с правой границей темы*/
.header-votes
{
    border-left: 0px !important;
}
.votes-count{
    border-left: 0px !important;
}

/* количество тем */

.views-count {
    border-left: 0px !important;
    border-right: 1px solid #A9B8C2 !important; /*правая почему-то отсутствовала в исходнике*/

}
.header-views {
    border-left: 0px !important;
    border-right: 1px solid #A9B8C2 !important;
}


/* количество сообщений */

.posts-count {
    border-left: 0px !important;
}
.header-posts {
    border-left: 0px !important;
}

/*----------------------------------------------------*/
/*вкл/выкл столбцов*/
/*голоса/лайки*/
.header-votes
{
    display: none;
}
.votes-count{
    display: none !important;
}

/* количество просмотров */

.views-count {
    display: none !important;
}
.header-views {
    display:none;
}


/* количество сообщений */
/*
.posts-count {
    display: none !important;
}
*/
/* заголовок количества сообщений включается принудительно, т.к. отключается в предыдущем разделе*/
.header-posts {
    display: table-cell;
}


/*---------------------------------------------------------------*/
/*правая граница ячейки темы*/
.header-topic{
        border-right: 1px solid #A9B8C2 !important;
}
.cell-topic{
        border-right: 1px solid #A9B8C2 !important;
}
}

