@-moz-document domain("twitch.tv") {  
/*** misc ***/
.has-sc--collapsed .social-column{margin-left:-48px;}
.has-sc--collapsed .social-column:hover{margin-left:0px;transition: 0.2s padding-left;}
.has-sc.has-sc--collapsed #main_col, .has-sc.has-sc--collapsed #flash {margin-left:0px}
.conversations-list .conversations-list-item-archive{height: 2.6rem;width: 2.6rem;}
#channel .player-column #video-1, .cn-hosted , .app-main.theatre .player--authenticated{margin-bottom: 0px;}
.has-sc.has-sc--collapsed #main_col.expandLeft .cn-bar-fixed {left: 0px;}
.app-main.theatre .js-player{top:0px !important;margin-left:0px !important;}
.popup{top:35px!important;left:inherit!important;right:5px!important;z-index:11 !important;}
.popup .dropmenu{padding:20px!important;z-index:11 !important;}
.notify-menu{z-index:11!important;position:absolute;bottom:inherit!important;top:35px!important;left:inherit!important;right:5px!important;opacity:1!important;}
.js-options-dropdown{top:35px!important;left:inherit!important;right:5px!important;}
.follow-button,.toggle-notification-menu,.subscribe-button,.svg-glyph_live{opacity:0.95;transition: opacity 0.3s;}
.upsell-banner{margin:110px 36px;padding:10px;background: rgba(0,0,0,0.6);z-index:5;color:#ddd;border-left: 5px solid #646464;}
#panels-edit-contain{position:absolute;top:calc(100% + 20px);left:30px;z-index:4;}
.following-list .game{position:absolute;left:60px;top:28px;width:190px;z-index:2;}
.following-list .channel{position:relative;}
.following-list .right{z-index:2!important;position:absolute;right:10px;}
.target-title{line-height:110%!important;margin-top:-100px;}
.closed .warp__item > span, .closed .warp__item > button, .closed .warp__item > a, .player-menu__menu {padding: 0rem;}
.share.balloon--up {top: 35px;left: -165px;bottom: auto;margin-bottom: 6px;}
.channel_options.balloon--up {top: 35px;left: -100px;bottom: auto;margin-bottom: 6px;}
/** new header bar **/
.has-sc #main_col.expandLeft .cn-bar-fixed, .has-sc.has-sc--collapsed #main_col.expandLeft .cn-bar-fixed{left:0px}
.has-sc .cn-bar-fixed {top:0px}
.cn-bar{position: absolute; top: 0px;left: 0px;  right: 0px; box-shadow: none!important;padding: 0px 40px 0px 40px!important}
  .cn-nav-wrapper{margin:0px 40px 0px 30px}
#left_col.open ~ #main_col .cn-bar {left:0px}
.metadata-box {position: absolute;bottom: 0px;left: 0px;  right: 0px; box-shadow: none!important;padding: 0px 40px 0px 30px!important;z-index: 5;}
.cn-hosting--top {position: absolute;bottom: 0px; right: 0px; background: none!important; box-shadow: none!important;padding: 0px 40px 0px 30px!important;z-index: 6;}
.cn-hosting--bottom {position: absolute;bottom: 0px; left:0px; right: 0px; padding: 0px 40px 0px 30px!important;z-index: 5;}
.cn-hosting a:hover .card__img--avatar, .tw-tabs__item>a:hover {box-shadow: 0 2px 0 0 #fff;}
.cn-content #channel, .cn-metabar .cn-metabar__title, .cn-metabar__more{padding-top: 0px;}
.cn-metabar, .metadata-box{height: 67px; min-height: 65px}
.mg-b-3{margin-bottom: 0 !important;}
.channel_options.balloon--up{top: -55px; left: -125px}
.cn-metabar{background: none!important;padding: 0;margin: 0}
.cn-bar__avatar-wrap{width: 5rem!important;height: 5rem!important; left: 70px;}
.cn-tabs__item > a.cn-tabs__profile::after{width: 5rem!important;}
.cn-tabs__item > a.active::after{border-color: rgba(255, 255, 255, 0.25) !important;}
#channel, .cn-content #channel {height:100%!important; min-height: 100%;}
.cn-tabs{background: none!important;}
.cn-bar-fixed .cn-bar__displayname{padding-left: 5rem;}
.cn-tabs__item--withseparator::after{border-right: 1px solid rgba(255,255,255,0.25);}
.cn-tabs__item > a:hover .cn-tabs__count {color: #FF0000}
/*** main area formatting ***/
#broadcast-meta{padding-left:45px!important;}
.stats-and-actions{padding:10px 16px 0 0;position:absolute!important;top:0px; right:10px;border:0;background:#000;width:98%;height:52px!important;}
#channel .player-column #broadcast-meta .info .edit-link span,.follow-button:not(.ember-follow) .follow,.theatre-button{color:#666;opacity:0.8;}
.app-main.theatre #channel .player-column #player{top:0px !important;margin:0px !important;padding: 0px;position:absolute !important;width: 100% !important;height: 100% !important;z-index:7;}
.theatre .player-controls-bottom, .theatre .player[data-controls="true"] .player-controls-bottom {padding-bottom: 0px}
.player-column, .host-frame{background:#000;height:117px;}  
.dynamic-player, .dynamic-player object, .dynamic-player video{width:100%!important;}
#channel .player-column #player {top:50px;position:absolute !important;width: 100% !important;height: calc(100% - 117px) !important;z-index: 4;}
#channel .player-column #video-1, .cn-hosted {position:absolute !important;bottom: 50px; width: 100% !important;height: 100% !important; background: none}  
.js-player-persistent {top:50px!important;width: 100% !important;height: calc(100% - 117px) !important;margin:0px!important;}
.player-mini{width: 500px!important; height: 330px !important;position:fixed !important; bottom: 40px!important; top:auto!important;}
.player-mini .player{width: 500px!important; height: 281px !important;}
.cn-hosted{top:0px}
div#player{box-shadow: 0 10px 20px -9px rgba(100, 100, 100, 0.5);}
.follow-button:hover,.toggle-notification-menu:hover,.subscribe-button:hover,.svg-glyph_live:hover{opacity:1;}
.warp__tab::after {background-color: #E0E0E0 !important;}
#right_close, #left_close{z-index:1000}
#left_close{left:10px!important;}
#left_close.closed{left:10px!important;}
#right_close{right:5px!important;}
#main_col .tse-scroll-content {overflow-y: scroll;}
#left_col .tse-scroll-content{overflow:hidden!important;}
.balloon--up::after {top: 0%; margin-top: -3px; left: 200px; border-radius: 0 0 1px; box-shadow: 1px 1px 0 rgba(0,0,0,.2);}
.ember-chat .chatters-view {background-color: rgba(10,10,10, 0.75)!important;}
.tabs__item--active, .tabs__item.active, .tabs__item.selected, .tabs > li.selected, .tabs > li > a.active {box-shadow: 0 -2px 0 #FFF inset;}
.tabs > .tab > a:focus, .tabs > li > a:focus, .tabs__item > a:focus {outline: rgb(100,100,100) auto 5px;}
.tabs > .tab:hover, .tabs > li:hover, .tabs__item:hover {box-shadow: 0 -1px 0 rgb(100,100,100) inset;}
/** resized right panel **/
  
#main_col:not([class*="expandRight"]):not([style*="margin-right: 340px;"]){margin-right:/*[[ChatWidth]]*/!important;}
#main_col[style*="margin-right: 340px;"]{margin-right:/*[[ChatWidth]]*/!important;}
#right_col:not([style*="width: 0px"]):not([style*="width: 340px"]){width:/*[[ChatWidth]]*/!important;}
#right_col .content, #right_nav,#chat.stretch{width:/*[[ChatWidth]]*/!important;}
.app-main.theatre #player{right: /*[[ChatWidth]]*/!important;}
  
.chat-container, #right_col, .tse-scrollbar, .app-main, #right_col.tab-container, .resizer, .chat-room-list, .chat-menu{background-color:rgb(12,12,12)!important;}
.twitch_subwindow_container{background: rgba(24, 24, 24, 0.9) none repeat scroll 0% 0%;}  
.warp__item--toggled > a, .brick--marked, .conversation-window .convoHeader {box-shadow: 3px 0 0 #FFF inset!important;}  
.theme--dark .social-column {box-shadow: -1px 0 0 rgb(30,30,30) inset}
/*** background ***/
.cn-nav-wrapper .flex, .newsfeed {background: none!important;}
.progress-bar--countdown .progress-bar__fill, .cn-tabs__item>a.active:after  {background-color:#FFF!important}
