#large_nav .game_filter.selected a:not(.button){border-left: 4px solid #FFF!important;}
#small_nav .game_filter.selected a:not(.button){border-left: 2px solid #FFF!important;}
#channel_panels_contain #channel_panels .panel h3 {color:#fefefe!important;}
#channel .player-column #broadcast-meta .info .channel {left:40px !important;font-size:10px !important;color:#999 !important;line-height:12px !important;}
#channel .player-column #broadcast-meta .profile-link .profile-photo img {padding-left:0px !important;width:35px !important;height:35px !important;}
.profile-link .profile-photo div:hover{font-size:6px;}
.channel-actions{position:absolute!important;top:0px; right:15px;z-index:9!important;}
.channel-stats{position:absolute!important;top:30px !important; right:10px!important;z-index:6!important;font-size:10px!important;}
