.editable{margin:6px 280px 6px 18px !important;position:absolute!important; top:0; left:0;width:calc(100% - 370px);z-index:11}
.profile-photo{background-color:transparent!important;border-color:transparent!important;}
.banner-contain .channel-link .live-status.live{background: #646464 none repeat scroll 0% 0%!important;}
.profile-link{opacity:0.9;} 
/*** following etc. ***/
#channel .player-column .stats-and-actions{padding:1px 0px!important;}
#right_col, .chat-container {box-shadow: 1px 0px 0px 0px rgba(255, 255, 255, 0.2) inset !important;}
.ember-chat .chat-header {box-shadow: 0px -1px 0px 0px rgba(255, 255, 255, 0.2) inset !important;}
.tse-scrollbar .drag-handle {box-shadow: 0px 0px 1px 1px rgba(0, 0, 0, 0.25) !important;}
.whatisthis {box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.25) !important;}
.whatisthis:before {border-color: transparent transparent #202020 !important;}
.whatisthis:after {border-color: transparent transparent rgba(255, 255, 255, 0.25) !important;}
#flyout .point:before {border-right-color: #202020 !important;}
#flyout .point:after {border-right-color: rgba(255, 255, 255, 0.25) !important;}
#site_header {box-shadow: 0px 0px 0px 2px rgba(0, 0, 0, 0.07), 0px 0px 0px 1px rgba(223, 223, 223, 0.2) !important;}
#carousel .nav ul li.active a:before {box-shadow: 0px 0px 0px 2px #000 inset, 0px 0px 0px 3px #FFF inset !important;}
#carousel .nav ul li a:before {box-shadow: 0px 0px 0px 2px #000 inset, 0px 0px 0px 3px #555 inset !important;}
pre {border-color: #6441A5 !important;box-shadow: 0px 0px 0px 1px #666 inset !important;}
.fullwidth_light {box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.2), 0px 0px 0px -1px rgba(255, 255, 255, 0.2) !important;}  
/*** images and fillers ***/
.svg-glyph_followers path,.svg-glyph_views path, .button.glyph svg path, .button.glyph-only svg path, .ember-chat .chat-menu-button-container svg path, #header_logo svg path, .svg-conversations, .svg-gear, .svg-viewerlist, .svg-close, .svg-popout, .svg-plus, .svg-star, .svg-minimize, .svg-hide, .svg-close_small, .bits-toggle.toggled path, .svg-emoticons, .svg-add-friend, .player-menu-html5-back-icon, svg path {fill: #7e8c9c !important;}
.button.glyph-only:hover svg path, .ember-chat .chat-menu-button-container:hover svg path, .hostmode-title-container .hostmode-title .host-profile-image svg path, .player-button:hover svg path, .game_filter.selected svg path, .svg-more {fill: #FFF !important;}
.channel-stats .stat svg path, #dash_main #stats .stat svg path, .button figure svg, .button figure img {fill: rgba(255, 255, 255, 0.35) !important;}
.channel-stats .live-count svg path, #dash_main #stats .stat.channel-viewer-count svg path {fill: #F00 !important;}
.following-col .col-header .search-contain .search .search-button svg path {fill: rgba(255, 255, 255, 0.25) !important;}
.ember-chat .chat-interface .textarea-contain .emoticon-selector-toggle svg path, .notification__dismiss svg {fill: rgba(255, 255, 255, 0.2) !important;}
.following-col .col-header .search-contain .search .search-button:hover svg path, .ember-chat .chat-interface .textarea-contain .emoticon-selector-toggle:hover svg path, .player-button svg path {fill: rgba(255, 255, 255, 0.5) !important;}
#channel .player-column #broadcast-meta .info .edit-link svg path {fill: rgba(255, 255, 255, 0.5) !important;}
.message_action.block {background-image: url("data:image/png;base64,") !important;}
.message_action.delete {
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAXklEQVQ4y2P4//8/AyWYYfAYgAyAfGUgNkbCUmjyWF0ghqYJFxbGZYAUkQZI4fQCkFZDUoRMw1ynhtMLaAbw4qBHDSDBAF00WhNKKxMyQACIDXDEP0icC19KHJjMBAALasrYcQA+LAAAAABJRU5ErkJggg==") !important;}
img[src*="g16_trash-00000020"] {visibility: hidden !important;}
.items-grid .items .game .game-item{background:rgb(30,30,30)!important;}
.items-grid .items .game a:hover{background:rgb(35,35,35)!important;}
/*** archive video ***/
#main_col .content #broadcast_meta .info .title_over h1, #main_col .content #broadcast_meta .info .title_over .title, #main_col .content #broadcast_meta #channel .player-column #broadcast-meta .info .title_over .title, #channel .player-column #broadcast-meta #main_col .content #broadcast_meta .info .title_over .title {background-color: #000 !important;}
#right_col #archives {background-color: #1A1A1A !important;}
#right_col #archives .video a:hover, #right_col #archives .more_archives:hover {background-color: rgb(40,40,40)}
#main_col .content #stats_and_actions {border-color: rgba(255, 255, 255, 0.2) !important;}
#right_col #archives .video a, #right_col #archives .more_archives {border-top-color: #444 !important;border-bottom-color: rgba(0, 0, 0, 0.5) !important;}
#right_col #archives .more_archives {box-shadow: 0px 1px 0px #444, 0px 1px rgba(0, 0, 0, 0.5), 0px 2px 0px rgba(0, 0, 0, 0.5) !important;}
#main_col .content #broadcast_meta .info .title, #main_col .content #broadcast_meta .info .title_over h1, #main_col .content #broadcast_meta .info .title_over .title, #main_col .content #broadcast_meta #channel .player-column #broadcast-meta .info .title_over .title, #channel .player-column #broadcast-meta #main_col .content #broadcast_meta .info .title_over .title, #right_col #archives .video a .title {color: #DDD !important;}
#main_col .content .archive_info p, #main_col .content .archive_info time {color: #BBB !important;}
#main_col .content #broadcast_meta .info .channel, #main_col .content #stats_and_actions #channel_stats .stat {color: #999 !important;}
#main_col .content #stats_and_actions #channel_stats .stat svg path {fill: rgba(255, 255, 255, 0.35) !important;}
/*** team page ***/
ul#nav_small, a.filter_icon, a.clearfix {background-color: rgb(30,30,30) !important;}
#stats_and_description, .user_list .user, .mixed_list .user, .video_list .video, .mixed_list .video {border-color: #333 !important;}
.member {box-shadow: 0px -1px 0px #333, 0px 1px 0px rgba(32, 32, 32, 0.65) !important;transition: none !important;}
.member.live {background-color: #a5a5a5;transition: all 0.2s ease-in-out 0s !important;}
.channel_count small{background-color: #a5a5a5!important;}
.member.js-playing {background-color: #646464 !important;background: -moz-linear-gradient(center top , rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.3)) repeat scroll 0% 0% #646464 !important;box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.1) inset, 0px 1px 1px rgba(32, 32, 32, 0.35) !important;}
.member.js-playing + div {box-shadow: 0px 1px 0px rgba(32, 32, 32, 0.65) !important;}
.member:hover {background-color: #202020 !important;}
.member.js-playing:hover, .member.js-playing:hover p {background-color: #202020 !important;background: -moz-linear-gradient(center top , rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.3)) repeat scroll 0% 0% #202020 !important;}
.member a, #team_member_list .page_links em {color: #FFF !important;}
.member .channel_count {color: #DDD !important;}
.video_list .video .title a, .mixed_list .video .title a, .user_list .user .title a, .mixed_list .user .title a {color: #C4C4C4 !important;}
.video_list.external .video .cap_and_profile .profile, .mixed_list.external .video .cap_and_profile .profile {background-color: #000 !important;box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.35) !important;}
#team_member_list .page_links a b.right {border-left-color: #a5a5a5;}
#team_member_list .page_links a b.left {border-right-color: #a5a5a5!important;}
.page_links span.disabled.next_page b, .page_links a.disabled.next_page b {border-left-color: #646464!important;}
.page_links span.disabled.previous_page b, .page_links a.disabled.previous_page b {border-right-color: #646464!important;}
/*** new icons ***/
#stats_and_description .stat#views_count, .video_list .video .video_stats .views_count, .mixed_list .video .video_stats .views_count {background-image: url("http://www-cdn.jtvnw.net/images/xarth/g/g18_eye-FFFFFF80.png") !important;}
#stats_and_description .stat#followers_count, .user_list .user .user_stats .followers_count, .mixed_list .user .user_stats .followers_count {background-image: url("http://www-cdn.jtvnw.net/images/xarth/g/g18_heart-FFFFFF80.png") !important;}
.g18_info-00000080 {background-image: url("http://www-cdn.jtvnw.net/images/xarth/g/g18_info-FFFFFF80.png") !important;}
img[src*="g18_camera-00000080"] {position: relative !important;right: 18px !important;padding-right: 18px !important;margin-right: -14px !important;background-image: url("http://www-cdn.jtvnw.net/images/xarth/g/g18_camera-FFFFFF80.png") !important;}
/*** item removals ***/
[data-name="kabam"], #nav_advertisement, .fp-banner-ad, #sizzle_strip, .advertisement, .new_advertisement, .js-new-channel-ad, #ad_actions, .ad_leader, .fp_ad {display: none !important;}/* remove ads */
#right_col, .rightcol-content {top: 0px !important;}
body[data-page*="front_page"] #mantle_skin {background-color: #262626 !important;background-image: none !important;}
.ember-list-view, .scrollabel_list {overflow: auto !important;}/* fix scrollbar bugs */
.extra-menu {height: 136px !important;}/* expand large nav extra menu */
#large_nav .left-col-dropdown {display: none !important;}/* expand large nav extra menu */
.ember-chat .badges .turbo, .js-turbo-more-colors, #header_turbo_promo, a[href="/settings/turbo"] {display: none !important;}/* remove turbo */
.notification-controls .toggle-notification-menu {background: rgb(20,20,20) !important;}/* remove turbo */
 #nav_games, #small_more, #footer > .links_group, .facebook_connect_contain {display: none !important;} /* remove spam */
#left_col:before{display:none;}
.theatre-button{display:none;}
.chat-header{display:none;}
.what--offlineChannelStatus{display:none;}
.button.drop:after{display:none!important;}
  
.chat-line .badges{display:/*[[ShowBadges]]*/!important;}
  
#nav_advertisement,.fp-banner-ad,#sizzle_strip,.advertisement,.new_advertisement,.js-new-channel-ad,#ad_actions,.ad_leader,.fp_ad,.promoted-game{display:none!important;}
}
/*** remove promoted channels ***/
@-moz-document regexp("https?:\/\/(www|beta)?\.?twitch\.tv\/(directory|subscriptions|broadcast|.+\/dashboard|.+\/manager).*") {
#nav_streams {display: none !important;}
}
/*** directory right panel fix ***/
@-moz-document regexp("http://.*twitch.tv/directory.*"){
div#right_col.column.fixed.following-col.open{width:340px!important;}
#main_col:not([class*="expandRight"]):not([style*="margin-right: 340px;"]){margin-right:340px!important;}
}
/*** profile right panel fix ***/
@-moz-document regexp("http://.*twitch.tv.*/profile"){
div#right_col.column.fixed.following-col.open{width:340px!important;}
#main_col:not([class*="expandRight"]):not([style*="margin-right: 340px;"]){margin-right:340px!important;}
#right_col, .rightcol-content{right:0px!important;width:340px!important;}
#channel_panels_contain{position:relative !important;;background:rgba(10,10,10,0.75) right top no-repeat!important;top:0px!important;left:0px!important;height:auto !important;width:auto !important;overflow:hidden !important;z-index: 100 !important;}
#channel_panels_contain:hover {padding:0px!important;background:rgba(10,10,10,0.75) right top no-repeat!important;left:0px!important;right:-0px!important;top:0px!important;width:auto !important;height:auto !important;overflow:auto!important;}
}
